insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5130, 'In Which We Serve', False, True, False, False, 1942, 7.2, 'This is the story of a British Naval ship, the HMS Torrin, from it''s construction to its sinking in the Mediterranean during action in World War II. The ships first and only commanding officer is the experienced Captain E.V. Kinross who trains his men not only to be loyal to him but to the country and most importantly, to themselves. They face challenges at sea and also at home. They lose some of theirs shipmates in action and some of their loved ones in the devastation of the blitz. Throughout it all, the men of the Torrin serve valiantly and heroically.', 'tt0034891', '/nBpx9srdlUHywZrVP7XRTFvmkL5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5132, 'Convoy', False, True, False, False, 1978, 6.4, 'Truckers form a mile long "convoy" in support of a trucker''s vendetta with an abusive sheriff...Based on the country song of same title by C.W. McCall.', 'tt0077369', '/xjowoBInlikSlvfAYDRN9ubaODB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5134, 'The Exorcist III', False, True, False, False, 1990, 6.4, 'Set fifteen years after the original film, The Exorcist III centers around the philosophical Lieutenant William F. Kinderman who is investigating a baffling series of murders around Georgetown that all contain the hallmarks of The Gemini, a deceased serial killer. It eventually leads him to a catatonic patient in a psychiatric hospital who has recently started to speak, claiming he is the The Gemini and detailing the murders, but bears a striking resemblance to Father Damien Karras.', 'tt0099528', '/xww5RyKpDKjGPXnOVYhaZrqCVcL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5136, 'Long Weekend', False, True, False, False, 1978, 6.6, 'Mother Nature gets revenge on some careless campers in this remastered version of the Australian horror classic.', 'tt1098356', '/szfiY3ycr0SBAjb81og8tIzKgGj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5138, 'Kid Cannabis', False, True, False, False, 2014, 6.4, 'An eighteen year old high school drop out and his twenty-seven year old friend start trafficking marijuana across the border of Canada in order to make money and their lives are changed forever.', 'tt0780548', '/32YMW8j4BbmVgXc9Jl17NFo9BOO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5139, 'Beauty Is Embarrassing', False, True, False, False, 2012, 7.4, 'Raised in the Tennessee mountains, Wayne White started his career as a cartoonist in NYC. He quickly found success as one of the creators of the Pee-wee''s Playhouse TV show which soon led to more work designing some of the most arresting and iconic images in pop culture. Recently his word paintings featuring pithy and and often sarcastic text statements finely crafted onto vintage landscape paintings have made him a darling of the fine art world. The movie chronicles the vaulted highs and crushing lows of an artist struggling to find peace and balance between his professional work and his personal art. This is especially complicated for a man who struggles with the virtues he most often mocks in his art...Vanity, ego and fame.', 'tt2040281', '/6QaxNpdODOAdXPysw5Coyxv26Jo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5141, 'The Inspector General', False, True, False, False, 1949, 6.8, 'An illiterate stooge in a traveling medicine show wanders into a strange town and is picked up on a vagrancy charge. The town''s corrupt officials mistake him for the inspector general whom they think is traveling in disguise. Fearing he will discover they''ve been pocketing tax money, they make several bungled attempts to kill him.', 'tt0041509', '/6CArPzDnzS04QNQ94wphh6cLecY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5142, 'Animal Factory', False, True, False, False, 2000, 6.6, 'A young man goes to prison and a tough, older convict takes him under his wing as a mentor.', 'tt0204137', '/gWjU181fqffVtWtduJBtVrqcdjL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5143, 'Shanghai Triad', False, True, False, False, 1995, 7.1, 'A provincial boy related to a Shanghai crime family is recruited by his uncle into cosmopolitan Shanghai in the 1930s to be a servant to a ganglord''s mistress.', 'tt0115012', '/6mexZlP0XhcXpK7jOwTfcCF9jOT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5145, 'The Prophecy', False, True, False, False, 1995, 6.5, 'The angel Gabriel comes to Earth to collect a soul which will end the stalemated war in Heaven, and only a former priest and a little girl can stop him.', 'tt0114194', '/4ZWFhQipzBHJze2llzCzhrSIZsn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5146, 'Manufactured Landscapes', False, True, False, False, 2006, 7.3, 'MANUFACTURED LANDSCAPES is the striking new documentary on the world and work of renowned artist Edward Burtynsky. Internationally acclaimed for his large-scale photographs of “manufactured landscapes”—quarries, recycling yards, factories, mines and dams—Burtynsky creates stunningly beautiful art from civilization’s materials and debris.', 'tt0832903', '/bsk0E1Zc5jEW0mRuCLOBdYXfDVS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5148, 'The Way of the Gun', False, True, False, False, 2000, 6.7, 'Parker and Longbaugh are a pair of low-level petty criminals, living off the grid and funding their existence through unconventional and often illegal means. Wanting to move past petty crime, they vow to get the proverbial "big score.', 'tt0202677', '/jimlDfzdPXZwOXCZVCCg1Kuouhm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5149, 'Soapdish', False, True, False, False, 1991, 6.6, 'Celeste Talbert is the star of the long-running soap opera "The Sun Also Sets." With the show''s ratings down, Celeste''s ruthlessly ambitious co-star, Montana Moorehead, and the show''s arrogant producer, David Seton Barnes, plot to aggravate her into leaving the show by bringing back her old flame, Jeffrey Anderson, and hiring her beautiful young niece, Lori Craven.', 'tt0102951', '/8iIHog4asUWOZS7Pc4t9ouYFc3N.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5151, 'Big Fan', False, True, False, False, 2009, 6.7, 'Paul Aufiero, a 35-year-old parking-garage attendant from Staten Island, is the self-described "world''s biggest New York Giants fan". One night, Paul and his best friend Sal spot Giants star linebacker Quantrell Bishop at a gas station and decide to follow him. At a strip club Paul cautiously decides to approach him but the chance encounter brings Paul''s world crashing down around him.', 'tt1228953', '/w29ex7m662TUot6fVsgDHoyTZaK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5152, 'Night Owls', False, True, False, False, 2015, 6.2, 'After workaholic Kevin has a drunken one night stand with the beautiful train-wreck Madeline, he''s horrified to discover that she''s actually his boss'' jilted ex-mistress. When she takes a bottle of sleeping pills, Kevin is forced to keep her awake... and over the course of the night the two begin to fall for each other.', 'tt3591436', '/wp6yVU19F8BmC4fD7RAmZbJurT5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5153, 'Call Me Lucky', False, True, False, False, 2015, 7.6, 'An inspiring, triumphant and wickedly funny portrait of one of comedy’s most enigmatic and important figures, CALL ME LUCKY tells the story of Barry Crimmins, a beer-swilling, politically outspoken and whip-smart comic whose efforts in the 70s and 80s fostered the talents of the next generation of standup comedians. But beneath Crimmins’ gruff, hard-drinking, curmudgeonly persona lay an undercurrent of rage stemming from his long-suppressed and horrific abuse as a child – a rage that eventually found its way out of the comedy clubs and television shows and into the political arena.', 'tt3899932', '/eewB9ORnDZsxPoJiTKCcZMocm1p.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5155, 'The Mule', False, True, False, False, 2014, 6.3, 'In 1983, a naive man is detained by Australian Federal Police with lethal narcotics hidden in his stomach. After being apprehended, ‘The Mule’ makes a desperate choice... to defy his bodily functions and withhold the evidence – literally.', 'tt2130270', '/qwMZYSkLofFC27oBeZ1hZWgliXw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5156, 'The Shout', False, True, False, False, 1978, 6.7, 'A traveller by the name of Crossley, forces himself upon a musician and his wife in a lonely part of Devon, and uses the aboriginal magic he has learned to displace his host.', 'tt0078259', '/bLITKNwcjux5UftI42jpV6ET1Dq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5157, 'The Dark', False, True, False, False, 2018, 5.6, 'At night the Mangler stalks the streets of Los Angeles, killing and mutilating random victims. On the trail are a TV reporter, the father of one of the victims, and a police detective, but despite their efforts only the mysterious psychic DeRenzy knows what the killer is and how to stop it.', 'tt0079027', '/c32dVjgoK3G40YzmP32dW1Q8rT5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5158, 'The Irish Pub', False, True, False, False, 2013, 7.3, 'The tradition of the Irish pub. Speaking to pub owners all over Ireland, Alex Fegan gets into the heart of what makes "the Irish pub" the institution that it is.', 'tt3229518', '/kXUTMXveIWGOzMLlRzeRJXr6yGM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5159, 'Charlie''s Country', False, True, False, False, 2013, 7.2, 'Blackfella Charlie is getting older, and he''s out of sorts. The intervention is making life more difficult on his remote community, what with the proper policing of whitefella laws that don''t generally make much sense, and Charlie''s kin and ken seeming more interested in going along with things than doing anything about it. So Charlie takes off, to live the old way, but in doing so sets off a chain of events in his life that has him return to his community chastened, and somewhat the wiser.', 'tt3244512', '/sqhOh1iFUgzDD0855ZRcCnV5sJ8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5161, 'Never on Sunday', False, True, False, False, 1960, 7.3, 'Never on Sunday is a 1960 Greek black-and-white film which tells the story of Ilya, a prostitute who lives in the port of Piraeus in Greece, and Homer, an American tourist from Middletown, Connecticut — a classical scholar enamored with all things Greek. Ilya is a character close to the "hooker with a heart of gold" cliché. Homer feels Ilya''s life style typifies the degradation of Greek classical culture and attempts to steer her onto the path of morality. It constitutes a variation of the Pygmalion story.  The film stars Melina Mercouri and Jules Dassin, and it gently submerges the viewer into Greek culture, including dance, music, and language (through the use of subtitles). The signature song and the bouzouki theme of the movie became hits of the 1960s and brought the composer, Manos Hadjidakis, an Academy Award.', 'tt0054198', '/hYpLEE5E998zVwT8AGlN4WRIuQP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5163, 'The Pope''s Toilet', False, True, False, False, 2007, 7.3, 'A small South American village is in a flurry over the Pope''s 1988 visit.', 'tt0482901', '/kn2GWfyqYzlSLZYQpocO4LT01yE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5165, 'Robin Hood', False, True, True, False, 1922, 7.2, 'Yet another version of the classic epic, with enough variation to make it interesting. The story is the same, but some of the characters are quite different from the usual, in particular Uma Thurman''s very special maid Marian. The photography is also great, giving the story a somewhat darker tone.', 'tt0102797', '/rmVyrko5XHRuoO37cRVMOWDm17W.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5166, 'Palo Alto', False, True, False, False, 2014, 6.2, 'Palo Alto weaves together three stories of teenage lust, boredom, and self-destruction: shy, sensitive April, torn between an illicit flirtation with her soccer coach and an unrequited crush on sweet stoner Teddy; Emily, who offers sexual favors to any boy to cross her path; and the increasingly dangerous exploits of Teddy and his best friend Fred, whose behavior may or may not be sociopathic.', 'tt2479800', '/p3GNOSu1c0008HslESUIpY4SDbL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5167, 'Ray Harryhausen: Special Effects Titan', False, True, False, False, 2011, 7.5, 'There’s only one person who so accurately personifies movie magic in the history of film, and that man is special effects maestro Ray Harryhausen. Focusing on the man behind the landmark effects on films like Clash Of The Titans, One Million Years B.C., Jason And The Argonauts and many more, this in-depth film features interviews with the great man himself, and with an array of animators and directors influenced by his work including Guillermo del Toro, Peter Jackson, Nick Park, Terry Gilliam, James Cameron and Steven Spielberg. The film also features unseen footage of tests and experiments recently uncovered.', 'tt1839590', '/7feAu14viNcTACirNdmqtlQ4DFH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5169, 'Park Row', False, True, False, False, 1952, 7.2, 'In New York''s 1880''s newspaper district a dedicated journalist manages to set up his own paper. It is an immediate success but attracts increasing opposition from one of the bigger papers and its newspaper heiress owner.', 'tt0045009', '/bEiXYFMOmYe4699FIk4GP6EHYNB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5170, 'Kundo: Age of the Rampant', False, True, False, False, 2014, 6.8, 'Set in the 19th century, when lowly butcher Dol Moo-Chi refuses to carry out an assignment, his failure causes tragedy to unfold upon his family and himself. As Dol Moo-Chi is about to be executed, a group of righteous thieves known as Kundo arrive.', 'tt2972482', '/vJg0hJrSKvdOtMzJReXJN3j2UsU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5172, 'King of the Ants', False, True, False, False, 2003, 6.2, 'A young drifter, struggling to makes ends meet, accepts a job to kill a prominent accountant. When he isn''t paid for the hit, revenge is now his path.', 'tt0328031', '/bFsxsZlEDo1rMemCLZiR06z9YlF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5173, 'The Opposite of Sex', False, True, False, False, 1998, 6.3, 'A 16-year-old girl visits her gay half-brother and ends up seducing his boyfriend, thus wreaking havoc on all of their lives.', 'tt0120777', '/xpILzxKHywYngftBC8AmvsqUfBj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5174, 'Hidden Agenda', False, True, False, False, 1990, 6.9, 'Former government Jason Price (Dolph Lundgren) makes people disappear for a living. But when a top lieutenant for a crime syndicate asks Price to help him drop out of sight, things take a grisly, unexpected turn. A mysterious hit man known as The Cleaner has infiltrated Price''s tightly-controlled system, leaving a bloody trail behind. With the help of an enigmatic seductress posing as the mobster''s chief assistant, Price methodically works through his network of associates to find out what went wrong, and is plunged headlong into a spiraling gauntlet of half-truths and full-blown lies where nobody is who they appear to be, and everyone has a deadly, hidden agenda', 'tt0271543', '/pMHcp3SPTfkCJdajkdx91sm7vVk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5175, 'The Italian', False, True, False, False, 2005, 7.5, 'An immigrant leaves his sweetheart in Italy to find a better life across the sea in the grimy slums of New York.', 'tt0005557', '/oeI2wnZoQCLvJ0G95liZ47oixKl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5179, 'The Hanging Garden', False, True, False, False, 1997, 6.8, 'William, a once obese and troubled teen, goes back to his family''s home after being gone, without word, for ten years and finds it (and his family) haunted with his past. He had moved to the city and become a fit, well-adjusted gay man, but during his visit home, he becomes unhinged as the newly remembered reasons for his miserable adolescence come to life in each of their presents.', 'tt0125128', '/kXhjuFB4U8xkEd6SY84S42w3Q8R.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5180, 'Dreamchild', False, True, False, False, 1985, 6.8, 'Exploring the somewhat darker and more mysterious side of the Lewis Carroll''s classic book, the movie follows Alice Liddell (the book''s inspiration) as an old woman who is haunted by the characters she was once so amused by. As she thinks back on it, she starts to see her relationship with the shy author/professor in a new way and realizes the vast change between the young Alice and the old.', 'tt0089052', '/vgmONyJ6Io0sPibW9SRiZFHVOO0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5181, 'Unmistaken Child', False, True, False, False, 2009, 7.5, 'In Nepal, a venerable monk, Geshe Lama Konchog, dies and one of his disciples, a youthful monk named Tenzin Zopa, searches for his master''s reincarnation. The film follows his search to the Tsum Valley where he finds a young boy of the right age who uncannily responds to Konchog''s possessions. Is this the reincarnation of the master? After the boy passes several tests, Tenzin takes him to meet the Dali Lama. Will the parents agree to let the boy go to the monastery, and, if so, how will the child respond? Central to the film is the relationship the child develops with Tenzin.', 'tt1286798', '/gkuDkFkyG2cw8llSDpVYwcgsLU8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5182, '22 Bullets', False, True, False, False, 2010, 6.7, 'Charly Matteï has turned his back on his life as an outlaw. For the last three years, he''s led a peaceful life devoting himself to his wife and two children. Then, one winter morning, he''s left for dead in the parking garage in Marseille''s Old Port, with 22 bullets in his body. Against all the odds, he doesn''t die...', 'tt1167638', '/pwG0LLr00EonphwCmIQrpb6aiVV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5183, 'The Scarlet Pimpernel', False, True, False, False, 1934, 7.4, 'During the French Revolution, a mysterious English nobleman known only as The Scarlet Pimpernel (a humble wayside flower), snatches French aristos from the jaws of the guillotine, while posing as the foppish Sir Percy Blakeney in society. Percy falls for and marries the beautiful actress Marguerite St. Just, but she is involved with Chauvelin and Robespierre, and Percy''s marriage to her may endanger the Pimpernel''s plans to save the little Dauphin', 'tt0084637', '/445d32U3gCoMYtk4ZaPNpWMLoFA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5184, 'The Story of Luke', False, True, False, False, 2013, 7.2, 'Sheltered by his grandparents, Luke, a young man with autism, is thrust into a world that doesn''t expect anything from him. But Luke is on a quest for a job and true love. And he isn''t taking no for an answer.', 'tt1038693', '/dSXad96zPtphKHnDNiOD34EhcA0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5186, 'Always Shine', False, True, False, False, 2016, 5.6, 'On a trip to Big Sur, two friends, both actresses, try to reconnect with one another. Once alone, the women''s suppressed jealousies and deep-seated resentments begin to rise, causing them to lose their grasp on not only the true nature of their relationship, but also their identities.', 'tt4392770', '/fLrFpga7qDf68lqvyGTpCYuaP4G.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5187, 'Elizabethtown', False, True, False, False, 2005, 6.4, 'Drew Baylor is fired after causing his shoe company to lose hundreds of millions of dollars. To make matters worse, he''s also dumped by his girlfriend. On the verge of ending it all, Drew gets a new lease on life when he returns to his family''s small Kentucky hometown after his father dies. Along the way, he meets a flight attendant with whom he falls in love.', 'tt0368709', '/mOdlzAQxhMgiaazRzif1YtJzg9s.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5188, 'After the Dark', False, True, False, False, 2013, 5.7, 'At an international school in Jakarta, a philosophy teacher challenges his class of twenty graduating seniors to choose which ten of them would take shelter underground and reboot the human race in the event of a nuclear apocalypse.', 'tt1928340', '/mTSBjTriWuvYp27dsTWF9hs25HB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5189, 'G.B.F.', False, True, False, False, 2013, 5.9, 'The bitter fight for supremacy between the three most popular girls at North Gateway High takes an unexpected turn when their classmate, Tanner, is outed and becomes the school’s first openly gay student. The trio races to bag the big trend in fashion accessories, the Gay Best Friend, while Tanner must decide whether his skyrocketing popularity is more important than the friendships he is leaving behind.', 'tt2429074', '/f4fR1WMgSv7K93ZPoJlPyvaApdE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5190, 'I Spit on Your Grave', False, True, False, False, 2010, 6.3, 'A remake of the 1979 controversial cult classic, I Spit on Your Grave retells the horrific tale of writer Jennifer Hills who takes a retreat from the city to a charming cabin in the woods to start on her next book. But Jennifer''s presence in the small town attracts the attention of a few morally deprived locals led by Johnny, the town''s service station owner, his two co-workers, Andy and Stanley, who along with their socially and mentally challenged friend Matthew, set out one night to teach this city girl a lesson.', 'tt1242432', '/7hUHtWT0I0ex84megfNKdTPTRWK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5191, 'American Gigolo', False, True, False, False, 1980, 6.2, 'Julian makes a lucrative living as an escort to older women in the Los Angeles area. He begins a relationship with Michelle, a local politician''s wife, without expecting any pay. One of his clients is murdered and Detective Sunday begins pumping him for details on his different clients, something he is reluctant to do considering the nature of his work. Julian begins to suspect he''s being framed. Meanwhile Michelle begins to fall in love with him.', 'tt0080365', '/zrsOgLFiFXYzleX9p3tp8eNUruk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5192, 'Outfoxed: Rupert Murdoch''s War on Journalism', False, True, False, False, 2004, 7.5, 'This film examines how media empires, led by Rupert Murdoch''s Fox News, have been running a "race to the bottom" in television news, and provides an in-depth look at Fox News and the dangerous impact on society when a broad swath of media is controlled by one person.  Media experts, including Jeff Cohen (FAIR) Bob McChesney (Free Press), Chellie Pingree (Common Cause), Jeff Chester (Center for Digital Democracy) and David Brock (Media Matters) provide context and guidance for the story of Fox News and its effect on society.  This documentary also reveals the secrets of Former Fox news producers, reporters, bookers and writers who expose what it''s like to work for Fox News.  These former Fox employees talk about how they were forced to push a "right-wing" point of view or risk their jobs. Some have even chosen to remain anonymous in order to protect their current livelihoods. As one employee said "There''s no sense of integrity as far as having a line that can''t be crossed."', 'tt0418038', '/5SodI4NHGIHghH6HGfqryiNlMSA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5193, 'Upside Down: The Creation Records Story', False, True, False, False, 2010, 7.2, 'Over a quarter of a century since it began and a decade after it folded, this is the definitive film about Creation Records, one of the world''s most successful and colorful independent labels. This is the story of the rock n roll dream and its accompanying nightmares. Millions of sales on both sides of the Atlantic, near bankruptcy, pills, thrills, spats, prats, success, excess, pick me ups, breakdowns and of course some of THE defining music of the late 20th Century. This is the definitive and fully authorised story of the UK''s most inspired and dissolute label, from the Jesus &amp; Mary Chain at the Living Room to Oasis at Knebworth.', 'tt1764726', '/jV4USc1ivMeaaD0vMrfVfWiWmWb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5194, 'White Zombie', False, True, False, False, 1932, 6.3, 'In Haiti, a wealthy landowner convinces a sorcerer to lure the American woman he has fallen for away from her fiance, only to have the madman decide to keep the woman for himself, as a zombie.', 'tt0023694', '/s5DnfWHGeCExlweIFF4JOHKyx3H.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5195, 'Pork Chop Hill', False, True, False, False, 1959, 7.1, 'American GI''s must retake a barren hill in Korea that has been overrun by Red Chinese troops. The ensuing battle becomes a meat grinder for American and Chinese alike.', 'tt0053183', '/AnbrYOCt9unXVAs9hdiCeAGj8vZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5196, 'As If I Am Not There', False, True, False, False, 2010, 7.2, 'A harsh dose of cinematic realism about a harsh time-the Bosnian War of the 1990s-Juanita Wilson''s drama is taken from true stories revealed during the International Criminal Tribunal in The Hague. Samira is a modern schoolteacher in Sarajevo who takes a job in a small country village just as the war is beginning to ramp up. When Serbian soldiers overrun the village, shoot the men and keep the women as laborers (the older ones) and sex objects (the younger ones), Samira is subjected to the basest form of treatment imaginable', 'tt1456477', '/dZxaSHjc2CUgCGmQ4psSU2tTHeO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5197, 'The Special Relationship', False, True, False, False, 2010, 6.7, 'A dramatisation that follows Tony Blair''s journey from political understudy waiting in the wings of the world arena to accomplished prime minister standing confidently in the spotlight of centre stage. It is a story about relationships, between two powerful men (Blair and Bill Clinton), two powerful couples, and husbands and wives.', 'tt1117646', '/5uCugkXyZ42VnAQnyttYZ8msp0L.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5198, 'Woman on the Run', False, True, False, False, 1950, 7.3, 'Frank Johnson, sole witness to a gangland murder, goes into hiding and is trailed by Police Inspector Ferris, on the theory that Frank is trying to escape from possible retaliation. Frank''s wife, Eleanor, suspects he is actually running away from their unsuccessful marriage. Aided by a newspaperman, Danny Leggett, Eleanor sets out to locate her husband. The killer is also looking for him, and keeps close tabs on Eleanor.', 'tt0043142', '/8RttDdrSVwYSSwGpmil0z3vu98g.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5199, 'Louis C.K.: Shameless', False, True, False, False, 2007, 8.6, 'Louis C.K. is back on HBO in an ALL-NEW hour of raw no-holds-barred stand-up comedy! The creator and star of the 2006 comedy series Lucky Louie, performs in front of a live audience in LA at the Henry Fonda Theater. Louis C.K. covers issues near and dear to his heart like marriage, lying to your spouse, having kids and losing your privacy, and obligatory sex among husbands and wives.', 'tt0927619', '/8FRUSS8zgqbyTUJY9qgjzehA4YR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5200, 'Code Black', False, True, False, False, 2014, 7.2, 'Code Black follows a team of young, idealistic and energetic ER doctors during the transition from the old to the new L.A. County as they try to avoid burnout and improve patient care. Why do they persist, despite being under siege by rules, regulations and paperwork?', 'tt2759372', '/dPur8SSlkV8QBbDMlOHEdqKUG9R.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5201, 'The Departure', False, True, False, False, 2017, 7, 'A fast-paced comedy about a young Belgian car nut and hairdresser''s apprentice, his girlfriend, and their legal and illegal attempts to get a Porsche under him for his nearing debut race.', 'tt0061605', '/nU9TcE8CWkZhCFa9JF8kTCQpYFe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5203, 'King Corn', False, True, False, False, 2007, 7, 'King Corn is a fun and crusading journey into the digestive tract of our fast food nation where one ultra-industrial, pesticide-laden, heavily-subsidized commodity dominates the food pyramid from top to bottom – corn. Fueled by curiosity and a dash of naiveté, two college buddies return to their ancestral home of Greene, Iowa to figure out how a modest kernel conquered America. With the help of some real farmers, oodles of fertilizer and government aide, and some genetically modified seeds, the friends manage to grow one acre of corn. Along the way, they unlock the hilarious absurdities and scary but hidden truths about America’s modern food system in this engrossing and eye-opening documentary.', 'tt1112115', '/zVHWzV9o2FU1SasNxuiJMW2FY0V.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5204, 'People, Places, Things', False, True, False, False, 2015, 6.9, 'Will Henry is a newly single graphic novelist balancing being a parent to his young twin daughters and teaching a classroom full of college students, all the while trying to navigate the rich complexities of new love and letting go of the woman who left him.', 'tt4104022', '/d1vHsWyFgPi0bKwNmd8xPpBdBtC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5205, 'Something from Nothing: The Art of Rap', False, True, False, False, 2012, 7.2, 'SOMETHING FROM NOTHING: THE ART OF RAP is a feature length performance documentary about the runaway juggernaut that is Rap music. At the wheel of this unstoppable beast is the film''s director and interviewer Ice-T. Taking us on a deeply personal journey Ice-T uncovers how this music of the street has grown to dominate the world. Along the way Ice-T meets a whole spectrum of Hip-Hop talent, from founders, to new faces, to the global superstars like Eminem, Dr Dre, Snoop Dogg and Kanye West. He exposes the roots and history of Rap and then, through meeting many of its most famous protagonists, studies the living mechanism of the music to reveal ''The Art Of Rap''. This extraordinary film features unique performances from the entire cast, without resorting to archive material, to build a fresh and surprising take on the phenomenon that is Rap.', 'tt2062996', '/vXEZjHxxwaQqKnkE92paaXg5bV1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5206, 'The Age of Stupid', False, True, False, False, 2009, 7, 'The Age of Stupid is the new movie from Director Franny Armstrong (McLibel) and producer John Battsek (One Day In September). Pete Postlethwaite stars as a man living alone in the devastated future world of 2055, looking at old footage from 2008 and asking: why didn’t we stop climate change when we had the chance?', 'tt1300563', '/t69rPoy7NXe9BVoPmhQB4WKNhJ8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5209, 'King and Country', False, True, False, False, 1964, 7.4, 'During World War I a British soldier, charged with desertion, is defended by an officer who at first despises his actions and then grows to feel sympathy for him.', 'tt0058263', '/1hojgtHlZ8lRr2LZEWaqJSOKQQs.jpg');
