insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5314, 'Rob the Mob', False, True, False, False, 2014, 6.3, 'The true-life story of a crazy-in-love Queens couple who robbed a series of mafia social clubs and got away with it… for a while… until they stumble upon a score bigger than they ever planned and become targets of both the mob and the FBI.', 'tt2481480', '/9wAHoj1yEkbWkLWR3IH6kxsUzgm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5318, 'Soaked in Bleach', False, True, False, False, 2015, 7.4, 'Twenty years ago, Kurt Cobain was found dead of an apparent gunshot wound to the head. The world was told it was a suicide, but evidence would lead many people to believe it might be otherwise. The film investigates the possibilities that exist that Kurt Cobain''s death might not have been a suicide, that the Seattle Police Department rushed their verdict, and the global media perpetuated lies and misinformation fed to them by Courtney Love that created the belief in many that Cobain killed himself, but when revealed to be lies, lead many to now question what happened.', 'tt3253624', '/1ScpSMDQvwi28Dro1xwSOcKCVln.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5319, 'Bachna Ae Haseeno', False, True, False, False, 2008, 6.2, 'The film follows the journey of Raj Sharma (Ranbir Kapoor) from the age of 18 to 30 and how he meets three girls at different stages of his life : Mahi (Minissha Lamba) - a small-town girl from Punjab, Radhika (Bipasha Basu) - a model from Mumbai and Gayatri (Deepika Padukone) - a NRI taxi driver and student from Australia.', 'tt1182972', '/HqUJdDNYJx4UTdMTz7Ezu1bQwA.jpg');
