insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5327, 'Love Affair', False, True, False, False, 1939, 7.3, 'French playboy Michel Marnet and American Terry McKay fall in love during the transatlantic passage of a ship. They arrange to reunite six months later, if neither has changed their mind.', 'tt0031593', '/52oz5IrZzGMPsSqAgAywqUZw8Et.jpg');
