insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5329, 'Les Misérables', False, True, False, False, 2019, 7.6, 'In 1935, American audiences were treated to two simultaneously released versions of Les Miserables, one made in Hollywood, the other in France. Much the same thing happened in 1952. While 20th Century-Fox''s Les Miserables, starring Michael Rennie as beleagured ex-convict Jean Valjean, was making the rounds, a second version, lensed in Italy, was also showing up in selected theatres. Running a fast 112 minutes (hardly enough time to do full justice to the novel), U.S. release of the Italian adaptation starred Gino Servi as Valjean, whose "new" life as a man of position and respect is forever threatened by dogged detective Javert. Valentine Cortese costars as Cosette, Valjean''s adopted daughter. English-language prints of the the Italian Les Miserables credit the direction to "Robert Hampton"; in fact, direction was handled by prominent European filmmaker Riccardo Freda.', 'tt0039629', '/svpSmGvJPX87v4iqDn4WZtttO6A.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5330, 'True Legend', False, True, False, False, 2010, 6.4, 'Su Qi-Er, a wealthy man living during the Qing Dynasty who loses his fortune and reputation as a result of a conspiracy against him. After being forced out onto the streets, Su dedicates his life to martial arts and reemerges as a patriotic hero known as the "King of Beggars."', 'tt1425257', '/6UICxESwvJbvRHdgT0jenH1xPmW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5331, 'The Round Up', False, True, False, False, 2010, 7.1, '1942. Joseph is eleven. And this June morning, he must go to school, a yellow star sown on his chest. He receives the support of a goods dealer. The mockery of a baker. Between kindness and contempt, Jo, his Jewish friends, their families, learn of life in an occupied Paris, on the Butte Montmartre, where they''ve taken shelter. At least that''s what they think, until that morning on July 16th 1942, when their fragile happiness is toppled over.', 'tt1382725', '/zR3qTRRf1kqY9qep4NNcLQjyM8r.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5332, 'Honey', False, True, False, False, 2013, 6.7, 'Irene, nicknamed ''Miele'', has devote herself to people looking for help, and tries to alleviate their suffering even when they make extreme decisions. One day she has to cope with Grimaldi and his invisible malaise.', 'tt2357461', '/s7QhAAnHcwc9w45pGQLrBUJwdxh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5334, 'Punching the Clown', False, True, False, False, 2010, 7.2, 'A satirical songwriter comes to Los Angeles and puts his life''s work in jeopardy.', 'tt1192624', '/5MUlQpNjWXHzfI1YcRB0MCtOvnP.jpg');
