insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5390, 'The Words', False, True, False, False, 2012, 7, 'The Words follows young writer Rory Jansen who finally achieves long sought after literary success after publishing the next great American novel. There''s only one catch - he didn''t write it. As the past comes back to haunt him and his literary star continues to rise, Jansen is forced to confront the steep price that must be paid for stealing another man''s work, and for placing ambition and success above life''s most fundamental three words.', 'tt1840417', '/gaugOXEtSKoMhwLaD4oHxlFzwMV.jpg');
