insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5636, 'Murder!', False, True, False, False, 1930, 6.4, 'When a woman is convicted of murder, one of the jurors selected to serve on the murder-trial jury believes the accused, an aspiring actress, is innocent of the crime and takes it upon himself to apprehend the real killer.', 'tt0021165', '/huuLmXGPCYjGdfUbXEn8bOSqx65.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5637, 'The Keeping Room', False, True, False, False, 2014, 6, 'In this radically reimagined American Western set towards the end of the Civil War, Southerner Augusta encounters two renegade, drunken soldiers who are on a mission of pillage and violence. After escaping an attempted assault, Augusta races back to the isolated farmhouse that she shares with her sister Louise and their female slave Mad. When the pair of soldiers track Augusta down intent on exacting revenge, the trio of women are forced to take up arms to fend off their assailants, finding ways to resourcefully defend their home––and themselves––as the escalating attacks become more unpredictable and relentless.', 'tt2488778', '/8XuVrzyFGPQw9pWWRocVqOZunY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5639, 'Bedrooms and Hallways', False, True, False, False, 1998, 6.6, 'At the suggestion of a friend, gay Leo (Kevin McKidd) enters therapy after many failed relationships make him fear for his social life. In a group session (led by Simon Callow), Leo comes clean about his feelings for a straight member of the group (James Purefoy), which leads to an affair between the two. The situation becomes further complicated by the appearance of Leo''s high school sweetheart (Jennifer Ehle), who still has feelings for him.', 'tt0126810', '/jRbhbHEJbyeJjcHv3u4T6bE7u9z.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5642, '100 Bloody Acres', False, True, False, False, 2012, 6, 'Reg and Lindsay run an organic fertiliser business. They need a fresh supply of their "secret ingredient" to process through the meat grinder. Reg comes across two guys and a girl with a broken-down vehicle on their way to a music festival.', 'tt2290065', '/jYUolYFQ7F8E2rEunRNwtbpLwmW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5643, 'George Carlin: Back in Town', False, True, False, False, 1996, 8.7, 'Back in Town is George Carlin''s ninth HBO special. It was also released on CD on September 17, 1996. This was also his first of many performances at the Beacon Theater in New York City. He rants about Abortion, The death penalty, prison farms, fart jokes, free floating hostility and words.', 'tt0246641', '/gMA34cMwtPKG1GbKlWokMSmUDIE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5644, 'B.B. King: The Life of Riley', False, True, False, False, 2012, 7.4, 'Documentary charting the life of blues guitarist B.B. King, with contributions from fellow musicians.', 'tt2259306', '/mUylDxl5mx5glgBdbp7mY7aXNtU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5646, 'Francofonia', False, True, False, False, 2015, 6.6, 'Master filmmaker Alexander Sokurov (Russian Ark) transforms a portrait of the world-renowned museum into a magisterial, centuries-spanning reflection on the relation between art, culture and power.', 'tt3451720', '/saCFbjXLlypGlX5UngU3iLbthGs.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5648, 'Zarafa', False, True, False, False, 2012, 7, 'Inspired by the true story of the first giraffe to visit France, Zarafa is a sumptuously animated and stirring adventure, and a throwback to a bygone era of hand-drawn animation and epic storytelling set among sweeping CinemaScope vistas of parched desert, wind-swept mountains and open skies.  Under the cover of darkness a small boy, Maki, loosens the shackles that bind him and escapes into the desert night. Pursued by slavers across the moon-lit savannah, Maki meets Zarafa, a baby giraffe – and an orphan, just like him – as well as the nomad Hassan, Prince of the Desert. Hassan takes them to Alexandria for an audience with the Pasha of Egypt, who orders him to deliver the exotic animal as a gift to King Charles of France.', 'tt2077908', '/gsTt2bxvffCIsRoZlXDqRGM01H9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5649, 'Goya''s Ghosts', False, True, False, False, 2006, 6.9, 'Painter Francisco Goya faces a scandal involving his muse, who is labeled a heretic by a monk.', 'tt0455957', '/qcU57EJpIRA6HUQCuYveONLQQiH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5650, 'Yu-Gi-Oh!: The Dark Side of Dimensions', False, True, False, False, 2016, 7, 'Yugi and Kaiba have a special duel that transcends dimensions.', 'tt4273562', '/nFQkn1KE0Is3FUW0yPPgdVvCnlo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5651, 'The Poker House', False, True, False, False, 2008, 6.4, 'Actress Lori Petty makes her directorial debut with this poignant, beautifully crafted film about a teenage girl trying to survive the dismal circumstances of her life with dignity, humor, and basketball.', 'tt1014806', '/xnhYvtlLVzwXhJnPTit2Q4lDI6v.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5653, 'Uzumasa Limelight', False, True, False, False, 2014, 6, 'A moving, nostalgic portrait of the men behind the golden age of chanbara (sword-fighting dramas and films) that goes behind the scenes of the distinctive film genre for which Japan is most famous, with dominant performance by real-life kirare-yaku Seizo Fukumoto.', 'tt3290882', '/fBx2Z4VwFpVzlM1nCSVbtIXFkOQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5654, 'Fugitive Pieces', False, True, False, False, 2007, 7, 'A child escapes from Poland during World War II and first heads to Greece before coming of age in Canada.', 'tt0765451', '/aKkSgPBmbJ6mDSfxRzTFpqSQCzE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5655, 'Sister My Sister', False, True, False, False, 1994, 6.6, 'The film is based on a true incident in Le Mans, France in 1933 called the Papin murder case, where two sisters brutally murdered their employer and her daughter. The murder shocked the country, and there was much speculation about the sisters, including allegations that they were having an incestous lesbian affair with each other.', 'tt0111205', '/5qUy8njNJPy8X8cqeDU3JmD2Fi6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5656, 'All This Panic', False, True, False, False, 2017, 6.8, 'Shot over a three-year period with unparalleled intimacy and access, ALL THIS PANIC is a feature length documentary that takes an intimate look at the interior lives of a group of teenage girls as they come of age in Brooklyn. A potent mix of vivid portraiture and vérité, we follow the girls as they navigate the ephemeral and fleeting transition between childhood and adulthood.', 'tt5590718', '/ucR8AKvGuhLzs2KvkgKYIwuu6Fy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5657, 'De-Lovely', False, True, False, False, 2004, 6.6, 'From Paris to Venice to Broadway to Hollywood, the lives of Cole Porter and his wife, Linda were never less than glamorous and wildly unconventional. And though Cole''s thirst for life strained their marriage, Linda never stopped being his muse, inspiring some of the greatest sons of the twentieth century.', 'tt0352277', '/QBqiBQW7MGqlDSyvid4fKzS0Xz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5658, 'Will Ferrell: You''re Welcome America - A Final Night with George W. Bush', False, True, False, False, 2009, 7.1, 'Will Ferrell stars in a stand-up comedy as George W. Bush in this take on the former President of the United States. After playing George W. Bush on Saturday Night Live for many years, funny man Will Ferrell brings his impression to Broadway to send up the 43rd President of the United States of America.', 'tt1386011', '/gwFHzglothAAME8s5NkV29rEISJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5660, 'Afternoon Delight', False, True, False, False, 2013, 5.7, 'Rachel is a quick-witted and lovable stay-at-home mom. Frustrated with the realities of preschool auctions, a lackluster sex life and career that''s gone kaput, Rachel visits a strip club to spice up her marriage and meets McKenna, a stripper she adopts as her live-in nanny.', 'tt2312890', '/y31eDXApakpZ8ox1klX8GfeyBYs.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5661, 'Author: The JT LeRoy Story', False, True, False, False, 2016, 7.1, 'New York magazine’s October 2005 issue sent shockwaves through the literary world when it unmasked “it boy” wunderkind JT LeRoy, whose tough prose about his sordid childhood had captivated icons and luminaries internationally. It turned out LeRoy didn’t actually exist. He was dreamed up by 40-year-old San Francisco punk rocker and phone sex operator, Laura Albert.', 'tt5278462', '/a1GvWz27yyGwf0HIUswckOVVSbu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5662, 'Time Regained', False, True, False, False, 1999, 6.7, 'Marcel Proust (1871-1922) is on his deathbed. Looking at photographs brings memories of his childhood, his youth, his lovers, and the way the Great War put an end to a stratum of society. His memories are in no particular order, they move back and forth in time. Marcel at various ages interacts with Odette, with the beautiful Gilberte and her doomed husband, with the pleasure-seeking Baron de Charlus, with Marcel''s lover Albertine, and with others; present also in memory are Marcel''s beloved mother and grandmother. It seems as if to live is to remember and to capture memories is to create a work of great art. The memories parallel the final volume of Proust''s novel.', 'tt0189142', '/1ZbBR18GU7XpVfooWV9NsrV4RGV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5663, 'My Favorite Brunette', False, True, False, False, 1947, 6.8, 'Baby photographer Ronnie Jackson, on death row in San Quentin, tells reporters how he got there: taking care of his private-eye neighbor''s office, Ronnie is asked by the irresistible Baroness Montay to find the missing Baron. There follow confusing but sinister doings in a gloomy mansion and a private sanatorium, with every plot twist a parody of thriller cliches.', 'tt0039645', '/5e4bdXZKUl1AH755vICEcSiOocL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5666, 'Cuban Fury', False, True, False, False, 2014, 6.2, 'Beneath Bruce Garrett''s under-confident, overweight exterior, the passionate heart of a salsa king lies dormant. Now, one woman is about to reignite his Latin fire.', 'tt2390237', '/io0LZoCtq63hUWd6d3x5FoalgDj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5667, 'Body Bags', False, True, False, False, 1993, 6.1, 'Three tales are told, each one increasingly terrifying. The first tells the story of a woman being stalked by an axe-weilding maniac. The second is the story of a man who pays the ultimate price for a beautiful head of hair. The final tale shows what it is like to see life through the eyes of a killer.', 'tt0106449', '/n7wDjPLfs6SCX5jmFY2wwtLjtxZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5669, 'Gone in 60 Seconds', False, True, False, False, 1974, 6.5, 'Insurance investigator Maindrian Pace and his team lead double-lives as unstoppable car thieves. When a South American drug lord pays Pace to steal 48 cars for him, all but one, a 1973 Ford Mustang, are in the bag. As Pace prepares to rip-off the fastback, codenamed "Eleanor", in Long Beach, he is unaware that his boss has tipped off the police after a business dispute.', 'tt0071571', '/qYyeAodAOe4nHuzkHdN35ValzLT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5670, 'Super High Me', False, True, False, False, 2007, 6.1, 'Determined to find out the true effects of marijuana on the human body, stand-up comedian and former Stoner of the Year Doug Benson documents his experience avoiding pot for 30 days and then consuming massive amounts of the drug for 30 days. More than just an amusing story of one man''s quest to get superhigh, this documentary also examines the hotly contested debate over medical marijuana use.', 'tt1111833', '/1dejvHkcSYvVMMWhl59HLn1ShFQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5671, '1:54', False, True, False, False, 2016, 6.6, 'Tim is a bit of a loner. At school he gravitates towards another shy boy and they become friends. But the verbal and physical bullying by a gang of kids, as well as an aggressive use of social media, unsettle Tim’s new mate and he backs away from him – an act of cowardice which will have serious repercussions. As a result of Tim’s unwillingness to snitch, the bullies appear to get away with their actions, none more so than the gang’s leader Jeff. He’s one of the school’s star athletes, who seems guaranteed a place in the national championships with an 800m time of 1.54 minutes. When Tim decides to start training in order to go up against Jeff, even having a girlfriend as cover, his enemies seem willing to go to any lengths to stop him.', 'tt5834036', '/j28i5dr6zqmZSM2u1BkjYxxV5Zv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5673, 'Noble', False, True, False, False, 2014, 6.8, 'Christina Noble overcomes the harsh difficulties of her childhood in Ireland to discover her destiny on the streets of Saigon. A true story.', 'tt2626090', '/yPEBvRVN3qBqTuseioKW9UlHAHE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5674, 'The Greatest Story Ever Told', False, True, False, False, 1965, 6.6, 'All-star epic retelling of Christ''s life.', 'tt0059245', '/sGtPyaPeMULx3g59jXz2u7cwbN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5676, 'Southwest of Salem: The Story of the San Antonio Four', False, True, False, False, 2016, 7.1, 'In 1994, four women were accused, tried, and convicted of the heinous sexual assault of two young girls—as one newscaster puts it, “the modern version of the witchcraft trials.” Twenty years later, the four women have maintained their innocence, insisting that the accusations were entirely fabricated, and borne of homophobic prejudice and a late-’90s mania about covens, cults, and child abuse.', 'tt5333812', '/pewfnkwtvB7lx4OZnxR6LGCY4UD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5678, 'TransFatty Lives', False, True, False, False, 2015, 8, 'Diagnosed with ALS and given 2 to 5 years to live, New York City DJ, internet personality, and filmmaker, TransFatty, brings his camera along for the ride in this unconventional examination of life, death, and everything in between.', 'tt4221762', '/8Ms5PnOhoqbWC4mw6OD77mRE1w9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5679, 'The Summit', False, True, False, False, 2012, 6.9, 'The Summit is a 2012 documentary film about the 2008 K2 disaster directed by Nick Ryan. It combines documentary footage with dramatized recreations of the events of the 2008 K2 disaster. On the way to and from the summit, eleven climbers died during a short time span creating one of the worst catastophes in climbing history. Much of the documentary footage was captured by Swedish mountaineer Fredrik Sträng. Sträng was planning to do a Documentary which was aborted due to the fact that he did not reach the summit. The footage was still valuable to help solving what really did happen since all the climbers had different stories about what happened.', 'tt2363471', '/n7hRnGpAIT75xSyBnTBPfAZQ213.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5680, 'Animals', False, True, False, False, 2014, 6.3, 'Pol is a teenager with a seemingly calm life: he lives with his brother and is still in high school. However, Pol has a secret: Deerhoof, a cuddly teddy bear who thinks, moves around and with whom he shares his best moments. Life goes on as normal until the arrival of Ikari, an enigmatic student who seems to be hiding something. Pol is fascinated by his new classmate and attracted to the dark side he harbors. An inexplicable death and a series of strange events will transform the meek student''s routine into a fantastic adventure which will take over their lives. Animals is a story of teenage love, of sacrifice, of the tragedy of two clashing worlds. A Tale in which the power of imagination blurs it´s borders with reality and where death turns out to be an ending as tempting as inevitable.', 'tt1298530', '/xTQULTGtQ5fb2Yk16W9y2JyJhKV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5682, 'George Carlin: Jammin'' in New York', False, True, False, False, 1992, 8.7, 'When George Carlin is asked which HBO concert is his favorite, his answer is always, "Jammin’ In New York." The show, taped at the Paramount Theater in Madison Square Garden and winner of the 1992 CableACE Award, is a perfect blend of biting social commentary and more gently-observed observational pieces.', 'tt0246643', '/vnUzbdtqkudKSBgX0KGivfpdYNB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5683, 'Ashby', False, True, False, False, 2015, 6.4, 'When new kid in town Ed Wallis is given an assignment to interview an older person, he turns to his mysterious neighbor, Ashby Holt for help.  That new connection leads to unexpected journeys for both of them, as Ashby – who turns out to be a retired CIA assassin – deals with a terminal prognosis, and Ed deals with adjusting to life with his newly single mom and developing relationship with a brainy classmate, Eloise.', 'tt3774466', '/hQDEvfqoYD33JnMWKccVDeWOkVG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5684, 'The Bigamist', False, True, False, False, 1953, 6.8, 'Mr Graham, a travelling salesman, is married to two women, Eve and Phyllis. Eventually, the truth comes out.', 'tt0045557', '/tdn6WDVVc8w5CfEX5P6jW8Re2Xy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5685, 'Madeinusa', False, True, False, False, 2006, 6.8, 'Madeinusa is a girl aged 14 with a sweet Indian face who lives in an isolated village in the Cordillera Blanca Mountain range of Peru. This strange place is characterized by its religious fervor. From Good Friday at three o''clock in the afternoon (the time of day when Christ died on the cross) to Easter Sunday, the whole village can do whatever it feels like. During the two holy days sin does not exist: God is dead and can''t see what is happening. Everything is accepted and allowed, without remorse. Year after year, Madeinusa and her sister Chale, and her father Don Cayo, the Mayor and local big shot, maintain this tradition without questioning it. However, everything changes with the arrival in the village of Salvador, a young geologist from Lima, who will unknowingly change the destiny of the girl.', 'tt0476298', '/hiDXPO4k1dozDCBq08LBRczdCPS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5687, 'Sons of Perdition', False, True, False, False, 2010, 6.9, 'An inside look at polygamist teens who have become religious refugees in mainstream America.', 'tt1296893', '/tlA4TYvaOA2a9VWcHXVQ7myAVi3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5688, 'Northern Soul', False, True, False, False, 2014, 6.4, 'Set in 1974, an authentic and uplifting tale of two friends whose horizons are opened up by the discovery of black American soul music.', 'tt1837613', '/nFRFJNKrlGFELT7XMt6RarW0WDV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5690, 'Moll Flanders', False, True, False, False, 1996, 6.5, 'The daughter of a thief, young Moll is placed in the care of a nunnery after the execution of her mother. However, the actions of an abusive priest lead Moll to rebel as a teenager, escaping to the dangerous streets of London. Further misfortunes drive her to accept a job as a prostitute from the conniving Mrs. Allworthy. It is there that Moll first meets Hibble, who is working as Allworthy''s servant but takes a special interest in the young woman''s well-being. With his help, she retains hope for the future, ultimately falling in love with an unconventional artist who promises the possibility of romantic happiness.', 'tt0117071', '/lHVpzsvjLU7qSCB8UWFqIAavSRu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5691, 'New York, I Love You', False, True, False, False, 2008, 6.2, 'Ten vignettes in New York City: a pickpocket meets his match; a young Hasidic woman, on the eve of her marriage, reveals herself to an Indian businessman; a writer tries a pick-up line; an artist seeks a model; a composer needs to read; two women connect; a man takes a child to Central Park; lovers meet; a couple takes a walk on their anniversary; a kid goes to the prom with a girl in a wheelchair; a retired singer contemplates suicide. There are eight million stories in the naked city: these have been ten of them.', 'tt0808399', '/541jVBSKFjVX7iSWsKJlMVhvLPq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5693, 'Undertow', False, True, False, False, 2004, 6.6, 'Undertow stars Scott Brady as a gambler just out of wartime military service. No longer interested in wagers and speculations, Brady wants only to open up a mountain vacation lodge. Before this can take place, Brady is framed for murder, and forced to hide out in the home of Peggy Dow. With the help of Dow and a policeman friend, Brady searches for the real murderer. Watch carefully in Undertow and you''ll spot new Universal contractee "Roc" Hudson as a plainclothes detective.', 'tt0042007', '/vL0lekVKRmfTADzyhMgEUjao3lQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5697, 'Our Nixon', False, True, False, False, 2013, 6.7, 'Never before seen Super 8 home movies filmed by Richard Nixon''s closest aides - and convicted Watergate conspirators - offer a surprising and intimate new look into his Presidency.', 'tt2282662', '/wAErrwcyBZSN7RSM2uQUi1Naol0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5698, 'Life of a King', False, True, False, False, 2013, 6.9, 'Life of a King is the true story of one man''s mission to give inner city kids of Washington DC something he never had - a future. After being incarcerated for eighteen years, Eugene Brown established the Big Chair Chess Club to get kids off the streets and working towards lives they never believed they were capable of. This is his inspirational story.', 'tt2708254', '/o8j2hA86rDoioGr5CVZHVHT7IrF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5699, 'Free and Easy', False, True, False, False, 2017, 6.3, 'Gopher City Kansas hosts a beauty contest. The winner, Elvira Plunkett, and her mother go to Hollywood. The Chamber of Commerce also provides Elvira with an agent, Gopher City''s own Elmer J. Butz. Elmer likes Elvira and the shy Elvira likes him, but Mrs. Plunkett, a formidable woman, has little use for hapless Elmer. On the train west, they meet movie star Larry Mitchell, who takes a shine to Elvira and helps her meet MGM directors once they get to Tinsel Town. Elmer, meanwhile, wants to help Elvira with her career and he also wants to be her man. Movie stardom does come to the Gopher City entourage, but to whom is a surprise. And who will win the lovely Elvira''s hand?', 'tt0020902', '/31UrsNkQOBNgaGkE7FF8oD6ONU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5700, 'Sordid Lives', False, True, False, False, 2000, 6.8, '"Sordid Lives" is about a family in a small Texas town preparing for the funeral of the mother. Among the characters are the grandson trying to find his identity in West Hollywood, the son who has spent the past twenty-three years dressed as Tammy Wynette, the sister and her best friend (who live in delightfully kitschy homes), and the two daughters (one strait-laced and one quite a bit looser).', 'tt0204640', '/b6Kj1ioQNUnGzothJMBwqbflA8P.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5701, 'Something Like Happiness', False, True, False, False, 2005, 7.1, 'Over the course of a year, in a small town scarred and beaten down by industrialization, Tonda and Monika, friends since childhood, come to experience what life might be like if they could be with one another – which they seemingly can''t. Or can they?', 'tt0406098', '/je5yBCtf4AkCVtIxB2WMimR3jEV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5702, 'Paint Your Wagon', False, True, False, False, 1969, 6.6, 'A Michigan farmer and a prospector form a partnership in the California gold country. Their adventures include buying and sharing a wife, hijacking a stage, kidnapping six prostitutes, and turning their mining camp into a boom town. Along the way there is plenty of drinking, gambling, and singing. They even find time to do some creative gold mining.', 'tt0064782', '/jKoMqlXw4wQSjEJwDaJcg3rRp5S.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5703, 'Toomelah', False, True, False, False, 2011, 6.1, 'In a remote Aboriginal community, 10 year old Daniel yearns to be a gangster, like the male role models in his life. Skipping school, getting into fights and running drugs for Linden, who leads the main gang in town.', 'tt1905071', '/upin6TJv2Lxk2M5v6DnuImXOcOO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5704, 'Future Shock! The Story of 2000AD', False, True, False, False, 2014, 7.2, 'A  long overdue documentary that tells the story of 2000AD, the unsung cult hero of the comics industry. This film will celebrate and pay respect to the comic and explore its importance and influence on contemporary pop culture.', 'tt2822584', '/bwVWBA9TceQWIrXRpjL63WtrUng.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5706, 'Radiant City', False, True, False, False, 2007, 7, 'Since the end of World War II, one of kind of urban residential development has dominate how cities in North America have grown, the suburbs. In these artificial neighborhoods, there is a sense of careless sprawl in an car dominated culture that ineffectually tries to create the more organically grown older communities. Interspersed with the comments of various experts about the nature of suburbia', 'tt0832937', '/6d0qv4WPgC6VeBkFx4U3pPPmKpt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5707, 'Winter Kills', False, True, False, False, 1979, 6.1, '19 years after President Timothy Keegan was assassinated, his brother Nick discovers a dying man claiming to have been the gunman. While trying to avoid his wealthy and domineering father''s attempts to control his actions, Nick follows the clues that have been handed to him. As he progresses, it becomes increasingly difficult to discern the real trails from the dead ends, and increasing dangerous as unknown parties try to stop Nick from uncovering the truth.', 'tt0080139', '/dGlXYtPYeM3F2RUdJIOL3G7sDHr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5709, 'Fear and Desire', False, True, False, False, 1953, 5.5, 'A ficticious war in an unidentified country provides the setting for this drama. Four soldiers survive the crash-landing of their plane to find themselves in a forest six miles behind enemy lines. The group, led by Lt. Corby, has a plan: They''ll make their way to a nearby river, build a raft, and then, under cover of night, float back to friendly territory. Their plans for getting back safely are sidetracked by a young woman who stumbles across them as they hide in the woods, and by the nearby presence of an enemy general who one member of the group is determined to kill.', 'tt0045758', '/4mD3LNkXayoI0q0qxNH5cXtc0em.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5710, 'Essential Killing', False, True, False, False, 2010, 6.1, 'A Taliban soldier struggles to survive after he escapes his captors and flees into the Polish countryside.', 'tt1561768', '/gwLUPEqjHz55khkXeQB3hOYx1ao.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5713, '1', False, True, False, False, 2013, 8, 'Set in the golden era of Grand Prix Racing ''1'' tells the story of a generation of charismatic drivers who raced on the edge, risking their lives during Formula 1''s deadliest period, and the men who stood up and changed the sport forever.', 'tt2518788', '/jo7KYaK7Rf7Ru6uhbDHAzMhjUG4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5714, 'Becoming Cary Grant', False, True, False, False, 2017, 6.6, 'For the first time one of Hollywood''s greatest stars tells his own story, in his own words. From a childhood of poverty to global fame, Cary Grant, the ultimate self-made star, explores his own screen image and what it took to create it.', 'tt5704748', '/tkvmbikfwJ0blIatdITYPfePwko.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5715, 'Sex and Broadcasting', False, True, False, False, 2014, 6.7, 'SEX AND BROADCASTING is a feature length documentary about New Jersey''s WFMU, the world''s strangest and most unique radio station, and one man''s attempt to keep it alive in the face of recession, the persistent threat of commercial media, and the challenges that come with keeping a rebellious group of outsiders together.', 'tt3548948', '/dk5KcUZy1OdZ5nwe6qXI9CFLdyn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5717, 'Tai Chi Zero', False, True, False, False, 2012, 6.1, 'In legendary Chen Village, everyone is a martial arts master, using their powerful Chen Style Tai Chi in all aspects of their lives. Lu Chan has arrived to train, but the villagers are forbidden to teach Chen Style to outsiders, and do their best to discourage him by challenging him to a series of fights. Everyone, from strong men to young children, defeats him using their Tai Chi moves. But when a man from the village''s past returns with a frightening steampowered machine and plans to build a railroad through the village at any costs, the villagers realize they may have no choice but to put their faith in Lu Chan... who has a secret power of his own.', 'tt1981080', '/5NN4korfB2UQPN4U18JTiMt6ZBl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5718, 'Howl', False, True, False, False, 2015, 5.4, 'When passengers on a train are attacked by a creature, they must band together in order to survive until morning.', 'tt2393827', '/XcwP3VGTRVHjc4doNP8w1fdOJ7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5719, 'Creation', False, True, False, False, 2009, 6.7, 'A psychological, heart-wrenching love story that provides a unique and inside look at Charles Darwin. Torn between faith and science, he struggles to finish his legendary book "On the Origin of the Species," which goes on to become the foundation for evolutionary biology.', 'tt0974014', '/vS6ne76vjRH7Z88rAqYLCcQatB4.jpg');
