insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5725, 'We are Twisted Fucking Sister!', False, True, False, False, 2016, 7.1, 'In 1984, American heavy metal band Twisted Sister became a global sensation. For 30 years, they been synonymous with hairspray, women''s clothing and tasteless album covers. Until now. Ten years ago, director Andrew Horn was granted access to the archives of Twisted Sister founder Jay French and in We are Twisted fucking Sister he explores the decade that preceded their breakthrough.', 'tt3538328', '/mRGTIjrb1vbvkWGocq4beKcdvzY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5727, 'Death Rides a Horse', False, True, False, False, 1967, 7.1, 'Death Rides a Horse (aka Da uomo a uomo, or As Man to Man) is a 1967 spaghetti western directed by Giulio Petroni, written by Luciano Vincenzoni, and starring Lee Van Cleef and John Phillip Law. Bill Meceita, a boy whose family was murdered in front of him by a gang, sets out 15 years later to exact revenge. On his journey, he finds himself continually sparring and occasionally cooperating with Ryan, a gunfighter on his own quest for vengeance, who knows more than he says about Bill''s tragedy. The film has lapsed into public domain.', 'tt0064208', '/aTk7W6gH4CdDIE1E6veGPwznDcP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5728, 'Q', False, True, False, False, 1982, 6, 'New York police are bemused by reports of a giant flying lizard that has been spotted around the rooftops of New York, until the lizard starts to eat people. An out-of-work ex-con is the only person who knows the location of the monster''s nest and is determined to turn the knowledge to his advantage, but will his gamble pay off or will he end up as lizard food?', 'tt0084556', '/hm5mvCN7IMAFjtkQwL4U1qwoJ4c.jpg');
