insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5760, 'Pandora''s Promise', False, True, False, False, 2013, 7.4, 'The atomic bomb, the specter of a global nuclear holocaust, and disasters like Fukushima have made nuclear energy synonymous with the darkest nightmares of the modern world. But what if everyone has nuclear power wrong? What if people knew that there are reactors that are self-sustaining and fully controllable and ones that require no waste disposal? What if nuclear power is the only energy source that has the ability to stop climate change?', 'tt1992193', '/hZKReG9s8wQAAckxwM0lRNoufT5.jpg');
