insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5763, 'Mr. Nice', False, True, False, False, 2010, 6.4, 'Biopic about 1970s Welsh marijuana trafficker Howard Marks, whose inventive smuggling schemes made him a huge success in the drug trade, as well as leading to dealings with both the IRA and British Intelligence. Based on Marks'' biography with the same title.', 'tt1183911', '/nO6PXkhltFcmRd4nNKJsEUdZvJH.jpg');
