insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5766, 'Gulliver''s Travels', False, True, False, False, 1939, 6.7, 'Gulliver washes ashore on Lilliput and attempts to prevent war between that tiny kingdom and its equally-miniscule rival, Blefiscu, as well as smooth the way for the romance between the Princess and Prince of the opposing lands. In this he is alternately aided and hampered by the Lilliputian town crier and general fussbudget, Gabby.', 'tt0115195', '/mO6AdYx5sWPqOIuv4iNza8AcReS.jpg');
