insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5781, 'Goin'' South', False, True, False, False, 1978, 6.2, 'Henry Moon is captured for a capital offense by a posse when his horse quits while trying to escape to Mexico. He finds that there is a post-Civil War law in the small town that any single or widowed woman can save him from the gallows by marrying him.', 'tt0077621', '/w8Bh4XSkFpXx0A5oQEqRSvcaSKG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5782, 'Support Your Local Gunfighter', False, True, False, False, 1971, 6.9, 'A con artist arrives in a mining town controlled by two competing companies. Both companies think he''s a famous gunfighter and try to hire him to drive the other out of town.', 'tt0067809', '/kDxgzFwLrLubxcKorV7BSQxG3NP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5783, 'Double Happiness', False, True, False, False, 1994, 7, 'Jade Li is a feisty, 20-something Chinese Canadian, trying to achieve that happy medium between giving in to her parent''s wishes and fulfilling her own needs and desires - double happiness. Naturally, something''s got to give and when love beckons in the shape of Mark, a white university student, the facade of the perfect Chinese daughter begins to slip.', 'tt0109655', '/woRZTcrRbvcW24zbNcCbVi4QfEF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5784, 'Before I Disappear', False, True, False, False, 2014, 7.2, 'Based on the 2013 Academy Award® winning short film ''Curfew''. At the lowest point of his life, Richie gets a call from his estranged sister, asking him to look after his eleven-year-old niece, Sophia, for a few hours.', 'tt3060492', '/2X7KDL2p7nrWnJA8HJrnU7DT0Pl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5786, 'The Immaculate Conception of Little Dizzle', False, True, False, False, 2010, 6.2, 'After he loses his high-paying job, Dory takes a gig as a night janitor in order to pay rent. Alone late at night inside a market research firm, he soon discovers the company is experimenting on their other janitors ...', 'tt1039786', '/5AYXkvKjxnYTcrLuFpxFHGFHmW9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5787, 'Rakka', False, True, False, False, 2017, 7.4, '"Rakka" is the story of broken humanity following the invasion of a technologically superior alien species. Bleak harrowing and unrelenting, the humans we meet must find enough courage to go on fighting.', 'tt6990734', '/4wVeP9BHsQul7CRgdrfiUt1Rds1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5789, 'GirlHouse', False, True, False, False, 2014, 5.5, 'In an attempt to make some extra cash while away at College, Kylie moves into a house that streams content to an X-rated website. After a deranged fan hacks in to determine the house''s location, she finds herself in a terrifying fight for her life.', 'tt2577172', '/4zR3QYWoeY2mTwVWnVxUQQptqdM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5790, 'Captain Kidd', False, True, False, False, 1945, 6.4, 'The unhistorical adventures of pirate Captain Kidd revolve around treasure and treachery. In 1699, William Kidd (Charles Laughton), a pirate who has recently captured the ship The Twelve Apostles and killed its crew, presents himself at the court of King William III (Henry Daniell) as an honest shipmaster seeking royal backing. With this backing, he recruits a crew from the inmates of Newgate and Marshalsea prisons, promising them a royal pardon at the end of their voyage. Among the new recruits is the quarrelsome, though cultured Adam Mercy (Randolph Scott), whom Kidd makes his new master gunner because of his claimed prior service with a famous pirate.', 'tt0037576', '/kQg1K5dics5W7eQe1D8GKaj6hfA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5792, 'Wild Horse, Wild Ride', False, True, False, False, 2012, 7.6, '100 Days. 100 gritty souls. 100 wild mustangs. That''s the premise of the annual Extreme Mustang Makeover, a competition that challenges horse trainers - both pros and amateurs - to each tame a totally wild horse in order to get it adopted into a better life beyond U.S. Government corrals. Intimately following a handful of participants in the 2009 event from their first uneasy meeting with their horses and up through the competition and adoption, the film documents the deep and profound bonds that can develop between man and beast.', 'tt2113113', '/1ZY6kzejSmmz3pY5pSu6haQlNWZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5793, 'The Patsy', False, True, False, False, 1964, 6.3, 'A hillarious movie from director King Vidor, with Marion Davies AND Marie Dresler! As the perpetually feuding mother and daughter, Dresler and Davies are not only side-splittingly funny, they are actually quite touching.', 'tt0019258', '/nipGLQV0TNrdN4T2w25B0NTUiKX.jpg');
