insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5795, 'Bodysong', False, True, False, False, 2003, 6.9, 'Documentary footage from various sources, set to music. Showing the whole of human life, from birth to death and beyond.', 'tt0349154', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5797, 'Flying Swords of Dragon Gate', False, True, False, False, 2011, 6, 'Flying Swords of Dragon Gate picks up three years after the infamous Dragon Inn was burnt down in the desert when its innkeeper Jade vanished. A new gang of marauders had taken over - innkeepers by day and treasure hunters by night. The inn is the rumoured location of a lost city buried under the desert, and its hidden treasure would only be revealed by a gigantic storm every 60 years. The gang used the inn as a front to locate the lost treasure.', 'tt1686784', '/kwe2XByBvoTEYZ7up4UfwBhF5q1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5798, 'Khakee', False, True, False, False, 2004, 7.4, 'Anant Shrivastav is assigned to escort a dangerous terrorist to Mumbai. But someone doesn''t want him to make it.', 'tt0347332', '/dMsqwWFl51t8jXR0tWzjEuO8H4l.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5799, 'For Those in Peril', False, True, False, False, 2013, 6.4, 'Aaron, a young misfit living in a remote Scottish fishing community, is the lone survivor of a strange fishing accident that claimed the lives of five men including his older brother. Spurred on by sea-going folklore and local superstition, the village blames Aaron for this tragedy, making him an outcast amongst his own people. Steadfastly refusing to believe that his brother has died, he sets out to recover him and the rest of men.', 'tt2315628', '/eOhv6aTB2bbnrPRImQva8Hh1KQL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5800, 'The Greasy Strangler', False, True, False, False, 2016, 5.7, 'Ronnie runs a Disco walking tour with his son, Brayden. When a sexy woman takes the tour, it begins a competition between father and son for her love. It also signals the arrival of an oily strangler who stalks the streets at night.', 'tt4381236', '/A6rEKUIDoJebQygLr7hxVCoe8Fz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5801, 'The Competition', False, True, False, False, 1980, 6.6, 'The movie centers on a piano competition whose winner is assured of success. It is Paul''s last chance to compete, but newcomer Heidi may be a better pianist. Can romance be far away? Will she take a dive despite the pressure to win from her teacher, Greta, or will she condemn Paul to obscurity?', 'tt0080556', '/14iMtdJY3HjwrmqPUZ55F4LzuCd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5802, 'Our Daily Bread', False, True, False, False, 1934, 7.1, 'Welcome to the world of industrial food production and high-tech farming! To the rhythm of conveyor belts and immense machines, the film looks without commenting into the places where food is produced in Europe: monumental spaces, surreal landscapes and bizarre sounds - a cool, industrial environment which leaves little space for individualism.', 'tt0765849', '/kdW7HPJqhRQ2HRU9ZurbDbpTyj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5803, 'Jeff Dunham: Spark of Insanity', False, True, False, False, 2007, 8, 'Combining his amazing talent and his unorthodox sense of humor, Jeff Dunham returns, yet again, with a hilarious stand-up comedy and ventriloquist performance. Starting off with the infamously known Walter, scrutinizing every bit of today''s American society. Followed by two new characters, Achmed the Dead Terrorist, who continuously threatens the crowd with Silence and Death, and Melvin the Superhero.', 'tt1143143', '/8HB4miftuPSJr7kCaS9MenC3PJt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5804, 'Little White Lie', False, True, False, False, 2014, 7.2, 'Tells Lacey Schwartz''s story of growing up in a typical upper-middle-class Jewish household in Woodstock, NY, with loving parents and a strong sense of her Jewish identity — despite the open questions from those around her about how a white girl could have such dark skin. She believes her family''s explanation that her looks were inherited from her dark-skinned Sicilian grandfather. But when her parents abruptly split, her gut starts to tell her something different.  At age of 18, she finally confronts her mother and learns the truth: her biological father was not the man who raised her, but a black man named Rodney with whom her mother had had an affair.', 'tt2088865', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5805, 'True Confessions', False, True, False, False, 1981, 6.3, 'A cop clashes with his priest brother while investigating the brutal murder of a young prostitute', 'tt0083232', '/qwTwHKesH4QcvSBkS1dE89AIFst.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5806, 'Siddharth', False, True, False, False, 2013, 7.3, 'A chain-wallah from Delhi travels across India in search of his missing son, in the hopes that whoever took him, returns him unharmed.', 'tt2932606', '/xY4YvKVbVePmZVMdZqwxq8Mjukz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5807, 'Paper Man', False, True, False, False, 2009, 6.6, 'A coming-of-middle-age comedy that chronicles the unlikely friendship between failed author Richard Dunne and a Long Island teen who teaches him a thing or two about growing up, all under the disapproving eye of his long-suffering wife and his imaginary Superhero friend.', 'tt0437405', '/BKaXYV4lujFxmzhwpLrMLGsYx6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5810, 'The Personals', False, True, False, False, 1998, 7.1, 'The Personals is a movie for anyone who hasn''t yet found what they''re looking for or who remembers how tough it can be to find it. Rene Liu plays an eye doctor who''s become disaffected with her present life and is craving something more – namely love and marriage. She''s attractive, has a good job, and a decent apartment. Up until now she has done everything she''s supposed to do, but it just hasn''t worked for her. She''s still alone. Now, she decides to take the extreme measure of advertising for a husband in the personals. The search leads her down the slippery slope of the modern dating scene whose universal quirkiness transcends the boundaries of all industrialized societies.', 'tt0212423', '/4Yly1AkVOL8js3vxCWPCw7NjXKh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5811, 'Psychomania', False, True, False, False, 1973, 5.8, 'A gang of young people call themselves the Living Dead. They terrorize the population from their small town. After an agreement with the devil, if they kill themselves firmly believing in it, they will survive and gain eternal life. Following their leader, they commit suicide one after the other, but things don''t necessarily turn out as expected...', 'tt0067630', '/vWhxbi9Cv7qo7Wyqh7jdKLEn6qf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5812, 'Beautiful Darling', False, True, False, False, 2010, 7.3, 'James Rasin''s documentary “Beautiful Darling” honors American Transgender actress and best-known Warhol Superstar, Candy Darling, and her all-too-brief life and career, with a combination of current and vintage interview material, rarely seen archival photos and footage, and extracts from Darling''s movies.', 'tt0960730', '/1EOUfqY4D8FMVz03nIlpZfcWkjQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5813, 'Double Impact', False, True, False, False, 1991, 5.6, 'Jean Claude Van Damme plays a dual role as Alex and Chad, twins separated at the death of their parents. Chad is raised by a family retainer in Paris, Alex becomes a petty crook in Hong Kong. Seeing a picture of Alex, Chad rejoins him and convinces him that his rival in Hong Kong is also the man who killed their parents. Alex is suspicious of Chad, especially when it comes to his girlfriend.', 'tt0101764', '/tmzwvSqoMC37Tgqwj4mA2dHNSmw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5814, 'The Stone Roses: Made of Stone', False, True, False, False, 2013, 7.3, 'A documentary about the English alternative rock band, The Stone Roses. Meadows interweaves archive film, intimate behind-the-scenes footage and never-before-seen material, delivering the definitive account of the band and their music. He was also granted unprecedented access to their rehearsals for the summer 2012 Manchester concerts. A momentous occasion in modern music, these were the first gigs performed by The Stone Roses in 16 years.', 'tt2126403', '/lW4uD2opxmaAFcL9nx4GuFDcnsQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5815, 'Bloodsucking Bastards', False, True, False, False, 2015, 5.7, 'An action-packed horror comedy, BLOODSUCKING BASTARDS stars Fran Kranz as Evan, a dutiful and overworked employee stuck at a soul-killing corporation with his beautiful co-worker and girlfriend Amanda (Emma Fitzpatrick) and his slacker best friend Tim (Joey Kern). Evans world begins to crumble when Amanda dumps him and his boss Ted (Joel Murray) hands his coveted promotion to his nemesis Max (Pedro Pascal). When his office mates start going through disturbing changes, Evan must find a way to stop the evil brewing admist the cubicles, and rescue his workplace pals before his life and career go from dead-end... to just dead.', 'tt3487994', '/oSLOjoNTF8TlDPItk2SWrtABlZk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5817, 'Freedom', False, True, False, False, 2009, 7.3, 'Ron, a young man in his late teens or early 20s, but emotionally younger, has no visible, employable assets, yet rails at his status in life -- blaming everyone for the fact that his dreams are not coming true.', 'tt0083971', '/coR1EMRCnBc8r2X8de28Fcf5qI3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5819, 'An Occurrence at Owl Creek Bridge', False, True, False, False, 1962, 8.1, 'During the Civil War, a Southern civilian is about to be hanged for attempting to sabotage a railway bridge. When the execution takes place from the bridge, the rope breaks and he begins his escape toward home.', 'tt0056300', '/biDEwiNvIZrc36NmpmjJD12wOJU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5820, 'Dil Bole Hadippa!', False, True, False, False, 2009, 4.5, 'Veera, a fire-cracker of a girl who lives in a small village but dreams of playing cricket in the big league. Rohan is an accomplished captain of a county cricket team in England. Rohan returns to India to captain his father''s cricket team which has been losing for the last 8 years. In a Village where girls don''t play Cricket, Veera has to put on a put on a turban &amp; beard to live her dream.', 'tt1202540', '/3AA4yN0ivMIHEzQUSBsjWMSmt2q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5821, 'Creepshow 2', False, True, False, False, 1987, 6.1, 'EC Comics-inspired weirdness returns with three tales. In the first, a wooden statue of a Native American comes to life...to exact vengeance on the murderer of his elderly owners. In the second, four teens are stranded on a raft on a lake with a blob that is hungry. And in the third, a hit and run woman is terrorized by the hitchhiker she accidentally killed...or did she really kill him?', 'tt0092796', '/2lZieUGwOMndAyM5pqa4d8KWXZM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5822, 'Lake Tahoe', False, True, False, False, 2008, 6.7, 'A story of a teenager and the strange events that take place in his small town.', 'tt1101675', '/A4elkkcqbFy8eAiAtFQi7V06tJZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5824, 'Louisiana Story', False, True, False, False, 1948, 6.7, 'The idyllic life of a young Cajun boy and his pet raccoon is disrupted when the tranquility of the bayou is broken by an oil well drilling near his home.', 'tt0040550', '/4WqxYBY1OvGhtpyAKam7Cs6GkpC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5825, 'Fists of Legend', False, True, False, False, 2013, 6.8, 'On a television show billed as “the greatest fight show on Earth,” eager amateurs with lots to prove boldly step into the ring and expect to leave bloody but victorious. The ratings hungry producers seek out “legendary” but largely forgotten streetfighters and mine their desperation for recaptured glory and redemption into ratings gold.', 'tt2828840', '/m4SynjszzAnIawvitT1t6OhI6ne.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5826, '11 Flowers', False, True, False, False, 2012, 7.1, 'A coming-of-age story set during China''s Cultural Revolution. 11 year old Wang Han finds himself entangled with a fugative and struggles to understand the adult world.', 'tt2042432', '/5HghPn0a53rotVXP6OtVGiTSAIQ.jpg');
