insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5828, 'Sometimes in April', False, True, False, False, 2005, 7.9, 'Debra Winger, Oris Erhuero and Idris Elba star in this drama framed by the Rwandan genocide.', 'tt0400063', '/jDr1iXVPKSdzPMb1QeGwacEL9Vq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5829, 'Life', False, True, False, False, 2015, 6.1, 'The six-member crew of the International Space Station is tasked with studying a sample from Mars that may be the first proof of extra-terrestrial life, which proves more intelligent than ever expected.', 'tt5442430', '/lja0Kh17LLleh6DhtixEh7Z7T6D.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5830, 'Flying Tigers', False, True, False, False, 1942, 6.8, 'Jim Gordon commands a unit of the famed Flying Tigers, the American Volunteer Group which fought the Japanese in China before America''s entry into World War II. Gordon must send his outnumbered band of fighter pilots out against overwhelming odds while juggling the disparate personalities and problems of his fellow flyers.', 'tt0034742', '/1Aee8HBwkq1yjKYMnL9YMPhXJZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5834, 'Felix and Meira', False, True, False, False, 2015, 6.7, 'A young married woman from Montreal''s Orthodox Jewish community finds freedom from the strictures of her faith through her relationship with a young man who is mourning the death of his estranged father.', 'tt3685218', '/yKmrsUGqiFSwhBJTZBsmKCrfV4Y.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5836, 'Five Corners', False, True, False, False, 1987, 6.2, 'A psychotic young man returns to his old neighborhood after release from prison. He seeks out the woman he previously tried to rape and the man who protected her, with twisted ideas of love for her and hate for him.', 'tt0093029', '/sjCavnds8Yfk57fG5GGtBhnNjrt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5837, 'Alexandra''s Project', False, True, False, False, 2003, 6.5, 'Steve is a man who has it all, a successful career, wonderful children, beautiful home and a loving wife. However, returning to his home after work on his birthday, he finds his house deserted and darkened with almost all the lightbulbs missing, all easy access outside cut off and a videotape waiting for him. Playing that tape, he watches a bizarre and grueling recording in which his wife explains her grievance with him, her reasons for disappearing with the children and her revenge for how he treated her in a way he would never forget.', 'tt0338706', '/28S5YzEF4DmygcZhyyMfvKJZWdD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5839, 'The Cat''s Meow', False, True, False, False, 2001, 6.3, 'Semi-true story of the Hollywood murder that occurred at a star-studded gathering aboard William Randolph Hearst''s yacht in 1924.', 'tt0266391', '/zEVZa9EuApL5AQl2mE9NNAeCUix.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5841, 'The Leading Man', False, True, False, False, 1996, 5.7, 'Successful playwright Felix Webb has a new play, ''The Hit Man'', in rehearsal. Directed by his old friend Humphrey, it is already being hailed as a masterpiece; but Felix can''t enjoy his success. He has fallen passionately in love with Hilary, a beautiful, fiesty young actress, and is preparing to desert his perfect family, his wife Elena and three lively children. His intolerable situation is further complicated when Humphrey casts Hilary as one of the leads in ''The Hit Man''. Enter Robin Grange, a charismatic young Hollywood actor making his London theatre debut. Robin is attractive, charming and dangerous, and soon inveigles his way into everyone''s life. He ingratiates himself with the cast and, quickly grasping Felix''s dilemma, sets about weaving his web of mischief. He suggests that if he were to seduce Elena, she would be distracted from Felix''s affair, regain her self respect, and perhaps even willingly part from the unfaithful husband to whom she clings...', 'tt0116845', '/7Aywawe75W7VKip12gWuiBOiDiT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5842, 'Happy', False, True, False, False, 2011, 7.2, 'Bunny, a pizza delivery boy is blamed by Madhumati, a calm and serious girl, of ruining her education and being responsible for an upcoming unwanted marriage. Left with no other option, Bunny chooses to help her by ploying a story to the would-be groom and in the process is forced to share a flat with her. But did Madhumati really forgive Bunny?', 'tt0491775', '/nPITY2XjohAubgnY9Ihq4k5DfjH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5843, 'The Berlin File', False, True, False, False, 2013, 6.6, 'JUNG Jin-soo, a South Korean intelligence agent, comes across an unidentifiable operative, a ''ghost,'' while surveilling a North Korean weapons deal in Berlin. The mystery figure is a North Korean secret agent, PYO Jong-seong, whose information cannot be found on any intelligence database. Jung quickly goes after Pyo to unveil his identity and gets himself embroiled in a vast international conspiracy. In the meanwhile, another North Korean operative, DONG Myung-soo, is dispatched to Berlin with a secret agenda to purge Pyo and take control of the North Korean embassy. Dong sets a trap to frame Pyo''s wife, RYUN Jung-hee, for treason and tightens the noose around Pyo''s neck. Pyo surveils his wife with hopes of clearing accusation against her but he plunges into deeper confusion when he discovers her secret.', 'tt2357377', '/j1FkEBJ6jK43ZONKWNZ33fsLchH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5845, 'Wind', False, True, False, False, 1992, 6.4, 'WIND is an animated short about the daily life of people living in a windy area who seem helplessly exposed
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5847, 'Sun Choke', False, True, False, False, 2015, 5.6, 'Janie’s just trying to get well. As she recovers from a violent psychotic break, she’s subjected each day to a bizarre holistic health and wellness regimen designed, and enforced, by her lifelong nanny and caretaker. But when she develops an obsession with a stranger, Janie''s buried demons begin to surface.', 'tt3395582', '/zS4dvm50qxLwvSPPI7u1luDiL8m.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5850, 'Athadu', False, True, False, False, 2005, 8.2, 'Athadu is a Action Movie Directed by Trivikram Srinivas Rao.Nandu (Mahesh Babu), an orphan, is a top-notch professional killer in town. He maneuvers his operations with the help of his buddy (Sonu Sood). In one such operation, he falls into the eyes of the cops. While fleeing from the crime scene, he unintentionally becomes the cause for the death of a stranger called Pardhoo (Rajiv Kanakala), a run-away youngster who wants to return home after 12 years. Nandu assumes the identity of Pardhoo and enters his household in a remote village to escape from the cops. The rest of the story is all about how Nandu solves all the problems.', 'tt0471571', '/ojZAu2KOemaDEfLnJXZeuU9QQko.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5851, 'Giuseppe Makes a Movie', False, True, False, False, 2014, 7.8, 'While the rest of America slept, DIY filmmaker/musician Giuseppe Andrews has made over 30 experimental features. Set in some demented alternate universe (i.e. Ventura, California), they are populated by real-life alcoholics and drug addicts, trash-talking senior citizens and trailer park residents dressed in cow outfits and costume-shop wigs, acting out booze-fueled vignettes filtered through Giuseppe''s John Waters-meets-Harmony Korine-meets-Werner Herzog sensibility.  Director Adam Rifkin (Look, The Dark Backward) creates a wildly surreal, outrageously funny and strangely touching portrait of a truly Outsider Artist inhabiting a world few of us even know exists, as he follows Giuseppe and his seriously impaired troupe on the production of his latest 2-day opus, Garbanzo Gas, starring Vietnam Ron as a Cow given a weekend reprieve from the slaughterhouse at the local motel.  An Official Selection of Hot Docs 2014, Los Angeles Film Festival (LAFF) and the Rooftop Films Summer Series.', 'tt3661072', '/84wEpo82TdGQV8yO7bv1yNw3wtf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5852, 'Sarah Silverman: We Are Miracles', False, True, False, False, 2013, 6.6, 'In her first-ever HBO solo special, Sarah Silverman takes the stage for an evening of adults-only stand-up comedy. Taped live in front of an intimate audience of 39 fans at Largo, a music and comedy club in Los Angeles, Sarah Silverman: We Are Miracles features Silverman taking aim at such subjects as cell-phone porn, crazy religions, specialty deodorants, terrible roommates, eyebrow waxing, her 19-year-old dog, Obama and Republicans, having babies, Pixar movies, the miracle of existence, and more.', 'tt2883224', '/rCCKMCiDMXkj44c74kowm0dIFcZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5853, 'Hot Coffee', False, True, False, False, 2011, 7.5, 'Most people think they know the "McDonald''s coffee case," but what they don''t know is that corporations have spent millions distorting the case to promote tort reform. HOT COFFEE reveals how big business, aided by the media, brewed a dangerous concoction of manipulation and lies to protect corporate interests. By following four people whose lives were devastated by the attacks on our courts, the film challenges the assumptions Americans hold about "jackpot justice."', 'tt1445203', '/sjRtcYBBizUxlk0m05svj59pTN7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5854, 'An Inspector Calls', False, True, False, False, 2015, 7.7, 'Based on a famous stage play and set in the year 1912, an upper crust English family dinner is interrupted by a police inspector who brings news that a girl known to everyone present has died in suspicious circumstances. It seems that any or all of them could have had a hand in her death. But who is the mysterious Inspector and what can he want of them?', 'tt0047119', '/7os4zTcPbSrXzYcQdj47vcCiDmh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5855, 'Falcon Rising', False, True, False, False, 2014, 5.8, 'Chapman is an ex-marine in Brazil''s slums, battling the yakuza outfit who attacked his sister and left her for dead.', 'tt2295722', '/4wnfTO8mvqcTU62YMkUeKq49VMT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5856, 'Bridgend', False, True, False, False, 2015, 5.5, 'Over a 5-year period in Bridgend in Wales, 79 people, many of them teenagers, committed suicide without leaving any clue as to why. This is the starting point for this mysterious social drama.', 'tt4180576', '/5mYC98zfbhJtC5Mso4rPBqMAQGv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5857, 'From Time to Time', False, True, False, False, 2010, 6.7, 'A haunting ghost story spanning two worlds, two centuries apart. When 13 year old Tolly finds he can mysteriously travel between the two, he begins an adventure that unlocks family secrets laid buried for generations.', 'tt1031241', '/4qVw09ZMpyXWsHt1odiFzdAPCFB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5858, 'A Screaming Man', False, True, False, False, 2010, 6.7, 'Present-day Chad. Adam, sixty something, a former swimming champion, is pool attendant at a smart N''Djamena hotel. When the hotel gets taken over by new Chinese owners, he is forced to give up his job to his son Abdel. Terribly resentful, he feels socially humiliated. The country is in the throes of a civil war. Rebel forces are attacking the government. The authorities demand that the population contribute to the "war effort", giving money or volunteers old enough to fight off the assailants. The District Chief constantly harasses Adam for his contribution. But Adam is penniless; he only has his son.', 'tt1639901', '/q90ysGM6CQmHnUTGGU4x5TBv4hn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5859, 'The Portrait of a Lady', False, True, False, False, 1996, 6.2, 'Ms. Isabel Archer isn''t afraid to challenge societal norms. Impressed by her free spirit, her kindhearted cousin writes her into his fatally ill father''s will. Suddenly rich and independent, Isabelle ventures into the world, along the way befriending a cynical intellectual and romancing an art enthusiast. However, the advantage of her affluence is called into question when she realizes the extent to which her money colors her relationships.', 'tt0117364', '/v2tN3LKXOlXrL2ZvoGPWGmqkLz7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5861, 'The Joyless Street', False, True, False, False, 1925, 7.2, 'In 1921 in the poor part of an Austrian town called Melchiorgasse there are only two wealthy people: the butcher Josef Geiringer and his wife. Mrs. Greifer runs a fashion boutique and a nightclub patronized by wealthy Viennese. Annexed to the nightclub is Merkl Hotel, a brothel to which the women of the nightclub bring their clients. The film follows the lives of two women from the same poor neighborhood as they try to better themselves during the period of Austrian postwar hyperinflation. They are Marie, who becomes a prostitute, and Grete who does not.', 'tt0015842', '/qmOQssz4GfbdtOmxwMP0jBj4P4F.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5862, 'Dangerous Liaisons', False, True, False, False, 2012, 5.9, 'Juliette Merteuil and Valmont is a sophisticated couple, always looking for fun and excitement. Both have sexual affairs with others and share their experiences with one another. But there is one rule: never fall in love. But this time Valmont falls madly in love with a girl he meets at a ski resort, Marianne.', 'tt0053002', '/zc68mhNgXEiXZObbNvQ6MvIk9rf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5863, 'Very Semi-Serious', False, True, False, False, 2015, 7.3, 'The New Yorker is the benchmark for the single-panel cartoon. This light-hearted and sometimes poignant look at the art and humor of the iconic drawings shows why they have inspired and even baffled us for decades. Very Semi-Serious is a window into the minds of cartooning legends and hopefuls, including editor Bob Mankoff, shedding light onto how their humor evolves.', 'tt4677300', '/nuD7wkEhQL2uFIdIGY2qIFX0F3m.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5866, 'Blue Hawaii', False, True, False, False, 1961, 6.1, 'Chad Gates has just gotten out of the Army, and is happy to be back in Hawaii with his surf-board, his beach buddies, and his girlfriend.', 'tt0054692', '/4kYgVtcCdeeZh40jiFlnYQF3RUv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5868, 'Late Phases', False, True, False, False, 2014, 6, 'When deadly attacks from the forests beset a secluded retirement community, it is up to a grizzled veteran to figure what the residents are hiding.', 'tt2420756', '/7ulbyPajXTNZkki9AtcLI9YYhc2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5869, 'Pu-239', False, True, False, False, 2006, 6.9, 'A worker at a Russian nuclear facility gets exposed to a lethal dose of radiation. In order to provide for his family, he steals some plutonium and sets out to sell it on Moscow''s black market with the help of an incompetent criminal.', 'tt0472156', '/fRyusBzphrpoEfMNff0jlR0ulQ6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5870, 'Our Town', False, True, False, False, 1940, 6.6, 'Kyung-ju is a struggling novelist who writes violent and gory murder mysteries. When a fierce argument with his landlady ends in her death, he stages the killing so that it looks like the act of the serial killer that has been terrorizing his village. Anxious that the police might realize the latest killing was a copycat murder, he begins his own invetstigation to track down the killer with help of his detective friend.', 'tt1201168', '/xK9TrtbVQbPlCSEtiGa40XdWBCw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5872, 'Phantom Detective', False, True, False, False, 2016, 6.3, 'HONG Gildong is an infallible private detective with an exceptional memory and quirky personality. While chasing the only target he failed to find, he gets entangled in a much bigger conspiracy than he bargained for.', 'tt5644726', '/510I2vK0GuNF3FeahGHrJUV8aZy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5873, 'Wheels on Meals', False, True, False, False, 1984, 7.2, 'Cousins Thomas and David, owners of a mobile restaurant, team up with their friend Moby, a bumbling private detective, to save the beautiful Sylvia, a pickpocket.', 'tt0087578', '/i5iZVBfOqlRs4g50dm2G6YGfhYD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5874, 'The Last Time I Saw Paris', False, True, False, False, 1954, 6.2, 'Charles returns to Paris to reminisce about the life he led in Paris after it was liberated. He worked on "Stars and Stripes" when he met Marion and Helen. He would marry and be happy staying in Paris after his discharge and working for a news organization. He would try to write his great novel and that would come between Charlie, his wife and his daughter', 'tt0047162', '/coW8Z2w0iyX3FPyPCvBYAWeKf8V.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5878, 'Stop-Loss', False, True, False, False, 2008, 6.4, 'A veteran soldier returns from his completed tour of duty in Iraq, only to find his life turned upside down when he is arbitrarily ordered to return to field duty by the Army.', 'tt0489281', '/hsmZcNzPGnZwRgCKAfIr1JngYu5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5879, 'Different for Girls', False, True, False, False, 1996, 6.9, 'Karl Foyle and Paul Prentice were best mates at school in the Seventies. But when they meet again in present-day London things are definitely not the same. Karl is now Kim, a transsexual, and she has no desire to stir up the past while she''s busy forging a neat and orderly new life. Prentice, on the other hand, has charm but is a social disaster stuck in a dead-end job. His main talent is for getting them both into trouble. Amid the squabbles, they start to fall in love. One night, Kim invites Prentice to a romantic dinner at her flat. Prentice, finding the seduction unexpectedly effective, freaks out. He proceeds to make a public display of both of them and winds up in court. Humiliated and angry, Kim runs away. Only she can save Prentice now, but will true love triumph for a new made woman and an aging punk?', 'tt0116102', '/aWO2iXpqQY3S4WhXRvf0B18QVR8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5880, 'Absolutely Anything', False, True, False, False, 2015, 6, 'Eccentric aliens give a man the power to do anything he wants to determine if Earth is worth saving.', 'tt1727770', '/tnBt9yPCQwnXLwioqfgnL5hqGM6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5881, 'Suture', False, True, False, False, 1993, 6.6, 'Brothers Vincent (rich) and Clay (poor) meet up for the first time after their father''s funeral and remark on how similar they look. But unknown to Clay, who thinks his life is taking a turn for the better, Vince is actually plotting to kill him with a car bomb and pass the corpse off as his own, planning to start a new life elsewhere with his father''s inheritance. But Clay survives the blast and has his face, memory and identity restored in hospital... but are they the right ones?', 'tt0108260', '/8lbOpuNaJBNT1gXbIDDyYnDIYut.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5883, 'Chopping Mall', False, True, False, False, 1986, 5.6, 'A group of teenagers that work at the mall all get together for a late night party in one of the stores. When the mall goes on lock down before they can get out, the robot security system activates after a malfunction and goes on a killing spree. One by one the three bots try to rid the mall of the "intruders". The only weapons the kids can use are the supplies in other stores, or if they can make it till morning when the mall opens back up.', 'tt0090837', '/mpIxDVVcHdgRawXOchVEdAE2h1P.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5885, 'The Legend of Bhagat Singh', False, True, False, False, 2002, 8.1, 'The Legend of Bhagat Singh is a 2002 Indian historical biographical film about Bhagat Singh, a freedom fighter who fought for Indian independence.It shows in detail how Singh came to develop his views on the British Raj and his struggle for Indian independence.', 'tt0319736', '/1OYCbYXllUvmkl6PEfefrQHPEWV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5886, 'Felony', False, True, False, False, 2014, 6.1, 'Three detectives become embroiled in a tense struggle after a tragic accident that leaves a child in critical condition. One is guilty of a crime, one will try to cover it up, and the other attempts to expose it. How far will these men go to disguise and unravel the truth?', 'tt2402603', '/b36OAp2q5m0oZ4oUcwg2B0cTHrV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5888, 'The Price We Pay', False, True, False, False, 2014, 7.3, 'A documentary on the history and present-day reality of big-business tax avoidance, which has seen multinationals depriving governments of trillions of dollars in tax revenues by harboring profits in offshore havens.', 'tt3921454', '/6cyB6lgHky6HRPe72I6GwzEDydQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5890, 'Red Lights', False, True, False, False, 2012, 6.2, 'Two investigators of paranormal hoaxes, the veteran Dr. Margaret Matheson and her young assistant, Tom Buckley, study the most varied metaphysical phenomena with the aim of proving their fraudulent origins. Simon Silver, a legendary blind psychic, reappears after an enigmatic absence of 30 years to become the greatest international challenge to both orthodox science and professional sceptics. Tom starts to develop an intense obsession with Silver, whose magnetism becomes stronger with each new manifestation of inexplicable events. As Tom gets closer to Silver, tension mounts, and his worldview is threatened to its core.', 'tt1748179', '/7h7jnVwtvefnyvUDLVj05XuS45l.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5892, 'Test', False, True, False, False, 2013, 6.5, 'They live in the steppe. A father and a daughter. Nothing may disturb the eternal order. Tomorrow always comes. And every morning the father goes away to work. And she stays alone. To wait for her father. And to feel. Two men are in love with her. She loves everyone. Her love cannot be divided. One day, the decision comes by itself. It comes from where the sun lives.', 'tt4131188', '/5plVKIpoWmzQfLZiWNvNFCtoDwI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5893, 'Jungle Book', False, True, False, False, 1942, 6.7, 'Rudyard Kipling''s Jungle Book is given the full treatment in this lavish retelling filled with huge sets, exotic animals, a large cast and the incomparable Sabu, starring as Mowgli, the young orphan boy raised by wolves. Curious to reconnect with his human village, Mowgli returns only to find disappointment in the greed and treachery of man. Over time, Mowgli and the village members do grow to trust one another, but not before the village finds itself under siege. It''s up to Mowgli and his jungle friends to save the day.', 'tt0034928', '/wHkgkGSfxbwz7xCkoAvaMviKLmD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5894, 'Twilight', False, True, False, False, 1998, 6.2, 'When Bella Swan moves to a small town in the Pacific Northwest to live with her father, she starts school and meets the reclusive Edward Cullen, a mysterious classmate who reveals himself to be a 108-year-old vampire. Despite Edward''s repeated cautions, Bella can''t help but fall in love with him, a fatal move that endangers her own life when a coven of bloodsuckers try to challenge the Cullen clan.', 'tt1099212', '/3Gkb6jm6962ADUPaCBqzz9CTbn9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5895, 'Bravetown', False, True, False, False, 2015, 6.2, 'After an accidental drug overdose, a talented teenage DJ goes to live with his estranged father in a small Army town, where he gets to the bottom of his own pain and learns empathy for others.', 'tt2950052', '/1XQvhq0bM9Odwr1Nf1fY6QsWOMM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5896, 'The Creeping Flesh', False, True, False, False, 1973, 6.3, 'A scientist comes to believe that evil is a disease of the blood and that the flesh of a skeleton he has brought back from New Guinea contains it in a pure form. Convinced that his wife, a Folies Bergere dancer who went insane, manifested this evil he is terrified that it will be passed on to their daughter. He tries to use the skeleton''s blood to immunise her against this eventuality, but his attempt has anything but the desired result.', 'tt0068424', '/gKLPEUipT2EIaYFwtjw2dyGjZxR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5898, 'The Alibi', False, True, False, False, 2006, 6.4, 'Con man Ray Elliot decides to leave crime behind to start a company that sells fake alibis to clients who have been unfaithful to their significant others. It seems that the streetwise Ray has found his calling, until he unexpectedly becomes a murder suspect in a case involving one of his most influential customers. Now, as the police and an assassin called "The Mormon" track Ray, he and his attractive assistant, Lola, must clear their own names.', 'tt0409681', '/7PaZLT7vfCIU6p3nG0QkIvdrTpF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5900, 'Bruce Lee: A Warrior''s Journey', False, True, False, False, 2000, 8.1, 'Legendary martial artist Bruce Lee is the subject of this thoughtful documentary by Lee aficionado John Little. Using interviews, behind-the-scenes footage and action sequences from Lee''s last (unfinished) film, Game of Death, Little paints a textured, complex portrait of the world''s most famous action hero', 'tt0297814', '/yvm10wHnOQaGl9a4X9KnDHaoJ8W.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5901, 'The Journey', False, True, False, False, 2017, 6.6, 'Firebrand Democratic Unionist Party leader Ian Paisley and Sinn Fein politician Martin McGuinness, two implacable enemies in Northern Ireland, are forced to take a short journey together in which they will take the biggest leap of faith and change the course of history.', 'tt4826674', '/avmWN1YGYbzW4n1cbffw4uLCZSr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5903, 'Bad Milo', False, True, False, False, 2013, 5.6, 'A horror comedy centered on a guy who learns that his unusual stomach problems are being caused by a demon living in his intestines.', 'tt2274570', '/1dmHunFILNXKzjOkl1A7IIsTI58.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5904, 'Feast of Love', False, True, False, False, 2007, 6.6, 'A meditation on love and its various incarnations, set within a community of friends in Oregon. It is described as an exploration of the magical, mysterious and sometimes painful incarnations of love.', 'tt0800027', '/6Uyl94M1Yi8U0o6GddMRqAfa466.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5905, 'Under the Gun', False, True, False, False, 2016, 5.5, 'A look at the aftermath of the Sandy Hook massacre where 20 children were murdered at school by a crazed gunman, but lead to no changes in American gun laws.', 'tt5275886', '/vtkkAtdIGWP1g7daSrNyLd3QkRk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5906, 'George Carlin: Complaints & Grievances', False, True, False, False, 2001, 8.5, 'George Carlin performs a hilarious set of never-before released material in "Complaints and Grievances." His 12th HBO special was recorded live at the Beacon Theater in New York City on November 17, 2001. In "Complaints and Grievances," Carlin shamelessly exposes the people and subjects that irritate him the most.', 'tt0287460', '/4IJoKrip2A3vdNVVCWO3WAfcbhK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5907, 'Parked', False, True, False, False, 2010, 6.7, 'Fred Daly returns to Ireland with nowhere to live but his car. Then dope-smoking 21-year-old Cathal parks beside him, and brightens up his lonely world. Encouraged by Cathal, Fred meets attractive music teacher Jules. Growing closer, these three outsiders are set on a course that will change their lives forever.', 'tt1571409', '/uYT3ERLIRpvkrpum2MnKpPMGWX6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5908, 'The Chinese Mayor', False, True, False, False, 2015, 7.7, 'Once the thriving capital of Imperial China, the city of Datong now lies in near ruins. Not only is it the most polluted city in the country, it is also crippled by decrepit infrastructure and even shakier economic prospects. But Mayor Geng Tanbo plans to change all that, announcing a bold, new plan to return Datong to its former glory, the cultural haven it was some 1,600 years ago. Such declarations, however, come at a devastatingly high cost. Thousands of homes are to be bulldozed, and a half-million of its residents (30 percent of Datong’s total population) will be relocated under his watch. Whether he succeeds depends entirely on his ability to calm swarms of furious workers and an increasingly perturbed ruling elite. The Chinese Mayor captures, with remarkable access, a man and, by extension, a country leaping frantically into an increasingly unstable future.', 'tt4056808', '/mu5u4M9h18MeJpe4lpYaUnx50BX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5909, 'Blackbird', False, True, False, False, 2012, 7.1, 'Seventeen-year-old Randy tries very hard to be a good person. Since his father left, Randy takes care of his emotionally disturbed mother, and he''s the kind of friend all of his classmates can depend on. As strong as he seems on the outside, Randy is hiding a secret inner struggle and denial of his true self. It''s not until he opens himself up to love that he discovers that becoming a man means accepting who you really are.', 'tt3181920', '/30ZQdfpt8097DwD3USxJDymHW8Q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5911, 'Bhoothnath', False, True, False, False, 2008, 6.3, 'Banku, his mother, Anjali Sharma and father move in to their new house -- the Nath villa, unaware of the fact that the house is inhabited by a ghost. It is learnt the ghost is not too happy with his new housemates. But what the ghost is not prepared for is his unlikely friendship with Banku. Now Banku must uncover the reason why his ghostly friend is stuck and help him to attain salvation.', 'tt0991346', '/2Tvwmsu4ihzDiltXxPWI4RwTF3W.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5912, 'No Tears for the Dead', False, True, False, False, 2014, 6.8, 'Abandoned by his mother shortly after immigrating to America, Gon is raised by the mafia and grows up to become a cold-blooded hitman. Though usually flawless in taking out his targets, Gon makes a terrible mistake of killing an innocent young girl. Swamped by feelings of regret, guilt and shame, Gon no longer wants to be a hitman, but his boss gives him one last mission. So Gon finds himself in Korea, the land of the mother that abandoned him, on the trail of his last target. But in bitter irony, that target is Mo-gyeong, the mother of the dead child.', 'tt3697566', '/t3vtsQtu76xMKLal0g1UYkmZD2e.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5913, 'Humanoids from the Deep', False, True, False, False, 1980, 5.7, 'They''re not human. But they hunt human women. Not for killing. For mating.', 'tt0080904', '/ntb2ZCDkuE65P6QFw4V2Ll2D0Mk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5914, 'Lazy Eye', False, True, False, False, 2016, 6.2, 'Passions re-ignite and secrets revealed when a graphic designer reconnects with the great, lost love of his life for a weekend tryst at a house in the desert near Joshua Tree.', 'tt4027504', '/paCOCR4iODQ7cBtCytRurSuEBgN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5915, 'The Color Wheel', False, True, False, False, 2011, 6.6, 'JR has broken up with her professor. She enlists her nervous and obnoxious younger brother Colin to take a short road trip in order to help move out her belongings. They bicker and fight, with one another and pretty much anybody they encounter, before being brought to a place of togetherness and understanding as a result of being pushed away by everybody in their lives except one another.', 'tt1734548', '/vAIq1n0LjePYFLwJlHrGih58bVZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5916, 'The Wait', False, True, False, False, 2015, 6.6, 'In the vast rooms of a beautiful Sicilian villa, Anna receives an unexpected guest. Twenty-something Jeanne has arrived from France, declaring herself to be the girlfriend of Anna’s son, Giuseppe, who has invited her to the house to spend Easter together. But this is news to Anna, and Guiseppe is not yet there...', 'tt3715122', '/o8TFnsmk1eVvhPdXuM1dK1lgoN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5917, 'Circo', False, True, False, False, 2011, 7.1, 'Tino Ponce operates Circo Mexico, which journeys across the Mexican countryside in search of paying customers. Wanting to please his father and continue the family business, Ponce has recruited his young children as performers while laboring night and day to maintain the circus''s faltering financial fortunes. But a growing resentment brewing within his wife about their hardscrabble existence suggests troubles on the horizon. While documenting the brutal regimen of circus life, Circo also peels back the curtain on the Ponce family''s inner dynamics, revealing generational divides and money worries that threaten to tear apart a marriage. Buttressed by indie-rock band Calexico''s evocative score, Schock''s film observes this family drama with a sympathetic but clear-eyed view of a vanishing way of life. And because Circo refuses to be sentimental in its handling of the material, the story''s twists become all the more poignant', 'tt1541666', '/vbcGmwoo7LnWBL8PnqbfQpjYHGr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5918, 'Bang Gang (A Modern Love Story)', False, True, False, False, 2015, 5.5, 'Biarritz. 16 year-old George, the high-school hottie, falls in love with Alex. To get his attention, she initiates a group game with Alex, Nikita, Laetitia and Gabriel. They will discover, test, and push the limits of their sexuality.', 'tt3838728', '/1F2SDyc8eZVqbZZo6kORJsfGcyT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5919, 'Roja', False, True, False, False, 1992, 8.2, 'Roja lives in a Tamil village, and her sister is about to marry a man from the city, who decides to marry Roja instead and gets a job assignment in Kashmir, where some militants decide to kidnap them.', 'tt0105271', '/6kHsXNX6pJ0VQSSHWBTNCIcon4R.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5920, 'Ae Dil Hai Mushkil', False, True, False, False, 2016, 5.8, 'Ayan falls in love with his soulmate, Alizeh, but she doesn’t reciprocate the feeling. Later, a relationship with Saba helps him realize Alizeh’s value in his life, irrespective of their relationship status.', 'tt4559006', '/nfCyGdc57hscruDseVu4fTgEuxZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5921, 'Made in Britain', False, True, False, False, 1982, 7.3, 'Trevor sometimes-violent skinhead with no regard for authority, and would rather spend his time stealing cars than sitting in the detention centre to which he is sent. His social worker, Harry Parker, tries to do his best, but Trevor is only interested when there’s something that he can get out of it. The authorities within the centre try to make Trevor conform to the norms of society, but he takes no notice, and would rather speak in a torrent of four-letter words and racial abuse.', 'tt0084287', '/4knR2kM9WYSwb0UVArlwA8MbFf5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5922, 'George Carlin: Life Is Worth Losing', False, True, False, False, 2005, 8.3, 'Carlin returns to the stage in his 13th live comedy stand-up special, performed at the Beacon Theatre in New York City for HBO®. His spot-on observations on the deterioration of human behavior include Americans’ obsession with their two favorite addictions - shopping and eating; his creative idea for The All-Suicide Channel, a new reality TV network; and the glorious rebirth of the planet to its original pristine condition - once the fires and floods destroy life as we know it.', 'tt0484855', '/xqkSO5Kd1XPVaZsq9ao1X1E24aC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5923, 'Zero Charisma', False, True, False, False, 2013, 6.2, 'An obsessive fantasy nerd gradually becomes unhinged when a charismatic hipster joins his role-playing game', 'tt2294965', '/iaSTaVaCelkmMMAj846D925QUpo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5924, 'Coldwater', False, True, False, False, 2013, 6.4, 'A teenage boy is sent to a juvenile reform facility in the wilderness. As we learn about the tragic events that sent him there, his struggle becomes one for survival with the inmates, counselors, and the retired war colonel in charge.', 'tt2198956', '/9M1zkV3YXToZx4EK6KeEgAvaKCP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5925, 'Letters to God', False, True, False, False, 2010, 6.3, 'A young boy fighting cancer writes letters to God, touching lives in his neighborhood and inspiring hope among everyone he comes in contact. An unsuspecting substitute postman, with a troubled life of his own, becomes entangled in the boy''s journey and his family by reading the letters. They inspire him to seek a better life for himself and his own son he''s lost through his alcohol addiction.', 'tt1462054', '/xdCwy0XmFxCf8oxVuQssI7X1fXg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5926, 'Keep the Aspidistra Flying', False, True, False, False, 1997, 6.3, 'Gordon Comstock is a copywriter at an ad agency, and his girlfriend Rosemary is a designer. Gordon believes he is a genius, a marvelous poet and quits the ad agency, trying to live on his poems, but poverty soon comes to him.', 'tt0119453', '/kOqZpuN5Q3F1GU6SFJjWnYd2uBo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5927, 'Rosenstrasse', False, True, False, False, 2003, 6.7, 'When Ruth''s husband dies in New York, in 2000, she imposes strict Jewish mourning, which puzzles her children. A stranger comes to the house - Ruth''s cousin - with a picture of Ruth, age 8, in Berlin, with a woman the cousin says helped Ruth escape. Hannah, Ruth''s daughter engaged to a gentile, goes to Berlin to find the woman, Lena Fisher, now 90. Posing as a journalist investigating intermarriage, Hannah interviews Lena who tells the story of a week in 1943 when the Jewish husbands of Aryan women were detained in a building on Rosenstrasse. The women gather daily for word of their husbands. The film goes back and forth to tell Ruth and Lena''s story. How will it affect Hannah?', 'tt0298131', '/baKfi8PyYUTm8q3QBZks697lANw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5928, 'A Turtle''s Tale: Sammy''s Adventures', False, True, False, False, 2010, 6.1, 'A sea turtle who was hatched in 1959 spends the next 50 years traveling the world while it is being changed by global warming. Born on a Baja, California beach in 1959, new hatchling Sammy must do what his fellow newborn sea turtles are doing: race across the beach to the ocean before they are captured by a seagull or crab. Thus begins Sammy''s incredible fifty-year ocean journey. Along the way he meets his best friend, a fellow turtle named Ray, and overcomes obstacles both natural and man-made while trying to fulfill his dream of travelling around the world. Throughout his voyage, Sammy never forgets about Shelly - the turtle he saved on his first day and loves passionately from afar. Based on the actual trajectory of a sea turtle''s life, the film illustrates the dangers humans pose to the species'' survival. The film combines entertainment with an important environmental message.', 'tt1230204', '/3rl95Ml7Igdkc5QKe2GEUWa1SFU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5930, 'Vision – From the Life of Hildegard von Bingen', False, True, False, False, 2009, 6.5, 'Hildegard von Bingen was truly a woman ahead of her time. A visionary in every sense of the word, this famed 12th-century Benedictine nun was a Christian mystic, composer, philosopher, playwright, poet, naturalist, scientist, physician, herbalist and ecological activist.', 'tt0995850', '/47GG5EA5xP0uKHsliWQAOg86Zfy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5931, 'Song to Song', False, True, False, False, 2017, 5.7, 'In this modern love story set against the Austin, Texas music scene, two entangled couples — struggling songwriters Faye and BV, and music mogul Cook and the waitress whom he ensnares — chase success through a rock ‘n’ roll landscape of seduction and betrayal.', 'tt2062700', '/itmaNi14GyWguTOywZ0mMzyScW9.jpg');
