insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5963, 'Runoff', False, True, False, False, 2015, 5.8, 'The beauty of the land cannot mask the brutality of a farm town. As harvest draws near, Betty confronts a terrifying new reality and will go to desperate lengths to save her family when they are threatened with being forced from their land.', 'tt2094034', '/qdikV9LJzerR4cmfZQwYrvMhHds.jpg');
