insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5966, 'Félicité', False, True, False, False, 2017, 6.5, 'Félicité, a strong and proud woman, sings in bars in Kinshasa. She drifts away from reality when her 14-year-old son gets into an accident. In electric Kinshasa, she wanders in a world of music & dreams... until love unexpectedly brings her back to life.', 'tt5980798', '/5rPREuak5JFfri7EOQqxtwa6ipx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5968, 'Late Night Shopping', False, True, False, False, 2001, 6.9, 'Four friends Sean, Vincent, Lenny and Jody find themselves at something of a deadend. Trapped in a twilight world of permanent night shift work, they hang out together in the local cafe, drinking coffee and entertaining themselves by observing Vincent''s unwavering success in pulling women. There seems to be little prospect of change...until Vincent accidently sleeps with Sean''s girlfriend.', 'tt0250491', '/mrcAVQ4Muexakqly5xYjS3zgT8t.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5969, 'The Quiet', False, True, False, False, 2005, 6.2, 'After her widowed father dies, deaf teenager Dot moves in with her godparents, Olivia and Paul Deer. The Deers'' daughter, Nina, is openly hostile to Dot, but that does not prevent her from telling her secrets to her silent stepsister, including the fact that she wants to kill her lecherous father.', 'tt0414951', '/lXIuHcRGCRk0YBOi41MCHMvGst5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5970, 'I Think We''re Alone Now', False, True, False, False, 2008, 6.8, 'Every celebrity deals with his or her share of obsessed fans. "I Think We''re Alone Now" is a documentary that focuses on two individuals, Jeff and Kelly, who claim to be in love with the 80''s pop singer Tiffany. Jeff Turner, a 50-year-old man from Santa Cruz, California has attended Tiffany concerts since 1988. Diagnosed with Asperger''s syndrome, he never had a girlfriend. Jeff spends his days hanging out on the streets of Santa Cruz, striking up conversations with anyone who has a moment to spare. Kelly McCormick is a 38-year-old hermaphrodite from Denver, Colorado, who claims to have been friends with Tiffany as a teenager. She credits Tiffany as the shining star who has motivated her to do everything in her life. Both Jeff and Kelly have been labeled stalkers by the media and other Tiffany fans. This film takes you inside the lonely lives these two characters, revealing the source of their clinging obsessions...', 'tt1152828', '/l3LuvJ50GkvZOXbKbjUFTapUYjL.jpg');
