insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5972, 'Little Italy', False, True, False, False, 2018, 5.7, 'Undercover cop Nico Giraldi travels to New York and Las Vegas to find a crooked cop who gave his squadron back in Italy a bad name.', 'tt0078312', '/IeZEHfmh6bGwVIOn9il4OWdHa5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5973, 'Prescription Thugs', False, True, False, False, 2016, 7, 'Americans consume 75% of the world’s prescription drugs. After losing his own brother to the growing epidemic of prescription drug abuse, documentarian Chris Bell sets out to demystify this insidious addiction. Bell’s examination into the motives of big pharma and doctors in this ever-growing market leads him to meet with experts on the nature of addiction, survivors with first-hand accounts of their struggle, and whistleblowers who testify to the dollar-driven aims of pharmaceutical corporations. Ultimately his investigation will point back to where it all began: his own front door.', 'tt4505666', '/7V6sDOLCNXcqHsqToGDACbK3HGE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5974, 'The Two Faces of Dr. Jekyll', False, True, False, False, 1960, 6.3, 'After a series of scientific experiments directed towards freeing the inner man and controlling human personalities, the kindly, generous Dr Henry Jekyll succeeds in freeing his own alter ego, Edward Hyde, a sadistic, evil creature whose pleasure is murder.', 'tt0054416', '/gW6ZF51GF60cWs0iWu0ZSSXEaUb.jpg');
