insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5985, 'Ghosts of Cité Soleil', False, True, False, False, 2006, 7.1, 'In the slum of Cité Soleil, President Aristide''s most loyal supporters were ruling as kings. The five major gang leaders were controlling heavily armed young men; the Chiméres. The Secret army of President Jean-Bertrand Aristide. "Ghosts of Cité Soleil" is a film about Billy and Haitian 2pac. Two brothers. Gang Leaders of the Chiméres.', 'tt0479046', '/xZWj1OY8Dz6hIvZ29bnIzDNjea9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5986, 'Being Ginger', False, True, False, False, 2013, 6, 'The story of one redhead''s attempt to regain his self-confidence.', 'tt3108840', '/lLlPM57UqKygjEtWBVtct88Ke6I.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5987, 'My Date with Drew', False, True, False, False, 2005, 6.4, 'Ever since the second grade when he first saw her in E.T. The Extraterrestrial, Brian Herzlinger has had a crush on Drew Barrymore. Now, 20 years later he''s decided to try to fulfill his lifelong dream by asking her for a date. There''s one small problem: She''s Drew Barrymore and he''s, well, Brian Herzlinger, a broke 27-year-old aspiring filmmaker from New Jersey.', 'tt0378407', '/19ChghJMN72BG9mQeDK0D209whF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5988, '23 Blast', False, True, False, False, 2014, 6.5, 'When a high school football star is suddenly stricken with irreversible total blindness, he must decide whether to live a safe handicapped life or bravely return to the life he once knew and the sport he still loves.', 'tt2304459', '/nauBE0RFJlb0ytAOXsBMHbeWZqp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5989, 'Fortress of War', False, True, False, False, 2010, 7.5, 'A war drama set during the Nazi invasion of the Soviet Union in June 1941, in which Russian troops held on to a border stronghold for nine days. The film shows the heroic defense of Brest Fortress, which has taken upon the first stroke of German fascist invaders on June 22 1941.', 'tt1343703', '/v99jG4CkBaLGrE4cCMLtc7e5zPZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5990, 'Liza, the Fox-Fairy', False, True, False, False, 2015, 7.6, 'Liza''s a nurse, seeking love. Her only company is a long-dead Japanese pop star, who turns her into a fox-fairy out of jealousy. Now, every men who desires Liza shall die horribly. Can she overcome the curse?', 'tt2210769', '/rXHXCkkcxB8E35KIenxVTEmG0MZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5991, '200 Pounds Beauty', False, True, False, False, 2006, 6.8, 'Based on a Japanese manga, "Kanna-San, Daiseikou Desu," this story revolves around Kang Han-na (Kim Ah-jung), an overweight phone sex employee and secret vocalist for Ammy, a famous Korean pop singer who actually lip syncs as she cannot sing. After getting humilitated publicly by an ungrateful Ammy, Han-na undergoes an extreme makeover to become a pop sensation herself.', 'tt0940642', '/AnyJmyOQQGiqR9DLIiQ9aejIbkx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5993, 'Escape from Sobibor', False, True, False, False, 1987, 7.4, 'During WWII, the death camp at Treblinka had an escape, causing the Commandant at a similar camp in Sobibor to vow (actually threaten) that his camp would never experience the same thing. But those who were its captives, the Jewish laborers that had been spared from the ovens, knew that they were on borrowed time and that their only hope was to escape... the only question was how to do it.  On October 14, 1943, members of the camp''s underground resistance succeeded in covertly killing eleven German officers and a number of Ukrainian guards. Of the 600 inmates in the camp, roughly 300 escaped, although most were later re-captured and killed. The escape forced the Nazis to close the death camp, dismantling it and planting a forest.', 'tt0092978', '/zxXeuxLgNdOWiT3zd8DrK98E6oj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5995, 'Heartburn', False, True, False, False, 1986, 6.1, 'Rachel is a food writer at a New York magazine who meets Washington columnist Mark at a wedding and ends up falling in love with him despite her reservations about marriage. They buy a house, have a daughter, and Rachel thinks they are living happily ever after until she discovers that Mark is having an affair while she is waddling around with a second pregnancy.', 'tt0091188', '/pF1uPAi9TWRSRLf9SHfIDQle2NK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5996, 'Jess + Moss', False, True, False, False, 2011, 6.4, 'Jess, age 18, and Moss, age 12 are second cousins in the dark-fire tobacco fields of rural Western Kentucky. Without immediate families that they can relate to, and lacking friends their own age, they only have each other. Over the course of a summer they venture on a journey exploring deep secrets and hopes of a future while being confronted with fears of isolation, abandonment and an unknown tomorrow.', 'tt1787731', '/yxek3Kh1Y7GCtEOUqze3m8UpnjQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (5998, 'Baghban', False, True, False, False, 2003, 7.4, 'Raj Malhotra and wife Pooja have four sons. The sons have settled down professionally and are quite independent. However, when Raj Malhotra retires, none of his children want to be burdened with the responsibility of taking care of their parents. Strangely, it is the adopted son who proves to be the most kind hearted of them all. Salman''s girlfriend eventually marries him. The question is, will Raj and Pooja''s sons learn the folly of their ways and turn over a new leaf?', 'tt0337578', '/p6NPup58fPjIEAU9tKU4XpQhtTA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6000, 'Boom Bust Boom', False, True, False, False, 2016, 7.1, 'Terry Jones presents Boom Bust Boom. The result of a meeting between writer, director, historian and Python Terry Jones and economics professor and entrepreneur Theo Kocken. Co-written by Jones and Kocken and featuring John Cusack, Nobel Prize winners Daniel Kahneman, Robert J Shiller and Paul Krugman, the film is part of a global movement to change the economic system through education to protect the world from boom and bust. A unique look at why economic crashes happen, Boom Bust Boom is a multimedia documentary combining live action with animation and puppetry to explain economics to everyone.', 'tt3332308', '/8n2I0jx8x83M3wsiXKm5S2dKey6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6001, 'It Felt Like Love', False, True, False, False, 2013, 5.6, 'Fourteen-year-old Lila is experiencing an ennui-filled Brooklyn summer. She awkwardly wears a Kabuki-esque mask of sunscreen at the beach and plays third wheel to Chiara, her more experienced friend, and Chiara’s boyfriend, Patrick. Determined to have a love interest of her own, a bravado-filled Lila pursues Sammy, a tough but handsome older boy....', 'tt2328922', '/9KY7iWkYlucv1EtbhAh3sawLmUg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6002, 'Big Ass Spider!', False, True, False, False, 2013, 5.3, 'When a giant alien spider escapes from a military lab and rampages across the city of Los Angeles, it is up to one clever exterminator and his security guard sidekick to kill the creature before the entire city is destroyed.', 'tt1830713', '/xVO2QHVTrF3CTS2KZWeqXpirJGk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6003, 'Book of Blood', False, True, False, False, 2009, 5.3, 'Based on the wraparound story penned by Clive Barker in the author''s "Books of Blood" collection, the story centers on a paranormal expert who, while investigating a gruesome slaying, finds a house that is at the intersection of "highways" transporting souls to the afterlife.', 'tt1169809', '/d7MtKvrJxyStm4ZPkig4Dx3atPh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6004, 'The Flaw', False, True, False, False, 2011, 7.3, 'The story of the credit bubble that  caused the financial crash. Through interviews with some of the world''s leading economists, including housing expert Robert Shiller, Nobel laureate Joseph Stiglitz, and economic historian Louis Hyman, as well as Wall Street insiders and victims of the crash including Ed Andrews - a former economics correspondent for The New York Times who found himself facing foreclosure - and Andrew Luan, once a bond trader at Deutsche Bank now running his own Wall Street tour guide business, the film presents an original and compelling account of the toxic combination of forces that nearly destroyed the world economy.', 'tt1787810', '/3LWHPi5b2MwJCxN1otfrPBoPhsN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6006, 'Style Wars', False, True, False, False, 1983, 8.1, 'Some call it tagging, some call it writing, still others call it bombing--it''s all graffiti. Whether it''s art or not is another matter, but it''s undeniably illegal. Tony Silver and Henry Chalfant''s historic PBS documentary Style Wars tracks the rise and fall of subway graffiti in New York in the late 1970s and early 1980s.', 'tt0177262', '/dGrfaVAcRklpBTmuko6Ong7ERfd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6007, 'Drew: The Man Behind the Poster', False, True, False, False, 2013, 7.5, 'A documentary on legendary movie-poster artist Drew Struzan.', 'tt1486843', '/dTJ7VoA689IXSuqiXJl75YqjOdv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6008, 'The Acid House', False, True, False, False, 1998, 6.4, 'THE ACID HOUSE is a surreal triptych adapted by "Trainspotting" author Irvine Welsh from his acclaimed collection of short stories.
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6010, 'Manborg', False, True, False, False, 2011, 5.5, 'The evil Count Draculon and his army of demons have conquered Earth, but a young soldier who lost his life in the first war against Hell has reawakened in the future as MANBORG! Together with a crew of post-apocalyptic rebels, the half-man half-machine hero will fight to defeat Draculon and take back the planet!', 'tt2060525', '/moHe8xUN6dkm3fZXQDAkUDs2v6O.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6011, 'Sunflower', False, True, False, False, 2006, 7.2, 'At the end of World War II, Giovanna, a war bride living near Milan refuses to accept that her husband, Antonio, missing on the Russian front, is dead. There''s a flashback to their brief courtship near her hometown of Naples, his 12-day leave to marry her, ruses to keep from deployment, and the ultimate farewell. Some years after the war, still with no word from Antonio, Giovanna goes to Russia to find him, starting in the town near the winter battle when he disappeared. Armed with his photograph, what will she find?', 'tt0065782', '/nZgSaOEwdqzGXZozatsn7WNpkxh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6012, 'United', False, True, False, False, 2011, 7.4, 'United is based on the true story of Manchester United''s legendary "Busby Babes", the youngest side ever to win the Football League and the 1958 Munich Air Crash that claimed eight of the their number. The film draws on first-hand interviews with the survivors and their families to tell the inspirational story of a team and community overcoming terrible tragedy.', 'tt1777034', '/kWWa20kIjk4B6OtRHqWj41HHmiN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6013, 'Darling', False, True, False, False, 2015, 5.5, 'Prabhas (Prabhas) has a childhood sweetheart named Nandini (Kajal Agarwal) and they are separated during their childhood. Nandini migrates to Swiss along with her father. Meanwhile Prabhas rejects love proposals from other girls as his heart belongs to Nandini. Prabhas gets a chance to meet Nandini for the first time after the separation during the reunion of their respective parents who have studied together. The rest of the story is all about how Prabhas wins the love of Nandini.', 'tt1649303', '/pyUPrARdT1y23bjVHSTsrzjfpDm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6014, 'Little Secrets', False, True, False, False, 2001, 6.4, 'Emily Lindstrom, 14, is an aspiring concert violinist; she''s spending the summer practicing for a big audition while her girlfriends are at camp. She''s also got a thriving neighborhood business: for 50 cents, she''ll keep your secret. Her mother is very pregnant, and her parents seem more concerned about the new baby than anything Emily cares about. A new family moves in next door; their son, Philip, 12, becomes Emily''s friend. Eventually, the weight of Emily''s secrets - her own, the ones she''s keeping professionally, and a secret Philip tells her, send her life temporarily crashing down.', 'tt0296845', '/4wTe1jDEUSX4VQaIuBPca2GSAig.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6016, 'Lakeboat', False, True, False, False, 2000, 6.1, 'Coming-of-age tale set aboard a shipping freighter traveling America''s Great Lakes. Dale is an Ivy League college student who briefly joins a world-weary crew. Exposed to a seafaring lifestyle which falls short of his literary visions, Dale instead finds the experience rich in unexpected ways. The men''s bravado and comical posturing gives way as their lively story-telling reveals more about their mythologized view of life than about what actually may have happened.', 'tt0210149', '/1OksNeNaSNUrPGuwNuunvnH0v4f.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6017, 'The House at the End of Time', False, True, False, False, 2013, 6.8, 'The House at the End of Time is the first Venezuelan thriller suspense and terror. It tells the story of Dulce (Ruddy Rodriguez) a mother who has encounters with apparitions inside her old house, where she must decipher a mystery that could trigger a prophecy: the death of her family.', 'tt2640474', '/92FQvweLxWVDstpEY2LPLi0kp9A.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6018, 'Genova', False, True, False, False, 2008, 6, 'A man moves his two daughters to Italy after their mother dies in a car accident, in order to revitalize their lives. Genoa changes all three of them as the youngest daughter starts to see the ghost of her mother, while the older one discovers her sexuality.', 'tt0791303', '/sxYarCXOudBzSyFbBBNt723R7q3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6020, 'Little England', False, True, False, False, 2013, 7.8, 'Τwo sisters from the island of Andros, dubbed Little England because of its affluence, are both in love with the same man. Set in the Greek Civil War period and ending in the 1950s, the movie is based on the homonymous novel by Ioanna Karystiani.', 'tt3253650', '/pvG3i4im7VBKOANIrLyDSEJtePi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6021, 'How to Talk to Girls at Parties', False, True, False, False, 2017, 5.7, 'A couple of British 1970s teen-aged boys, Enn and Vic, go to a party to meet girls, only to find that the girls are very different from the boys'' expectations.', 'tt3859310', '/v6mPfyGshwXd1R6kQlMEyZ8Zu2s.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6022, 'Necessary Roughness', False, True, False, False, 1991, 6.2, 'When the Texas Southern Armadillos football team is disqualified for cheating and poor grades, the University is forced to pick from a team that actually goes to school. Will they even win a single game?', 'tt0102517', '/fuq4ft5uIKdCswcg43XaNXTyHNy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6023, 'Trouble Every Day', False, True, False, False, 2001, 6, 'Scientist Shane Brown neglects his new bride, instead spending their honeymoon searching for an old colleague who disappeared after a research paper he had written was discredited by the medical community. It turns out that Dr. Semeneau is living in obscurity in order to protect his wife, whom he keeps prisoner in a room with boards nailed across the doorway. The narrative unfolds the dark secret that drives each party.', 'tt0204700', '/uap6eKgnII5lLPFEESAvqtpPciI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6025, 'Intimate Enemies', False, True, False, False, 2007, 6.9, 'A drama following a French platoon during Algeria''s war of independence', 'tt0825248', '/mQlekXRJwYB10yvMKkR6cCMvjQv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6027, 'Numb', False, True, False, False, 2015, 5.5, 'Hudson Milbank is a successful Hollywood screenwriter who suddenly and strangely finds himself without any emotional feelings. He tries doctor after doctor and shrink after shrink, but nothing works. The Golf Channel, lesbian exercise classes and a dizzying variety of pills get him through the day, but don''t quite solve his problem. His writing partner tries everything to get him back to normal, but it''s not until Hudson meets Sara that he finds a real motivation to get better and to actually start feeling again. From the writer of Deuce Bigalow, comes NUMB, a romantic comedy following an unusual man looking for strange love.', 'tt0795439', '/tH8olC1mCnxqqby8xdNOiy15B1q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6028, 'Serenity', False, True, False, False, 2019, 5.3, 'When the renegade crew of Serenity agrees to hide a fugitive on their ship, they find themselves in an action-packed battle between the relentless military might of a totalitarian regime who will destroy anything – or anyone – to get the girl back and the bloodthirsty creatures who roam the uncharted areas of space. But... the greatest danger of all may be on their ship.', 'tt0379786', '/2ZOm4cedJanmN85ZiWrwZPUNv0d.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6029, 'Medora', False, True, False, False, 2013, 7, 'In America’s basketball heartland, four resilient boys from rural Medora, Indiana, fight to end their high school team’s three-year losing streak, as their dwindling town faces the threat of extinction.', 'tt2514894', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6031, 'Nameless Gangster', False, True, False, False, 2012, 7.1, 'A corrupted customs officer, Choi Ik-hyeon, faces losing his job. Then, his life turns around as he meets ganster Choi Hyeong-bae who has connection with the Yakuza. They quickly form a partnetship; Hyeong-bae helps Ik-hyeon set up a business with the money from drug trafficking while Ik-hyeon lobbies for his partner Hyeong-bae. As the government declares war on crime, however, their partnership begins to crack.', 'tt2082221', '/gJxTwJUoo2GU7VQdjCzVBSxvA6b.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6033, 'Neighbours', False, True, False, False, 1952, 7.9, 'This film, shot in pixilation (a kind of stop-motion animation with actors), is about two neighbours who come to barbaric blows over a flower that straddles the property line.', 'tt0044958', '/dymMmwlmyQ1YGnkUKGHgCyIoS5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6034, 'Losing Isaiah', False, True, False, False, 1995, 6.5, 'Khaila Richards, a crack-addicted single mother, accidentally leaves her baby in a dumpster while high and returns the next day in a panic to find he is missing. In reality, the baby has been adopted by a warm-hearted social worker, Margaret Lewin, and her husband, Charles. Years later, Khaila has gone through rehab and holds a steady job. After learning that her child is still alive, she challenges Margaret for the custody.', 'tt0113691', '/ySu9LMUlrt7kSUvyVO7MldY3rNA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6035, 'Jesus Christ Vampire Hunter', False, True, False, False, 2001, 5, 'The first testament says "an eye for an eye." - The second testament says "love thy neighbour." - The third testament ... Kicks Ass!!!', 'tt0311361', '/iYAiPgkzaCAlMwQ7hyAr0h3D2uF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6037, 'Killers', False, True, False, False, 2010, 5.4, 'Mr. Nomura is an eerily handsome, sharply dressed, sociopathic serial killer who preys on the women of Tokyo. In Jakarta, a world-weary journalist named Bayu finds himself unexpectedly falling into vigilantism after brutally killing two sadistic robbers. When each posts videos of their violent sprees online, the pair find one another on the Internet and begin a toxic and competitive duel. While Bayu clings to the hope that he can resume a normal life, Nomura continues to spill blood without remorse. Killing, advises Nomura, is something everyone ought to consider.', 'tt2409300', '/6yfKaeX8cVxvOuLoK6XsfpiJsAe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6038, 'Guernica', False, True, False, False, 2016, 5.8, 'The fates of Henry - an American correspondent - and Teresa, one of the Republic''s censors during the Spanish Civil War.', 'tt2556114', '/xngfVEXUvtWHzYbrARHNTi51JeK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6039, 'Digging Up the Marrow', False, True, False, False, 2015, 5.8, 'He’s one of the hardest working filmmakers in the genre business.......So what’s HATCHET man Adam Green been up to this past year apart from working on his sit-com ‘Holliston’, writing KILLER PIZZA and prepping EXORCISM ON CROOKED LAKE? The answer is this documentary starting out exploring genre-based monster art and then taking an odd turn into the blurring of fantasy and reality. Because halfway through producing this treatise with cinematographer Will Barratt at his L.A. ArieScope Pictures offices, they are contacted by former policeman William Dekker who claims he can prove that monsters are indeed real. That they live in world just below our own named The Marrow and he knows where one of the entrances to this dark hidden universe is. Green of course is intrigued and so the monster hunting expedition begins… to become something else far more frightening than he ever imagined.', 'tt1991031', '/jwiiBtxyLj7QyysKRS99W8Rpad9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6041, 'Mr. Pip', False, True, False, False, 2012, 7.1, 'As a war rages on in the province of Bougainville in Papua New Guinea, a young girl becomes transfixed by the Charles Dickens novel Great Expectations, which is being read at school by the only white man in the village.  In 1991, a war over a copper mine in the South Pacific tore the island of Bougainville apart. The reclusive “Popeye” (Hugh Laurie) offers the children in fourteen-year-old Matilda’s tiny village an escape with Charles Dickens'' Great Expectations. But on an island at war, fiction can have dangerous consequences.', 'tt1485749', '/3PRCi4hWPwPsvpKcJJ4WRYJtQKo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6043, 'My Sweet Little Village', False, True, False, False, 1985, 8, 'The movie''s main storyline follows the life of Otík, a mentally retarded young man, in a tight-knit village community. The sweet-tempered Otík works as an assistant truck driver with Mr. Pávek, his older colleague and practical-minded neighbor. Pávek''s family takes care of Otík, whose parents are dead. However, the two coworkers become at odds over Otík''s inability to perform even the simplest tasks. Pávek demands that Otík be transferred to assist another driver, who happens to be a choleric and suspicious man named Turek (Turk in Czech). Rather than work with Turek, Otík decides to accept an offer of employment in Prague, but finds he does not fit in to the city life. After discovering that the transfer of Otík to Prague was a trick by a crooked politician to get a deal on Otík''s large inherited house, Pávek agrees to give Otík a second chance and retrieves him from the city to resume their work together.', 'tt0090257', '/zHliFDhZ9brANaN0ND7DCGT7I9H.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6045, 'Downhill', False, True, False, False, 2014, 6.5, 'Four old school friends reunite to attempt the epic coast to coast walk, across the United Kingdom. As their journey unfolds, this comically incompatible foursome walk full tilt into their mid-life crises.', 'tt2701390', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6049, 'If I Want to Whistle, I Whistle', False, True, False, False, 2010, 7.1, 'A teenager prisoner awaits his release when two weeks before that happens he''s told that his mother is returned home. Meanwhile, he finds himself in love with a Sociology student, Ana, working in the penitentiary as an intern.', 'tt1590024', '/hvWW28cMo8sCckquL4CFbHh3rN2.jpg');
