insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6083, 'Bustin'' Down the Door', False, True, False, False, 2008, 7, 'During the winter of 1975 in Hawaii, surfing was shaken to its core. A group of young surfers from Australia and South Africa sacrificed everything and put it all on the line to create a sport, a culture, and an industry that is today worth billions of dollars and has captured the imagination of the world. With a radical new approach and a brash colonial attitude, these surfers crashed headlong into a culture that was not ready for revolution. Surfing was never to be the same again.', 'tt1129921', '/5BroMyrqB7JTx6v8BJ7ATZi6tb6.jpg');
