insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6086, 'Silsila', False, True, False, False, 1981, 7.4, 'Although orphaned at very young ages, Scriptwriter Amit and military pilot Shekhar are brothers and also very good friends. The latter is happily engaged to Shobha, while the former has just fallen in love with pretty, young Chandni. But tragedy strikes when Shekhar''s plane is shot down in Kashmir. Amit feels it to be his obligation to leave the girl he loves and marry his brother''s pregnant fiancée instead. But when, by an unexpected turn, Amit and the now also married Chandni meet again, their love for each other proves to be stronger than their marital vows.', 'tt0083081', '/7uzX38mxGZBBEdbxB4yxEw2vFvF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6087, 'Inside Hana''s Suitcase', False, True, False, False, 2009, 7.7, 'A Theatrical Documentary Synopsis "Inside Hana''s Suitcase", is the poignant story of two young children who grew up in pre-WWII Czechoslovakia and the terrible events that they endured just because they happened to be born Jewish. Based on the internationally acclaimed book "Hana''s Suitcase" which has been translated into 40 languages', 'tt1329370', '/4bvoKkaVJSqCIfgjibDDjhejTL6.jpg');
