insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6109, 'Dark Horse', False, True, False, False, 2011, 5.9, 'DARK HORSE tells the larger than life true story of how a barmaid in a former mining village in South Wales bred a racehorse on her allotment that went on to become a champion.  Jan had successfully bred dogs and birds and believed she could do the same with a different animal – though she knew nothing about racing and had never been on a horse. Convincing a handful of locals to part with ten pound a week for her scheme, she found a thoroughbred mare with a terrible racing record for £300, a stallion past his best, put them together and – against all the odds – bred a winner.  It’s an audacious tale of luck and chance and beating the odds; a story of how a gaggle of working class folk from the Welsh Valleys took on the racing elite, broke through class and financial barriers, and brought hope and pride back to their depressed community.', 'tt4137902', '/b0hyVLGAD31L0yzjB4eKSavVc7f.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6110, 'Black Sun', False, True, False, False, 2005, 7.4, 'Blurred images illustrate the narration of Hugues de Montalembert, who was blinded during a mugging.', 'tt0478101', '/cY1rFXQpyXOIKQeThf6N0CK6e5W.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6111, 'Werewolf', False, True, False, False, 2017, 6, 'A team of archaeologists with unplaceable accents unearth a skeleton at a dig in an Arizona quarry. The Native Americans on the dig instantly recognize it as the remains of a werewolf, so the team leaders immediately take it to their lab and stare at it. Natalie, one of the team members, thinks it''s absolutely fascinating, but Yuri siezes on greater ambitions when one of the dig assistants, who injured themself on the remains, becomes infected and turns into a werewolf in full view of hospital personnel. Yuri then conducts an experiment infecting other people in town to see if they turn into werewolves as well. When Paul arrives at the lab to assist in analyzing the remains, Yuri infects him, too. After Paul''s transformation (under a week-long full moon), Natalie tries to save him.', 'tt0118137', '/fpS1AajcFeSlDzbqtbOPbGBDJmz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6112, 'The Funhouse Massacre', False, True, False, False, 2015, 5.2, 'Six of the worlds scariest psychopaths escape from a local Asylum and proceed to unleash terror on the unsuspecting crowd of a Halloween Funhouse whose themed mazes are inspired by their various reigns of terror.', 'tt4483220', '/vKgDw6miuun9ij8H7YSAte7PLZA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6113, 'Lou', False, True, False, False, 2017, 7.9, 'A Pixar short about a lost-and-found box and the unseen monster within.', 'tt6267732', '/zAQmOmQPWyvU34B3gcK163P4jmH.jpg');
