insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6117, 'Extremities', False, True, False, False, 1986, 6.3, 'A woman escapes from the man who is about to rape her, but leaves her purse behind. Afraid that her attacker might come after her, she goes to the police, but with no proof of the incident, they can do nothing. In fact, the man does use the information in her bag and comes to her apartment with the intent of rape, but she sprays him in the face with insect repellent, and then holds him captive. She is then faced with deciding whether to go to the police who might not believe her and release him, or to kill him.', 'tt0091024', '/zG2EsB1lw9uNRoYn6BQNUEFADZZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6118, 'I Know That Voice', False, True, False, False, 2014, 7.5, 'A documentary about voice-over actors.', 'tt2113683', '/y5aSllFoF11fVu945IUZtR4h37l.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6119, 'All These Sleepless Nights', False, True, False, False, 2016, 6.6, 'What does it mean to be awake in a world that seems satisfied to be asleep? Kris and Michal push their experiences of life and love to a breaking point as they restlessly roam the city streets in search of answers, adrift in the euphoria and uncertainty of youth.', 'tt5146068', '/rovjP5yCUpTAHX8XlNYE9Rerygg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6121, 'Hwayi: A Monster Boy', False, True, False, False, 2013, 7, 'A 5 member gang, led by Seok-Tae (Kim Yun-Seok), kidnaps a baby boy named Hwa-Yi and raises the baby like their own son. The baby boy is now 17 years old (Yeo Jin-Goo) and has turned into a lethal killer. Taking part in his father''s gang, Hwa-Yi learns about his own past. Hwa-Yi pulls out his gun to find out who he really is.', 'tt2972362', '/t2O3v15Gxgc1Z89AL3QkJmIJQf9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6122, 'Hunter Gatherer', False, True, False, False, 2016, 6.2, 'After a three-year stint in prison, an unreasonably optimistic middle-aged man returns to his stagnant neighborhood to win back his girlfriend only to find that she and his family have done what they always wanted to do — forget he exists.', 'tt3717316', '/iWfR9cCCio8zFSIU7SKYj2DpqXe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6123, 'Ecstasy', False, True, False, False, 1933, 6.7, 'ECSTASY is a dark romantic comedy, based on the controversial book, “Ecstasy”, by Irvine Welsh. “Ecstasy”, was translated into 20 languages and was a number one bestseller in over 20 countries. Mr. Welsh’s first book, “Trainspotting”, published in 1993, (and voted by Waterstone, Europe’s largest bookstore chain, as one of the Ten Best Books of the Century), sold over 1 million copies in the UK alone, and has its own Cinematic Cinderella success story.', 'tt1809287', '/xNow6mpgMUtrY2Q0mMKszD4KFLu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6125, 'The Relic', False, True, False, False, 1997, 5.8, 'A researcher at Chicago''s Natural History Museum returns from South America with some crates containing his findings. When the crates arrive at the museum without the owner there appears to be very little inside. However, police discover gruesome murders on the cargo ship that brought the crates to the US and then another murder in the museum itself.', 'tt0120004', '/3MpMbZTrXp6ccrir16nV7YTJ4vx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6127, 'Augustine', False, True, False, False, 2012, 6, 'Set in Belle Époque France, the story follows nineteen-year-old "hysteria" patient Augustine, the star of Professor Charcot''s experiments in hypnosis, as she transitions from object of study to object of desire.', 'tt2098628', '/ucz3pPJoupNypRZggxJkH2wy61e.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6128, 'Leonard Cohen: I''m Your Man', False, True, False, False, 2006, 6.8, 'Poet, singer / songwriter and ladies man Leonard Cohen is interviewed in his home about his life and times. The interview is interspersed with archive photos and exuberant praise and live perfomances from an eclectic mix of musicians, including: Jarvis Cocker, Rufus &amp; Martha Wainwright, Teddy Thompson, Antony Hegarty, The Handsome Family and U2''s Bono and The Edge.', 'tt0478197', '/5b2G1junuUkQn5MtMDXobzHUbDZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6129, 'Lamb', False, True, False, False, 2016, 6.3, 'Lamb, based on the novel by Bonnie Nadzam, traces the self-discovery of David Lamb in the weeks following the disintegration of his marriage and the death of his father. Hoping to regain some faith in his own goodness, he turns his attention to Tommie, an awkward and unpopular eleven-year-old girl. Lamb is convinced that he can help her avoid a destiny of apathy and emptiness, and takes Tommie for a road trip from Chicago to the Rockies, planning to initiate her into the beauty of the mountain wilderness. The journey shakes them in ways neither expects.', 'tt3908598', '/aj0pgKR4dWlRRb1Q05JGQtwxHbj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6130, 'The Double', False, True, False, False, 2011, 5.9, 'Funny comedy about a life of super-famous mega-star Igor Uspensky and his fight for "Man of a Year" title.', 'tt2593290', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6131, 'Disco Pigs', False, True, False, False, 2001, 6.7, 'Pig and Runt born on the same day, in the same hospital, moments apart. Twins, all but by bloodline. Inseparable from birth, they are almost telepathic. They are one, needing no one else, inhabiting a delicate, insular and dangerous world where they make their own rules and have their own language. But days before their 17th birthday the balance of their world begins to shift. Pig''s sexual awakening and jealousy begins to threaten their private universe.', 'tt0236157', '/f9q7RttrBQwkoKh8pjkNWXxVMtd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6132, 'Wish Upon a Star', False, True, False, False, 1996, 6.7, 'Seventeen-year-old Alexia Wheaton hasn''t been herself lately. Neighter has her 15-year-old sister Hayley. In fact they''ve been each other. Alexia becomes fashion-challenged Hayley and she in turn becomes styling Alexia and the complications come fast furious and funny.', 'tt0118178', '/5Iu6Wljx1BgLiN3SwLNKObITxTJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6133, 'The Lords of Flatbush', False, True, False, False, 1974, 5.8, 'Directed by Martin Davidson and Stephen Verona, The Lords of Flatbush is a low budget film starring Perry King, Henry Winkler and Sylvester Stallone (who also wrote additional dialog). Set in the late 1950s, the coming-of-age story follows four Brooklyn teenagers known as The Lords of Flatbush. The Lords chase girls, steal cars, play pool and hang out at a local malt shop. The film focuses on Chico (King) attempting to win over Jane (Susan Blakely), a girl who wants little to do with him, and Stanley (Stallone), who impregnates his girlfriend Frannie, who wants him to marry her.', 'tt0071772', '/zmXNjWFK4zlkStngN2qdVZ9Vprh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6134, 'Age of Uprising: The Legend of Michael Kohlhaas', False, True, False, False, 2013, 6.1, 'In the 16th century in the Cévennes, a horse dealer by the name of Michael Kohlhaas leads a happy and prosperous family life. When a lord treats him unjustly, this pious, upstanding man raises an army and puts the country to fire and sword in order to have his rights restored.', 'tt2054790', '/hlLec0D9nq9TSv5OjbgqPqEDGKK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6136, 'Mad Money', False, True, False, False, 2008, 5.8, 'Three female employees of the Federal Reserve plot to steal money that is about to be destroyed.', 'tt0951216', '/le0r7Hf7YOy3QFdOoYXRI29T1mW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6139, 'Herman''s House', False, True, False, False, 2012, 6.3, '''What kind of house does a man who has been imprisoned in a six-foot-by-nine-foot cell for over 30 years dream of?'' This film captures the remarkable creative journey and friendship of Herman Wallace, one of the Angola 3, and artist Jackie Sumell while examining the injustice of prolonged solitary confinement.', 'tt2079512', '/6qUlCGHp1gtNQqd4ooFoJVSVqSb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6140, 'Tracers', False, True, False, False, 2015, 5.6, 'Wanted by the mafia, a New York City bike messenger escapes into the world of parkour after meeting a beautiful stranger.', 'tt2401097', '/4JZ5XlgOuyPN3yHePbvWOSj2FrV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6141, 'Cromwell', False, True, False, False, 1970, 6.9, 'Disgusted with the policies of King Charles I, Oliver Cromwell plans to take his family to the New World. But on the eve of their departure, Cromwell is drawn into the tangled web of religion and politics that will result in the English Civil War.', 'tt0065593', '/1qlnKlQGEdRl70KDpAv03F8uGzI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6142, 'Honeydripper', False, True, False, False, 2007, 6.6, 'In 1950s Alabama, the owner of the Honeydripper juke joint finds his business dropping off and against his better judgment, hires a young electric guitarist in a last ditch effort to draw crowds during harvest time.', 'tt0829193', '/x1L0yIPOKWL8KVx6oWlPRl4ExaC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6145, 'Flu', False, True, False, False, 2013, 6.7, 'Infected through the respiratory system, there is a new kind of disease that kills in 36 hours once infected. The people of the city struggle to survive this. The worst epidemic ever seen is sweeping through Bundang, the suburb of Seoul. After smuggling illegal immigrants into the country, Byung-woo dies from an unknown virus. Soon after that, the same symptoms are plaguing scores of residents in Bundang.', 'tt2351310', '/mvTXhTddHIZbOU9pmrcPODI1kRx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6146, 'Wassup Rockers', False, True, False, False, 2005, 6, 'Instead of adhering to the norms of their South Central neighborhood, a group of skater boys opt to bus into Hollywood and Beverly Hills, where they attract local rich girls - and plenty of trouble with the police, jealous boyfriends, and nervous parents.', 'tt0413466', '/mABP5REdreZ4ZMhP0jUgyQlQBqB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6147, 'The Mighty Macs', False, True, False, False, 2009, 6.5, 'In the early 70s, Cathy Rush becomes the head basketball coach at a tiny, all-girls Catholic college. Though her team has no gym and no uniforms -- and the school itself is in danger of being sold -- Coach Rush looks to steer her girls to their first national championship.', 'tt1034324', '/frNp8Rmkr8wBDyKrZ89gYvrtxBa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6149, 'Jolene', False, True, False, False, 2008, 6.4, 'A teenage orphan spends ten years traveling to experience life.', 'tt0867334', '/890q23ZDbtdGZqAYyscu1Hb4ybr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6150, 'Impact', False, True, False, False, 1949, 7.1, 'While the entire world watches the largest meteor shower in 10,000 years, a rogue asteroid, hidden by the meteor field, smashes into the moon in a tremendous explosion of rock and debris. Fragments from the asteroid, and even from the moon itself, penetrate Earth''s atmosphere and make impact. Even though the initial damage is minimal, nerves are frayed throughout the planet. There is significant physical damage to the lunar surface, but experts quickly conclude there will be no lasting ramifications. Then strange anomalies begin to manifest themselves on Earth. It starts small - cell phone disruptions, unusual static charges and odd tidal behavior. The world''s leading scientists, including Alex Kittner, Maddie Rhodes and Roland Emerson, begin piecing together evidence that suggests the moon''s properties, and its orbit, may have been permanently altered. Their fears are realized...', 'tt1227637', '/rtVhL5dPE3hIopJZumidh9e35Jp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6151, 'Frankie & Alice', False, True, False, False, 2010, 6.5, 'A drama centered on a go-go dancer with multiple personality disorder who struggles to remain her true self and begins working with a psychotherapist to uncover the mystery of the inner ghosts that haunt her.', 'tt1221208', '/4zXkZirn2QezrEFHWeAOkOxdjxj.jpg');
