insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6153, 'Elephant Song', False, True, False, False, 2014, 6.6, 'A psychiatrist is drawn into a complex mind game when he questions a disturbed patient about the disappearance of a colleague.', 'tt3754976', '/y3qOJfntEY7Fm2W0cClS9X5vGYq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6155, 'The Jazz Singer', False, True, False, False, 1980, 5.9, 'A young Jewish man is torn between tradition and individuality when his old-fashioned family objects to his career as a jazz singer. This is the first full length feature film to use synchronized sound, and is the original film musical.', 'tt0018037', '/hO3pM0Pf8eGnkt8049aKYr8yqJF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6156, '9th Company', False, True, False, False, 2005, 7.1, 'The picture opens in Krasnoyarsk, Siberia, in late 1988, when military trainees Petrovsky, Ryaba, Chugun, Stas, Pinochet, Lyutev and Vorobyev are whipped into shape at a training camp by the brutal, sadistic commander, Warrant Officer Dygalo - prior to being sent off to the front lines. After several one-by-one dalliances with the local whore, Snow White, and a cautionary lecture on the history, geography and culture of Afghanistan (which most of the men sleep through), the trainees head off to battle - first to the Bagram air base, then to the Afghani province of Khost to secure supply lines. But nothing can begin to prepare them for the brutal devastation into which they are plunged, or the relentless tide of slaughter that scatters thousands of Soviet victims in its wake.', 'tt0417397', '/5hEv2v90DvhS9NjdLG5kL2amD2Z.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6160, 'Sharknado', False, True, False, False, 2013, 3.3, 'A freak hurricane hits Los Angeles, causing man-eating sharks to be scooped up in tornadoes and flooding the city with shark-infested seawater. Surfer and bar-owner Fin sets out with his friends Baz and Nova to rescue his estranged wife April and teenage daughter Claudia', 'tt2724064', '/atEmHkVFTSGRYt2PeCiziQqbZnI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6162, 'Aliyah', False, True, False, False, 2012, 6.1, 'Paris 2011. Alex is 27. He''s lives off dealing and pays the debts of his brother, Isaac, who after being his support has now become dead weight. When his cousin announces he''s going to open a restaurant in Tel Aviv, Alex imagines he can join him and change his life. Set on emigrating, Alex has to find money, leave his beloved Paris, end his complicated love life, drop his destructive brother and find his way.', 'tt2339351', '/kGHtsrYbDqApmGnvlDEcGVdwg6h.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6163, 'Mambo Italiano', False, True, False, False, 2003, 6.7, 'A sweet comic film about an Italian man who comes out of the closet and the affect it has on his life and his crazy family. A family movie about the stereotypes of homosexuals and Italians – called by critics "the gay" My Big Fat Greek Wedding.', 'tt0330602', '/rzR8eP1TfvsyiA04EUX0y7cOGv0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6164, 'The Terrorizers', False, True, False, False, 1986, 7.8, 'The lives of a couple, an amateur photographer and an injured girl intertwine.', 'tt0091355', '/6UCPktMrZNMwydkTIhru6qSVONp.jpg');
