insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6167, 'Rock-A-Doodle', False, True, False, False, 1991, 6.1, 'Chanticleer is a foolhardy farm rooster who believes his crows can actually make the sun come up and shine. When the sun rises one morning without Chanticleer''s crow, he leaves the farm in disgrace and runs off to become a rock ''n'' roll singer. But in his absence, a sinister, sunshine-hating owl prepares to take over.', 'tt0102802', '/q5xSS8MNmrwR3em6VB6OC3992d6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6168, 'The Trouble with the Truth', False, True, False, False, 2011, 5.5, 'Musician and starving artist Robert reconsiders his own failed marriage to Emily after his daughter announces that she''s engaged.', 'tt1727381', '/P5knUMiDTgIi6FpZoasjmAO0hp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6170, 'About Alex', False, True, False, False, 2014, 6.4, 'A circle of twenty-something friends reunite for a weekend away to console a suicidal member of their group. Yet, despite their best efforts to enjoy themselves, a tinderbox of old jealousies, unrequited love, and widening political differences leads to an explosion of drama that, coupled with the flammable combination of drugs, wine, and risotto, cannot be contained. A Big Chill for our current social media moment, About Alex is a lighthearted look at the struggles of a generation that has it all—and wants more.', 'tt2667918', '/h2V85AL6bt5jQi7rLHKy9CSLlFd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6171, 'The Group', False, True, False, False, 1966, 6.5, 'Sidney Lumet''s adaptation of the acclaimed Mary McCarthy novel. It''s 1933, and eight young women are friends and members of the upper- class group at a private girl''s school, about to graduate and start their own lives. The film documents the years between their graduation and the beginning of the World War in Europe, and shows, in a serialized style, their romances and marriages, their searches for careers or meaning in their lives, their highs and their lows.', 'tt0060479', '/sdi14MruD5SJEAxmJisPwE3ZJNI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6172, 'Eating Out', False, True, False, False, 2004, 5.8, 'After getting dumped by his girlfriend Tiffani (Rebekah Kochan), Caleb (Scott Lunsford) commiserates with his roommate Kyle (Jim Verraros), who notes that while he has trouble getting the men he wants he could get any woman because he''s gay.', 'tt0372884', '/xSbgNViEhXOFpaAZ1nB1AEC4pwg.jpg');
