insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6176, 'The Outlaw', False, True, False, False, 1946, 5.5, 'Newly appointed sheriff Pat Garrett is pleased when his old friend Doc Holliday arrives in Lincoln, New Mexico on the stage. Doc is trailing his stolen horse, and it is discovered in the possession of Billy the Kid. In a surprising turnaround, Billy and Doc become friends. This causes the friendship between Doc and Pat to cool. The odd relationship between Doc and Billy grows stranger when Doc hides Billy at his girl, Rio''s, place after Billy is shot.', 'tt0036241', '/gogVbTwpSLRD4bAc4L66q5hBwBt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6179, 'Good Kids', False, True, False, False, 2016, 6.1, 'Four high school students look to redefine themselves after graduation.', 'tt2170427', '/9R3hZnqFNyFmG10qwKzaUntlUbg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6181, 'Dare', False, True, False, False, 2009, 5.8, 'A drama centered around three high school seniors - an aspiring actress, her misfit best friend, and a loner - who become engaged in an intimate and complicated relationship.', 'tt1241316', '/aNVGaSM1MC4YOEEuvkXx5U6sBVu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6182, 'Gunnin'' for That #1 Spot', False, True, False, False, 2008, 6.5, 'The film follows 8 of the top high school basketball players in the US at the time of filming, in 2006. The plot centers around the first annual Boost Mobile Elite 24 Hoops Classic game at the legendary Rucker Park in Harlem.', 'tt1204298', '/w1AvotnLjPEvLLcU7rtLlLkxd7k.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6183, 'Dear Lemon Lima', False, True, False, False, 2009, 6.5, 'Vanessa gets a dose of reality when Philip, her one true love, ends their relationship, again. The quirky teen enrolls in his school to win him back, but ends up making matters worse. Downgraded to social outcast, Vanessa struggles to reclaim Phillip''s affection. Luckily, when Vanessa is declared a captain for the school''s Snowstorm Survivor competition, she assembles a team of like-minded misfits to prove they deserve to compete and hopefully win her love''s heart again.', 'tt1242423', '/hSu4m2UDjIl4aRg5pIo4JcZt1LN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6185, 'H.H. Holmes: America''s First Serial Killer', False, True, False, False, 2004, 6.3, 'Torture chambers, acid vats, greased chutes and gassing rooms were just some of the devices of death designed by the Torture Doctor, H.H. Holmes in his castle of horrors. Follows Holmes'' entire life as a criminal mastermind.', 'tt0358500', '/bZOfFhkrwhqBynucPcgXkZ5SFAW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6186, 'Technotise: Edit & I', False, True, False, False, 2009, 7.5, 'Technotise: Edit &amp; I is a Serbian animated feature film. The plot is set in 2074 in Belgrade. The main character is Edit Stefanović, a female psychology student who, after failing the same university exam for the sixth time, decides to visit a dealer on the black market who installs a stolen military chip in her body that will record everything she sees to help pass the exam. Edit also has a job at a scientific and social research company, in taking care of Abel Mustafov, an autistic math genius who discovered a formula that connects all forces in the world, but no computer was able to calculate it fully without becoming self aware and shutting down immediately after that. After Edit sees the formula graph, the chip calculates the formula, able to "survive" thanks to its connection to Edit, develops a parallel personality and affords her abilities greater than she ever imagined...', 'tt1372301', '/lhtA4bJqyxjLTU8r91JwmfRZToU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6187, 'Showrunners: The Art of Running a TV Show', False, True, False, False, 2014, 6.7, '“Showrunners” is the first ever feature length documentary film to explore the fascinating world of US television showrunners and the creative forces aligned around them. These are the people responsible for creating, writing and overseeing every element of production on one of the United State’s biggest exports – television drama and comedy series.  Often described as the most complex job in the entertainment business, a showrunner is the chief writer / producer on a TV series and, in most instances, the show’s creator. Battling daily between art and commerce, showrunners manage every aspect of a TV show’s development and production: creative, financial and logistical.', 'tt1942971', '/kWEPPZKsF3OFNZgyEoFc56I3VOr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6188, 'Hiding Out', False, True, False, False, 1987, 6.4, 'A very successful stock broker is called to court to testify against a mob boss who was into some inside trading. Andrew Morenski must become Max Hauser and go back to high school for protection from the mob.', 'tt0093186', '/jtGLfT8ckPmQgVhB3qhb3p0mnv8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6190, 'The Saratov Approach', False, True, False, False, 2013, 5.6, 'An inspirational true story.  On what seemed like any other day, Elders Travis Tuttle (Corbin Allred) and Andrew Propst (Maclain Nelson) are approached by Nikolai (Nikita Bogolyubov) to teach a friend. But then the missionaries experience the unimaginable - they are kidnapped, beaten, and held for ransom. While their families, friends, and the world pray for their safe release, Tuttle and Propst are tested physically, emotionally, and most of all spiritually.', 'tt2887322', '/iRBQn1zWbtw5YbxjbuAPHqL81qg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6191, 'Yesterday', False, True, False, False, 2004, 7.6, 'The late 1960s was the time of Beatles and Rolling Stones, the time of sexual revolution. These events have their echo in Bulgarian English-learning school. The school order provokes a protest of the students due to the narrow-minded teachers.', 'tt0170783', '/mFGJwHaxBV8pVP4SFBY373N5vbo.jpg');
