insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6195, 'Extinction', False, True, False, False, 2015, 5.8, 'And suddenly, overnight, the world came to a halt. Two men, two survivors, one kid, and hatred that separates them. A place forgotten by everyone, including the creatures that inhabit the Earth... until now.', 'tt3467412', '/zwIDreu9pYCqKpv4jomqVM9yX1e.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6196, 'Dark Was the Night', False, True, False, False, 2014, 5.6, 'An evil is unleashed in a small town when a logging company sets up shop in the neighboring woods. Isolated and threatened, a mysterious force hidden within the trees outside the small town of Maiden Woods, strikes fear in the townspeople as Sheriff Paul Shields attempts to overcome the demons of his past while protecting those that he loves.', 'tt2251281', '/eLJPuZltlAJ9E4OTGYLYh5LDjsB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6197, 'Southern Baptist Sissies', False, True, False, False, 2013, 6.8, 'Southern Baptist Sissies is the live film of the GLAAD Award winning play by Del Shores.  Southern Baptist Sissies is the story of four boys who are gay growing up in the Southern Baptist Church and how they each deal differently with the conflict between the teachings of the church and their sexuality.', 'tt2729818', '/pffnWInrQGsb3o4JH8fCNLvRJIU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6198, 'Under the Boardwalk: The Monopoly Story', False, True, False, False, 2011, 6.2, 'The 2010 documentary Under the Boardwalk: The MONOPOLY Story, covering the history and players of the game, won an Audience Award for Best Documentary at the 2010 Anaheim International Film Festival. The film played theatrically in the US beginning in March 2011 and will be released on Amazon &amp; iTunes on February 14, 2012. The film is narrated by Zachary Levi.', 'tt1250861', '/wQ5FFOSOoGgocLOry5FMJ8c71q2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6199, 'Clandestine Childhood', False, True, False, False, 2011, 7, 'Juan lives in clandestinity. Just like his mum, his dad and his adored uncle Beto, outside his home he has another name. At school, Juan is known as Ernesto. And he meets María, who only has one name. Based on true facts, set in the Argentina of 1979, this film is "one about love".', 'tt1726888', '/f9kD66dPRbJZYZkw1jCU4ubCjCC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6200, 'The Gruffalo''s Child', False, True, False, False, 2011, 7.1, 'A follow up to the 2009 animated feature and adapted from the childrens'' book by Julia Donaldson and Alex Scheffler.  The Gruffalo''s child explores the deep dark wood in search of the big bad mouse and meets the Snake, Owl and Fox in the process.  She eventually finds the mouse, who manages to outwit her like the Gruffalo before!', 'tt2132486', '/n8fu0eATvUWtmR9CsDlL1gwqTcp.jpg');
