insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6202, 'Edmond', False, True, False, False, 2005, 6.2, 'Seemingly mild-mannered businessman Edmond Burke visits a fortuneteller and hears a remark that spurs him to leave his wife abruptly and seek what is missing from his life. Encounters with strangers and unsavory people weaken the barriers encompassing his long-suppressed rage, until Edmond explodes in violence.', 'tt0443496', '/2VFdGJDrl5n9WoC7d4I07hN3v5c.jpg');
