insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6207, 'The Take', False, True, False, False, 2007, 5.8, 'Freddie Jackson is just out of prison. He''s done his time, made the right connections and now he''s ready to use them. His wife Jackie dreams of having her husband home but she''s forgotten the rows and the girls Freddie can''t leave alone. His younger cousin, Jimmy, dreams of making a name for himself on Freddie''s coattails.', 'tt1366321', '/oOymJmOKBE9YlOmGVdPEpnNgeYx.jpg');
