insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6414, 'For My Father', False, True, False, False, 2008, 7.3, '', 'tt1043869', '/wOmzC0N9aulFdVgg6PamLD3dOQ0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6415, 'Teenage', False, True, False, False, 2013, 6.6, 'Teenagers did not exist before the 20th century. Not until the early 1950s did the term gain widespread recognition, but with Teenage, Matt Wolf offers compelling evidence that “teenagers” had a tumultuous effect on the previous half-decade.', 'tt2196055', '/asdsho25knSTdpCHtjPGSHVLOs5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6416, 'Off the Black', False, True, False, False, 2006, 6.6, 'An aging, disillusioned alcoholic (Nick Nolte) gets a younger friend (Trevor Morgan) and wants him to pose as his son at a school reunion.', 'tt0479965', '/6831QPXkThrg81HFbABYuJqCrpS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6418, 'Tuff Turf', False, True, False, False, 1985, 6.2, 'The new guy (James Spader) in a Los Angeles high school does some singing and fights a hotshot (Paul Mones) over a disco dancer (Kim Richards).', 'tt0090213', '/9FueZsXEXNqFlmB2o2WuGQPdkv5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6419, 'The English Teacher', False, True, False, False, 2013, 5.8, 'Teacher Linda Sinclair (Julianne Moore) balances her staid home life with an incredible passion for her subject, but her routine is forever altered when a former star pupil and his unsupportive father reenter her life. Go-to television director Craig Zisk, whose credits include Scrubs, Weeds and United States of Tara, takes a turn on the big screen with this insightful comedy about self-discovery co-starring Greg Kinnear, Nathan Lane, Michael Angarano and Lily Collins.', 'tt2055765', '/mF14chZNzgDRZffs8Wk8lPwdNQA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6420, 'The Lost Arcade', False, True, False, False, 2015, 6.6, 'Chinatown Fair opened as a penny arcadeon Mott Street in 1944. Over the decades, the dimly lit gathering place, known for its tic-tac-toe playing chicken, became an institution, surviving turf wars between rival gangs, changing tastes and the explosive growth of home gaming systems like Xbox and Playstation that shuttered most other arcadesin the city. But as the neighborhood gentrified, this haven for a diverse, unlikely community faced its strongest challenge, inspiring its biggest devotees to next-level greatness.', 'tt3780754', '/orkoG3HcFlJNpiqwWqbEGLx9kOF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6421, 'Low Down', False, True, False, False, 2014, 6.1, 'The daughter of jazz pianist Joe Albany witnesses her beloved father''s struggle -- and failure -- to kick his heroin habit.', 'tt1864405', '/f4fbeXE37SAySKJhpnM0cla5WJ4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6422, 'Where the Trail Ends', False, True, False, False, 2012, 7.7, '"Where the Trail Ends..." is a film following the worlds'' top freeride mountain bikers as they search for un-ridden terrain around the globe, ultimately shaping the future of big mountain freeriding.  This unparalleled story documents man''s challenge of mother nature and himself showcased through a cast of colorful characters. This is the most progressive and ambitious mountain biking ever attempted resulting in an entertainment adventure unlike anything experienced before.', 'tt2509922', '/28siwjNzwvOkRkoEUoF9ok1neQe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6423, 'The Salt of Life', False, True, False, False, 2011, 6.3, 'Gianni is sixty. He is retired but has not become lazy for all that. In fact he is a helpful fellow who gives a hand to all those who need one: shopping for his wife, walking the pretty neighbor''s dog, and so on. Everybody likes Gianni, but is it for the right reasons? Doesn''t his wife profit by the situation (she still works so it is only logical that Gianni do all the chores)? Isn''t he subject to the excruciating whims of his rich mother?... Sure, everybody LIKES Gianni, but who LOVES him? Agreed, being kind to them, he is the ladies pet, but he does not attract them anymore. That is why, when his macho lawyer friend Alfonso blames him for not having young mistresses "like every other senior Italian male", Gianni, who is beginning to ask himself questions about what it is like to become old, starts chasing dames.', 'tt1813327', '/hytpMG2j3UqTt6abLH2RlnFnmiz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6424, 'Assault on Wall Street', False, True, False, False, 2013, 6, 'Jim is an average New Yorker living a peaceful life with a well paying job and a loving family. Suddenly, everything changes when the economy crashes causing Jim to lose everything. Filled with anger and rage, Jim snaps and goes to extreme lengths to seek revenge for the life taken from him.', 'tt2368553', '/4dm5XEXIT73aB0g1nrplwYguaU9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6426, 'The Emperor Jones', False, True, False, False, 1933, 6.4, 'Unscrupulously ambitious ,Brutus Jones escapes from jail after killing a guard and, through bluff and bravado, finds himself the emperor of a Caribbean island.', 'tt0023985', '/676jzPaamXDHe0pJdiiHkt9ZTFd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6427, 'Here and There', False, True, False, False, 2009, 6.3, 'Here and There (Serbian: Tamo i ovde) is a Serbian film which was premiered at the Belgrade Film Festival FEST 2009. Here and There follows two interconnected stories on two different continents. Robert (Thornton), a depressed New Yorker, tries to make quick cash and ends up in Serbia, where instead of money he finds his soul. At the same time, a young Serbian immigrant, Branko (Trifunović), struggles in an unforgiving New York, desperately trying to bring his girlfriend from Serbia to the United States. Mirijana Karanović plays Branko''s mother.', 'tt1118045', '/3L7NbxK9bi4NrD09i2uzt0XvFB4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6428, 'The One-Armed Swordsman', False, True, False, False, 1967, 7.4, 'An evil gang attacks the Chi school of Golden Sword Kung Fu. One student sacrifices his life to save his teacher and his school, his dying wish is that his son be taken in as a student. Young Fang Kang grows up in the school and treasures his father''s broken sword and the memory of his father''s sacrifice. The other students (including the teacher''s daughter) resent him and try to drive him away. The teacher''s daughter challenges him to a fight and when he refuses she becomes enraged and recklessly chops off his arm! He retreats, broken and bloody, and is found by a young poor girl living alone who nurses him back to health. Meanwhile, the evil gang who originally attacked the Golden Sword school develops a weapon that renders the Golden Sword useless and starts killing off all of the schools students. Fang Kang eventually recovers with the girl''s help but must now face a life with only one arm. Will he be able to recover and live to defend the school as his father did?', 'tt0061597', '/y4qXyE6Y9oLFLoJRwq8B703QxRN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6429, 'Motorama', False, True, False, False, 1991, 6.4, 'A ten year old boy gets tired of life with abusive parents and cashes in his piggy bank and steals a Mustang. He rides off into a surreal America playing "Motorama," a game sponsored by Chimera Gas Company. He has various encounters with different people, and eventually reaches the Chimera Gas Company where he finds they are not playing by the rules of the game.', 'tt0104922', '/dh3cSeQSffe9KuGCr2Z88wfOi1S.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6430, 'The Royal Tailor', False, True, False, False, 2014, 7, 'Depicts the love lives, jealousies and desires of those that work within the Sanguiwon during the Joseon Dynasty period. The Sanguiwon are responsible for the attire worn by royalty. Dol-Seok (Han Suk-Kyu) is the best master artisan in charge of royal attire. He views set rules as paramount to his job. Kong-Jin (Ko Soo) is a genius like designer, born with dexterity and an excellent sense. He was brought to the palace by nobleman Pan-Soo (Ma Dong-Seok) who first spotted his talent. The King (Yoo Yeon-Seok) and Queen (Park Shin-Hye) then become embroiled in a critical case because of the royal attire made by Dol-Seok and Kong-Jin.', 'tt4024104', '/mjndh3KmqMC13vtiSecOLksPsi4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6431, 'The End of Poverty?', False, True, False, False, 2008, 7.4, 'The End of Poverty? asks if the true causes of poverty today stem from a deliberate orchestration since colonial times which has evolved into our modern system whereby wealthy nations exploit the poor. People living and fighting against poverty answer condemning colonialism and its consequences; land grab, exploitation of natural resources, debt, free markets, demand for corporate profits and the evolution of an economic system in in which 25% of the world''s population consumes 85% of its wealth. Featuring Nobel Prize winner Amartya Sen and Joseph Stiglitz, authors/activist Susan George, Eric Toussaint, Bolivian Vice President Alvaro Garcia Linera and more.', 'tt0903943', '/1vAaVqy9CJUQsysMWBYtqKFCmEz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6432, 'What Did You Do in the War, Daddy?', False, True, False, False, 1966, 6.8, 'A by-the-book Captain is ordered to capture a strategic village in Italy. The Italian soldiers are willing to surrender, if they can have a festival first. The lieutenant convinces the Captain this is the only way. Because of aerial reconnaissance, they must look like they are fighting. To sort this out an intelligence officer is sent in. Meanwhile the festival gets complicated with the Mayors daughter.', 'tt0061176', '/pQ3lPGHe0NJAiimDsGxAhPkHW83.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6433, 'Fragile', False, True, False, False, 2005, 6.1, 'Haunted by memories of a patient''s death, a nurse  takes a job at an antiquated hospital for children. Soon she learns that the kids fear a ghost that prowls the floors and will not allow anyone to leave. Amy tries to protect them and convince the other staffers of the evil that lurks there.', 'tt0422272', '/s7gCOegufXiQR48IhuTsPNy0Fav.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6436, 'Summer', False, True, False, False, 2008, 6.8, 'Characters familiar from Kevade are now in their 20s. In the beginning of the 20th century, Joosep Toots has returned from Russia, where he learned agriculture. He wants to start reforming his father''s farm, Ülesoo. At the same time, he''s competing with Kiir to win the love of Teele. Teele has stated that she agrees to marry with a farmer only. So Kiir decides to go to Russia to learn agriculture too.', 'tt0075292', '/h7rg35UkEiXkd02QtkE7zPhU5T4.jpg');
