insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6439, 'Stick Man', False, True, False, False, 2015, 7.1, 'Stick Man lives in the family tree with his Stick Lady Love and their stick children three, and he''s heading on an epic adventure across the seasons. Will he get back to his family in time for Christmas?', 'tt5095510', '/hChFSATMxo9cDpDGl5iit5dx7yy.jpg');
