insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6445, 'A Guy Thing', False, True, False, False, 2003, 5.6, 'Paul Morse is a good guy. When his friends throw him a wild bachelor party, he just wants to keep his conscience clean -- which is why he''s shocked when he wakes up in bed with a beautiful girl named Becky and can''t remember the night before. Desperate to keep his fiancée, Karen, from finding out what may or may not be the truth, he tells her a teensy lie. Soon his lies are spiraling out of control and his life is a series of comical misunderstandings.', 'tt0295289', '/rpLNAkJqAxzkIChwOJyBLr2MJsB.jpg');
