insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6448, 'Down to Earth', False, True, False, False, 2001, 5.5, 'After dying before his time, an aspiring comic gets a second shot at life... by being reincarnated as a wealthy but un-likable businessman.', 'tt0231775', '/w2XM4YCfBOOWEL4a0abQji7QxwI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6449, 'The Heavenly Kid', False, True, False, False, 1985, 6.4, 'When Bobby dies in a car accident he is not allowed to enter heaven but has to stay in one of the lower levels until he has worked enough as an guardian angel in order to deserve paradise. One of his jobs is Lenny, a gifted boy, who loves his schoolmate Sharon who is the most beautiful girl of the class. But she does not even notice him so that he is about to commit suicide. Bobby prevents this and dresses Lenny up making him a playboy and tough guy so that he gets what he wants although Bobby knows that this is not the best. Thinking that his job is done, Bobby realizes that Lenny''s mother is his former girlfriend who has married another guy. Although not being allowed he makes himself visible to her...', 'tt0089265', '/yn4ri4OJI29M6wvn2ziXhHd8EuX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6450, 'The Fitzgerald Family Christmas', False, True, False, False, 2012, 6.2, 'The siblings of the Fitzgerald family must decide if the dad who abandoned them 20 years ago can come home for Christmas. Can the big Irish clan get past their grievances to be a family again?', 'tt2331880', '/dNvgepbxYluMY3szgG6n8msXmEe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6452, '7 Chinese Brothers', False, True, False, False, 2015, 5.6, 'Larry is an unqualified, unemployable, inebriated prankster who rides a tide of booze onto the glorious shores of an undiscriminating Quick-Lube.  Taking a part-time job vacuuming and washing windshields, Larry finds himself mixed up with hostile co-workers and unsatisfied customers, while also finding himself smitten with his lovely boss, Lupe Torrez.  Will Larry keep it together long enough to win the girl, provide for man''s best friend (his dog Arrow), and do his grandmother proud?', 'tt3488184', '/k1vtozOhbk76ZmBJzKfmv1pev9i.jpg');
