insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6455, 'The Demons', False, True, False, False, 2015, 6.7, 'A young boy begins to experience the adult world as he enters adolescence.', 'tt4247492', '/mXnfi1f7XD15OLaSbP2HNRnoPsI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6457, 'Only Old Men Are Going to Battle', False, True, False, False, 1973, 8.4, 'Bogdan Titarenko is very talented pilot and brave leader of "Singing group". He also must look after some new cadets and fight together with them against German Luftwaffe planes. Close to Bogdan always are his friends, they are all from different parts of the country, but they all became real brothers.', 'tt0070861', '/4TE8gM826e1OU62AErJ5LS4F1os.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6458, 'F/X2', False, True, False, False, 1991, 5.9, 'F/X man Rollie Tyler is now a toymaker. Mike, the ex-husband of his girlfriend Kim, is a cop. He asks Rollie to help catch a killer. The operation goes well until some unknown man kills both the killer and Mike. Mike''s boss, Silak says it was the killer who killed Mike but Rollie knows it wasn''t. Obviously, Silak is involved with Mike''s death, so he calls on Leo McCarthy, the cop from the last movie, who is now a P.I., for help and they discover it''s not just Silak they have to worry about.', 'tt0101846', '/v0xCAl44FEJQn7E1FvmY0pUTB14.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6461, 'Torn', False, True, False, False, 2013, 5.9, 'Two families bond when their teenage sons are killed in an explosion at a suburban mall only to discover one of their children is the prime suspect.', 'tt1423636', '/lPUOxfwSdX9DYwcnMGvrvEOxrJS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6464, 'The Calling', False, True, False, False, 2014, 5.8, 'Detective Hazel Micallef hasn''t had much to worry about in the sleepy town of Port Dundas until a string of gruesome murders in the surrounding countryside brings her face to face with a serial killer driven by a higher calling.', 'tt1666335', '/kqJGIh2mZVwTD20GeDVTUNOwd5p.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6465, 'Dust in the Wind', False, True, False, False, 1986, 7.7, 'A-yuan and A-yun are both from the small mining town of Jio-fen. In the city, A-yuan is an apprentice by day and goes to night school, and A-yun works as a helper at a tailors. Everyone thinks they are meant for each other, so do they. They fail to see time and fate are beyond their control.', 'tt0091406', '/9QbsT3gkG0WdchoDY8kqIwcVN0i.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6467, 'Sebastian Maniscalco: Aren''t You Embarrassed?', False, True, False, False, 2014, 7.8, 'Sebastian Maniscalco examines the shameless behaviors of our modern society - from the small daily annoyances to the publicly obnoxious - when he asks, "Aren''t You Embarrassed?" This new, one-hour comedy special is deeply relatable and highly entertaining as it is inspired by real-life irritations that keep audiences rolling in laughter.', 'tt4240704', '/lTqvwDmkZIC6PQpCBjy0yn2SaPG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6469, 'Doomsdays', False, True, False, False, 2013, 6.7, 'Follows the dark-comic adventures of two men who live an itinerant existence breaking into country houses.', 'tt2395146', '/jhfu19Lk5SACKURaSYLnvhHwbc7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6470, 'The Incredible Hulk Returns', False, True, False, False, 1988, 5.8, 'Dr. David Banner meets a former student, who has a magical hammer that summons Thor, a Norse god who is prevented from entering Valhalla. When the two superheroes stop feuding long enough to breathe, they are a team unmatched by any of their enemies.', 'tt0095368', '/1fdyTaFdivd2rpT51SwmxCFNCTW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6472, 'Therapy for a Vampire', False, True, False, False, 2014, 6.3, 'Horror comedy film following vampire count Geza von Kösznöm who''s visiting groundbreaking neurologist Sigund Freud because he''s bored of his life and frustrated of the "eternally long" relationship with his wife Elsa.', 'tt3400980', '/xNvQGWrww3hB30ttGUn5EOelmAO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6473, 'One Small Hitch', False, True, False, False, 2013, 6.6, 'On a flight home to Chicago for a family wedding, childhood friends Josh and Molly innocently agree to fake a wedding engagement to make Josh''s dying father happy. Things quickly get out of hand with their two boisterous families, and a series of events causes them to pretend to be a couple and start planning a phony wedding. When the playacting begins to foster real feelings, the two must make some serious decisions: Split up and return to their lives in LA, or make a life as a couple back in Chicago?', 'tt1937269', '/pmr4lL6ikvXgk4LAVK94XYLXpvu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6474, 'Bon Voyage, Charlie Brown (and Don''t Come Back!)', False, True, False, False, 1980, 7.3, 'Charlie Brown, Linus, Peppermint Patty and Marcie travel to France as foreign exchange students. Also along is Snoopy and Woodstock. While everyone is excited about the opportunity to travel to a foreign country, Charlie is disturbed by a letter he receives from a mysterious girl from France who invites him as a her guest only to find that he does not seem welcomed to her Chateau.', 'tt0080461', '/eds4cxarUjHn7OdDd26SauphUnR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6477, 'The Wind Journeys', False, True, False, False, 2009, 7.4, 'Ignacio Carrillo (Marciano Martínez) is a vallenato singer from Majagual, Sucre, who decides, after his wife''s sudden death, to stop playing and return his accordion, which is said to be cursed, to his master. He is joined by Fermín Morales (Yull Núñez), a teenage boy who admires Ignacio and wishes to become a juglar like him. Carrillo reluctantly accepts, given his loneliness. On Ash Wednesday 1968, Carrillo, Morales and their donkey start a journey throughout several towns in the Caribbean region in Northern Colombia, until Taroa (a small caserío in Uribia jurisdiction), in La Guajira desert, where Carrillo''s maestro supposedly lives. During their journey, Carrillo participates in the first version of the Vallenato Legend Festival in Valledupar.', 'tt1426374', '/hLMPYRK7Lkm68msH75uUQO08Utt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6479, 'Ladies in Lavender', False, True, False, False, 2004, 7, 'Andrea, a gifted young Polish violinist from Krakow, is bound for America when he is swept overboard by a storm. When the Widdington sisters discover the handsome stranger on the beach below their house, they nurse him back to health. However, the presence of the musically talented young man disrupts the peaceful lives of Ursula and Janet and the community in which they live.', 'tt0377084', '/wm3wG0qf1qhUj0xtgI0BarQpmbT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6480, 'OC87: The Obsessive Compulsive, Major Depression, Bipolar, Asperger''s Movie', False, True, False, False, 2012, 6.4, 'Director Bud Clayman documents his struggle with OCD and Asperger''s Syndrome and how it derailed his plan to become a filmmaker.', 'tt1550557', '/rwIKTBkYEhXDOdDR9LkeVmQAg8h.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6481, 'Stand Clear of the Closing Doors', False, True, False, False, 2014, 6.3, 'When autistic teen Ricky is scolded for skipping class, he escapes into the subway for a days-long odyssey among the subway’s disparate denizens. Meanwhile, his mother wages an escalating search effort above ground. Based on a true story and set in Far Rockaway, Queens, in the days leading up to Hurricane Sandy, these parallel stories of mother and son take the viewer on a touching journey of community and connection in and below New York City.', 'tt2405372', '/oKFywm0BSOBVYkSpW7JmXVM8SDo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6482, 'The Light Thief', False, True, False, False, 2011, 6.4, 'The main hero of the film is an electrician with a far greater effect on the people around him than his job defines. He is the last link in a huge energetic system and he becomes the binding bridge between the geopolitical problems of post-soviet space and the common people. The economic devastation of the country had an enormous impact on the industrial workers and yet despite the upheaval, these people did not seize to love and suffer, to have and be friends and to enjoy their lives. In particular our resilient electrician, who possesses a wonderful and open heart. He not only brings electric light (which is often out) to the lives of the inhabitants of this small city, but he also spreads the light of love, loyalty, life and mainly laughter.', 'tt1646979', '/djmlqJwXG9EiuzfPiXd4A2ylb6x.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6483, 'Veronika Decides to Die', False, True, False, False, 2009, 6.4, 'After a frantic suicide attempt, Veronika awakens inside a mysterious mental asylum. Under the supervision of an unorthodox psychiatrist who specializes in controversial treatment, Veronika learns that she has only weeks to live.', 'tt1068678', '/oUo9CNSKz4ELnxAh4l3BPcXcxKq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6484, 'Hell Drivers', False, True, False, False, 1957, 7.2, 'An ex-con trying to go clean ends up working for a crooked trucking company swindling money.', 'tt0051713', '/iVq23A9DDzU2sLHBkKbHooolnH5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6485, 'Leviathan', False, True, False, False, 1989, 5.8, 'In a Russian coastal town, Kolya is forced to fight the corrupt mayor when he is told that his house will be demolished. He recruits a lawyer friend to help, but the man''s arrival brings further misfortune for Kolya and his family.', 'tt2802154', '/dwmHSBBiu2ow271zrQ33rPKZDSC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6486, 'Doug Stanhope: No Refunds', False, True, False, False, 2007, 8.2, 'Not all stand-up comedy is spot-cleaned and pre-packaged for the masses. Like the legends who were born out of smokey, booze-soaked nightclubs of decades past, Doug Stanhope spews his own brand of moral outrage in an unmatched style that borders on self-destruction. Nothing is sacred, no subject off-limits and most importantly nothing is contrived. From critically acclaimed appearances at the Ed', 'tt1047610', '/leh2zShssPQ3M8wlIt1SPaFIFkW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6487, 'The Lottery', False, True, False, False, 2010, 7, 'On his deathbed in the hospital, Jason''s father asks that his ashes be spread over the top of his wife''s grave. Jason, who came to the city with his father at a young age, remembers little of the town or his mother, but he undertakes the charge. It is an idyllic place, but gradually snips of memory return, until he remembers the significance of the fact that most of the tombstones in the town cemetery, including his mothers, bear the same day and month, one death each year. Caught up in the memory of the lottery he saw as a child, and the one he is trapped in today, escape is difficult and retribution is... Written by Bruce Cameron', 'tt0116924', '/5pkQ4miPHbmgFQpD6nXtkrjuWbF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6488, 'Heidi Fleiss: Hollywood Madam', False, True, False, False, 1995, 6.5, 'A documentary crew from the BBC arrives in L.A. intent on interviewing Heidi Fleiss, a year after her arrest for running a brothel but before her trial. Several months elapse before the interview, so the crew searches for anyone who''ll talk about the young woman. Two people have a lot to say to the camera: a retired madam named Alex for whom Fleiss once worked and Fleiss''s one-time boyfriend, Ivan Nagy, who introduced her to Alex. Alex and Nagy don''t like each other, so the crew shuttles between them with "she said" and "he said." When they finally interview Fleiss, they spend their time reciting what Alex and Nagy have had to say and asking her reaction.', 'tt0113283', '/laPSb9I8TuYslOh1OcgaTByWKnw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6489, 'Abraham Lincoln', False, True, False, False, 1930, 5.7, 'Silent film master D.W. Griffith''s first talkie works as a companion piece to his classic BIRTH OF A NATION, providing a detailed biographical sketch of the 16th president. We see his birth in a log cabin, the tragic death of his first love, Ann Rutledge (Una Merkel), his debates with Douglas, his accepting of the presidency, the terrible toll of the Civil War, and finally the tragic assassination at Ford''s Theater. Griffith shows his usual meticulous attention to period detail, and the framing of the various vignettes has the feel of historical photographs come to life. Walter Huston is excellent in the title role, with a portrayal that subtly evolves from laconic, wizened rascal to noble elder statesman. This is a fascinating, worthy film, and an interesting historical document in and of itself.', 'tt0020620', '/jat652dVfzIBzFbtQdIeDp6ihlo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6490, 'There''s Something Wrong with Aunt Diane', False, True, False, False, 2011, 6.7, 'HBO: Documentary Revisits the mysterious tragedy of Diane Schuler, who, on July 26, 2009, drove the wrong way on the Taconic Parkway in New York for nearly two miles and then smashed into an oncoming SUV, killing herself, her daughter, her three nieces and all three people in the other car. Premieres Monday, July 25 at 9pm.', 'tt2011325', '/kC7Nz2GnC2zUK3c5fVvCKTOjExa.jpg');
