insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6494, 'In Dubious Battle', False, True, False, False, 2017, 6.1, 'In the California apple country, nine hundred migratory workers rise up "in dubious battle" against the landowners. The group takes on a life of its own-stronger than its individual members and more frightening. Led by the doomed Jim Nolan, the strike is founded on his tragic idealism-on the "courage never to submit or yield."', 'tt4411618', '/bC7N0pUc6RzuDqSNHsV0VXk06Cq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6495, 'George Carlin: Playin'' with Your Head', False, True, False, False, 1986, 8.1, 'George Carlin is in top form with these stand-up recorded at the Beverly Theater in Los Angeles in 1986. Routines included are "Losing Things," "Charities," "Sports," "Hello and Goodbye," "Battered Plants," "Earrings," and "A Moment of Silence." Also included is a short film entitled "The Envelope" co-starring Vic Tayback.', 'tt0199554', '/oz2MdKJCjTVYdUwUC59J4b0LXJ3.jpg');
