insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6509, 'Double Take', False, True, False, False, 2009, 6.2, 'Director Johan Grimonprez casts Alfred Hitchcock as a paranoid history professor, unwittingly caught up in a double take on the cold war period. Subverting a meticulous array of TV footage and using ''The Birds'' as an essential metaphor, DOUBLE TAKE traces catastrophe culture''s relentless assault on the home, from moving images'' inception to the present day.', 'tt1386926', '/au2rn9Syy5iJEfsFakxv1GOwfCj.jpg');
