insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6543, 'C.H.U.D.', False, True, False, False, 1984, 5.6, 'A rash of bizarre murders in New York City seems to point to a group of grotesquely deformed vagrants living in the sewers. A courageous policeman, a photo journalist and his girlfriend, and a nutty bum, who seems to know a lot about the creatures, band together to try and determine what the creatures are and how to stop them.', 'tt0087015', '/8BVw5RIDBTZwdMAR1VKpAkwrMjj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6544, 'The Killer Is Loose', False, True, False, False, 1956, 6.6, 'A savings-and-loan bank is robbed; later, a police wiretap identifies teller Leon Poole as inside man. In capturing him, detective Sam Wagner accidentally kills Poole''s young wife, and at his trial Poole swears vengeance against Wagner. Poole begins his plans to get revenge when he escapes his captors.', 'tt0049405', '/62RIcia5eRlI3Q5GbLbBRar7b8a.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6545, 'Detective Chinatown', False, True, False, False, 2015, 6.6, 'After being rejected from the police college, a mannerly man travels to Bangkok where he and an energetic distant relative must solve a murder case.', 'tt5290882', '/sYmdUFpcpRqlmeETqhcMrYJbjbN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6546, 'Phantasm: Ravager', False, True, False, False, 2016, 5, 'Brothers Mike and Jody join family friend Reggie to battle the Tall Man and his evil minions from another dimension, for the final time.', 'tt3627704', '/zfZPcIYyVX2LW799SWxLFcTWQxa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6548, 'The Vessel', False, True, False, False, 2016, 5.3, 'Ten years after a tsunami destroyed a small-town elementary school with all the children inside, a young man builds a mysterious structure out of the school''s remains, setting the town aflame with passions long forgotten.', 'tt4083052', '/oRZ0QAKhuAGtBJA1UMM7hzFEtD5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6550, 'The World Made Straight', False, True, False, False, 2015, 5.5, 'In a rural Appalachian community haunted by the legacy of a Civil War massacre, a rebellious young man struggles to escape the violence that would bind him to the past.', 'tt2420166', '/AwDOpMAHK69nUe6IYyHtmVjW9GR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6551, 'Orphans', False, True, False, False, 1998, 7, 'The star of Ken Loach''s MY NAME IS JOE, Mullan proves that his talent isn''t relegated to acting. As a writer/director, he has crafted a supremely entertaining motion picture. ORPHANS tells the grittily realistic, hysterical, and deeply moving tale of a group of siblings who reunite in Glasgow on the eve of their mother''s funeral. The four children mourn their mother''s passing in a variety of ways, some of which are heartfelt and some of which are bizarre. As a potential thunderstorm threatens to damage the city, the situation compounds itself even further.', 'tt0119842', '/xJqQmrCktJVoCoNU8HSMcTBUbdT.jpg');
