insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6666, 'The Last Survivors', False, True, False, False, 2014, 5.3, 'In the near future, society collapses and water becomes scarce. When a greedy water baron starts violently clearing out survivors, Kendal, a 17-year-old teenager, fights the baron''s henchman to keep a well open.', 'tt2558318', '/rHSrw8yPuuWbtVhTiCTHllVxMIA.jpg');
