insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6669, 'Neurons to Nirvana', False, True, False, False, 2013, 7.7, 'Through interviews with leading psychologists and scientists, Neurons to Nirvana explores the history of four powerful psychedelic substances (LSD, Psilocybin, MDMA and Ayahuasca) and their previously established medicinal potential. Strictly focusing on the science and medicinal properties of these drugs, Neurons to Nirvana looks into why our society has created such a social and political bias against even allowing research to continue the exploration of any possible positive effects they can present in treating some of today''s most challenging afflictions.', 'tt2195566', '/vyGDvVoG6lS33sCTzK1WTmbTqQz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6670, 'Spanish Lake', False, True, False, False, 2014, 6.5, 'A bold and unflinching documentary on ''white flight'' in the area of Spanish Lake, Missouri, a post WW2 suburb. The town experiences rapid economic decline and population turnover due to racism and governmental policies which support the white exodus. The themes of the film parallel America''s growing political divide, racial tension, and rise of anti-government sentiment.', 'tt1977915', '/yy7twFC8VsyBo3LFYv9LFK8Kcyn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6671, 'Ok Jaanu', False, True, False, False, 2017, 5.2, 'OK Jaanu is an upcoming Indian romantic drama film directed by Shaad Ali and produced by Karan Johar under his banner Dharma Productions. It is an official remake of Mani Ratnam''s Tamil film OK Kanmani.', 'tt5764024', '/gVhIc36GNHMlFl3egvmex82RwJl.jpg');
