insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6697, 'Patch Town', False, True, False, False, 2014, 5.6, 'Patch Town, inspired by the award-winning short film of the same name.  After years in a loving home, Jon, a toy, was forgotten, deserted and ultimately betrayed by his adoptive mother. He returns to live a sad life as a worker on the line; a life of factory work and oppression in a place where hundreds of cabbage babies are born every day. The thankless task of shucking, picking, and processing these newborns to go out into the world and to their new mothers has taken its toll on Jon. With each new birth, Jon slips deeper into sadness, lamenting the days when life was good and he was loved.', 'tt2189418', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6698, 'Griffin & Phoenix', False, True, False, False, 2006, 7, 'A terminally ill man falls in love with a woman who has a secret that threatens their time spent together.', 'tt0460812', '/kL1Jh2ahQH62SLDETwD2TpDhljP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6699, 'Gigantic', False, True, False, False, 2008, 6, 'Young mattress salesman Brian decides to adopt a baby from China but is distracted when he forms a relationship with quirky, wealthy Harriet whom he meets at his mattress store. As their relationship flourishes, unbeknownst to them, a hitman is trying to kill Brian.', 'tt1176251', '/2TVP6bbVp9ouZVmnstUbpq3blgH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6700, 'The Final Season', False, True, False, False, 2007, 6.6, 'True story of Kent Stock, who in the early 90''s, gives up a job and ditches his wedding plans to take over as head coach as the Norway High School baseball team. Kent must win over his players and convince them and himself that he can fill their former coach''s shoes and that they can go out winners. In the summer of 1991 Norway High''s baseball tradition ended on a triumphant but sombre note.', 'tt0449018', '/ybJXbRlepM7D7bkHvwv8N4U51YZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6701, 'Free the Mind', False, True, False, False, 2013, 6.3, 'Brain scientist Professor Richard Davidson sets up his mind to conduct an unusual experiment: He will teach American war veterans and children meditation and yoga. Can veterans through meditation and yoga ease their pain and nervous system, find happiness and be more peaceful and get back to a life more like the one they had before the war?', 'tt1673376', '/wCGEkS046Y10TVlm27iXqRmf5yo.jpg');
