insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6708, 'Unfinished Business', False, True, False, False, 2015, 5.4, 'A hard-working small business owner and his two associates travel to Europe to close the most important deal of their lives.  But what began as a routine business trip goes off the rails in every imaginable – and unimaginable – way, including unplanned stops at a massive sex fetish event and a global economic summit.', 'tt2358925', '/eVWVZf3fkx9eNqbHyn9xNlmC2h4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6709, 'Ticked-Off Trannies with Knives', False, True, False, False, 2010, 5.5, 'A group of trannies are violently bashed and left for dead. The surviving ladies regain consciousness, confidence, and courage ready to seek out revenge on the ones who attacked them.', 'tt1587431', '/ihxrPkrsXYkwbstEgHC88V9Mpb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6711, 'The Seduction of Mimi', False, True, False, False, 1972, 7.4, 'Mimi is a Sicilian dockworker who loses his job when he votes against the Mafia candidate in what he thinks is a secret ballot. He leaves his wife behind and goes to Turin, where he meets and moves in with Fiore, a street vendor and Communist organizer. They have a child, he works non-union jobs, and again he comes to the Mafia''s attention. This time they''re impressed, promoting him to a supervisor''s job back in Sicily. He must keep Fiore and the child a secret, which is fine with Fiore, as long as he never makes love to his wife. He doesn''t, and when she becomes pregnant, he knows he''s a cuckold. His personal revenge and the Mafia''s tentacles then intertwine in tragicomic ways.', 'tt0068950', '/gOaLlPEZ18E2Qa0beTwfwMIp2MZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6713, 'Keeping Rosy', False, True, False, False, 2014, 6.4, 'A woman becomes enraged after being passed over for a promotion at her job.', 'tt2614700', '/caT4Cg1yeZZ9RgGV8RzmmQYgwNG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6714, 'Time Traveller: The Girl Who Leapt Through Time', False, True, False, False, 2010, 6.5, 'Akari Yoshiyama is graduating high school soon and is expected to lead new life after she passes the university entrance exam, while her mother is working as a pharmacologist. However, her mother has a car accident and the situation is totally changed. She decides to go to 1972 to fullfil her mother''s wish.', 'tt1614408', '/hios2NxpDTkSFvdZV7CkgNkeNUC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6718, 'Crazy Mama', False, True, False, False, 1975, 5.5, 'Jonathan Demme directs this joyous unrelentlessly kitchy celebration of 50''s America.', 'tt0072829', '/868gQ8gYhBggAy3meIx57fUfsDY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6719, 'Pitfall', False, True, False, False, 1948, 7.2, 'A man wanders into a seemingly deserted town with his young son in search of work. But after a bit of bad luck, he joins the town''s population of lost souls.', 'tt0203612', '/7VJ9RvCehgLlGI0NXmS9RUZMTND.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6720, 'A Simple Curve', False, True, False, False, 2005, 6.6, 'Caleb is 27, and was raised in the majestic Kootenays by his bush hippie parents. He loves his father Jim and understands his reverence for wood working, coriander and Soya products, but he just doesn''t get Jim''s determined effort to achieve economic disaster at every turn. His father is a relentlessly unsuccessful business man, and as the junior partner in their carpentry shop, Caleb is powerless to stop the fiscal self sabotage. When Matthew, an old friend of Jim''s, arrives in the valley to develop a high-end fishing lodge, Caleb sees fortune looming, provided he can keep his father distracted long enough. But a small deception leads to colossal betrayal, and soon Caleb must face the fact that he''s reached that treasured day when a boy becomes man enough to tell his father get lost.', 'tt0422458', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6721, 'The Coca-Cola Kid', False, True, False, False, 1985, 6, 'An eccentric marketing guru visits a Coca-Cola subsidiary in Australia to try and increase market penetration. He finds zero penetration in a valley owned by an old man who makes his own soft drinks, and visits the valley to see why. After "the Kid''s" persistence is tested he''s given a tour of the man''s plant, and they begin talking of a joint venture. Things get more complicated when the Coca-Cola man begins falling in love with his temporary secretary, who seems to have connections to the valley.', 'tt0088931', '/r3Y3lhFa1tiMuG78WoBsbto9jem.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6722, 'Vazhakku Enn 18/9', False, True, False, False, 2012, 8.3, 'Vazhakku Enn 18/9 is a 2012 Tamil Crime Thriller film written and directed by Balaji Sakthivel, starring newcomers Mithun, Sri, Urmila and Manisha in the lead roles.Pre-production of the film began in 2008, with the filming commencing in 2010 only,following several delays. Cinematography is handled by Vijay Milton, while noted guitarist R. Prasanna has scored the music. The movie released on 4th May 2012 with very high critical acclaim.The film has highest rating in all medias', 'tt2146960', '/mjH2T7BeBz4wgEIS0ymGl8Mc0tw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6723, 'Ajab Prem Ki Ghazab Kahani', False, True, False, False, 2009, 6.3, 'Prem is an ajab kind of a guy. As life president of happy club, he''s always trying to make everybody happy, but somehow it always ends in a disaster! He kidnaps Jenny by mistake!! He think she''s beautiful. Love can make you do the strangest things and if Prem is ajab, his love is ghazab too! He falls for her and turns over a new leave. Now he is working harder than ever to keep her happy!!', 'tt1252596', '/qEJZqbmhQ6AoNI15GaYBZSACY8l.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6725, 'All About the Money', False, True, False, False, 2017, 5.8, 'Two "down on their luck" buddies are convinced by a third to take a vacation. Only after landing in the third world country do they realize that they are there to attempt to capture the United States'' most wanted criminal - with no military training.', 'tt2312184', '/s0xib9gZqbMt6wy9GO66d6TUNtR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6727, 'Slice', False, True, False, False, 2018, 4.5, 'A serial killer is preying on the rich and influential, exposing their sexual proclivities, dismembering them and dumping them and their severed body parts in various locations around Thailand. The police officer on the case is not even close to finding the killer, but given an ultimatum to solve it in 15 days, Papa Chin (Chatchai Plenpanich) turns to the one man he doesn’t want to use — an imprisoned hitman named Tai (Arak Amornsupasiri), who thinks the killer is a friend of his from childhood. Tai let out of prison while his girlfriend Noi is kept under close watch by Chin. Tai heads back to his hometown to search for clues about where his old friend might be. As he recalls his childhood and friendship with the troubled boy Nut, the events of the present day crystallize until Tai is confronted with a shocking reality.', 'tt1603807', '/4sicKNOWFO2edxtzuQyO4npcoMJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6730, 'Nora', False, True, False, False, 2000, 6, 'In 1904, in Dublin, James Joyce chats up Nora Barnacle, a hotel maid recently come from Galway. She enchants him with her frank, direct and uninhibited manner, and before long, he''s convinced her to come with him to Trieste, where he has a job with Berlitz. Over time, Nora pulls him through phobias, tolerates his drinking, takes in his brother Stan, and bests Joyce at ''the writin'' game'' to bring him back to Italy from Dublin where he''s gone to open a cinema. But his sexual jealousy threatens the relationship and sends her back to Galway with the children. Is there any way to tame Jim''s green-eyed monster? And, will the lad ever get his stories published?', 'tt0158033', '/3G1dPq5hf9g7m98uKrksBIu1kbL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6732, 'Kaagaz Ke Phool', False, True, False, False, 1959, 8, 'The film tells, in flashback, the story of Suresh Sinha (Guru Dutt), a famous film director and his relationship with an aspiring actress.', 'tt0052954', '/coAns7HJD19FAP5umbotgNWFLly.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6733, 'For Grace', False, True, False, False, 2015, 7.3, 'Curtis Duffy, a teen who fought and stole for the thrill, discovered his place in the kitchen after a home economics teacher nurtured his talents. After an unimaginable tragedy involving his parents, Duffy doubled down on his cooking career. Soon, his intense drive earned him accolades as one of the country''s most renowned chefs. But as he began building his dream restaurant in Chicago, called Grace, Duffy found himself in another point of personal crisis: His laser focus cost him his marriage and two young daughters.  For Grace is a documentary about food, family, sacrifice, and the journey from concrete box to opening night of one of the world''s most acclaimed restaurants.', 'tt4125856', '/sqL7WHWshbAhn8prfY2Lky419QT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6734, 'Play the Game', False, True, False, False, 2009, 6.4, 'When ladies'' man David Mitchell (Paul Campbell) gives his lonely grandfather, Joe (Andy Griffith), some pointers on dating, Joe becomes a big hit with the women in his retirement community. But David strikes out with his own tricks when he tries to woo a girl named Julie (Marla Sokoloff). Now it''s up to Joe to teach his grandson how to win at love without playing games. Doris Roberts and Liz Sheridan co-star in this award-winning comedy.', 'tt1124388', '/7TdgZz8RWOC8xxLRvxHnDypWNS9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6735, 'Combat Girls', False, True, False, False, 2011, 6.8, 'Marisa, a 20-year-old German girl, hates foreigners, Jews, cops, and everyone she finds guilty for the decline of her country. She provokes, drinks, fights and her next tattoo will be a portrait of Adolf Hitler. The only place she feels home is the Neo-Nazi gang she belongs to, where hate, violence, and heavy parties are the daily rules. When 14-year-old Svenja joins the group, Marisa appears like a role model to her: she fits the purest idea of a combat girl fighting for the group''s ideology. But Marisa''s convictions will slowly evolve when she accidentally meets a young Afghan refugee. Confronted to him, she will learn that the black and white principles of her gang are not the only way. Will Marisa ever be able to get out of this group?', 'tt1890373', '/uxqxBVwkiyo21XmGNCVY8soTWZz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6736, 'God of Love', False, True, False, False, 2010, 7.6, 'A lovestruck, lounge-singing darts champion finds his prayers are answered -- literally -- when he mysteriously receives a box of love-inducing darts.', 'tt1631323', '/5niikw9JFCYihWcV7D9vb9yejg1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6738, 'Dakota Skye', False, True, False, False, 2008, 6.6, 'For as long as she could remember, Dakota Skye has been cursed with a super power. She has the ability to see the truth in any lie she hears. From small, harmless white lies, to the more devious kind, they have come from the people that she should trust the most; her family, friends and teachers. These lies have snowballed, leading to her becoming bitter and apathetic towards the world around her.', 'tt0907848', '/cjfQTFxVwy842dSLidCUHWRCp8X.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6739, 'Bruno', False, True, False, False, 2000, 6.1, 'The story of a unique young boy genius, Bruno, whose expression of his own individuality leads his family and community along an emotional journey.', 'tt0123003', '/xs2XKvQkKFB0zm4eLVLcMMRexbJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6740, 'The Sheik', False, True, False, False, 2014, 7.1, 'When Khosrow Vaziri became the World Wrestling Federations Iron Sheik and camel-clutched his way to fame in the 1980s, he achieved the American Dream by personifying a foreign villain. Losing his world championship belt to Hulk Hogan became a defining moment in professional wrestling. These days, the Sheiks smackdowns are on Twitter, where hes gained a new following.  Once an Olympic hopeful, bodyguard to Irans Shah and pop culture icon, we witness Vaziri struggling with addiction and despair as a family man. But with the help of Torontos Magen brothers, the Sheik begins a road to redemption and renewed status as a public figure.  Showcasing his powerful past and at times painful present, this is an insightful look at one of wrestlings biggest stars, but also a powerful story of personal sacrifice that, in the Sheiks own words, will make you humble.', 'tt3607812', '/yBqXqf8K12nwm8MrntQpbJMW90v.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6741, 'Genius on Hold', False, True, False, False, 2013, 7, 'The story of Walter L. Shaw, a telecommunications genius, and his son Walter T. Shaw, a ruthless jewel thief, is a metaphor and a symbol for its time - for the twentieth century and for today.', 'tt1806910', '/yOqNFb0ila68mDwbiToZMP7B2Ms.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6742, 'Hey Hey It''s Esther Blueburger', False, True, False, False, 2008, 6.3, 'This film is a smart, rueful and dead-on portrait of life''s unending quest to fit in; and the girl who solves it by completely breaking out - introduces a feisty outsider hero unlike any other seen on screen. Esther Blueburger''s quest begins when she escapes from her Bat Mitzvah party and is befriended by Sunni.., the effortlessly cool girl who is everything Esther thinks she wants to be. With the help of Sunni, Esther goes away from her ordinary life and leaves behind her malfunctioning Jewish family to hang out with Sunni''s far breezier and super-hip single mom Mary and attend Sunni''s forbidden public school as a Swedish exchange student.', 'tt0469099', '/dluxHdIEQZKpJOO1vFNFRHJQoUE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6743, 'Vice Squad', False, True, False, False, 1953, 6.6, 'A Los Angeles police captain (Edward G. Robinson) ties the case of a slain policeman to a bank robbery, all in a day.', 'tt0046514', '/xqLrdFzllruOHoov7CKtsoV6jJu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6744, 'Blueberry', False, True, False, False, 2004, 5.2, 'U.S Marshal Mike Donovan has dark memories of the death of his first love. He keeps peace between the Americans and the natives who had temporarily adopted and taken care of him. The evil actions of a white sorcerer lead him to confront the villain in the Sacred Mountains, and, through shamanic rituals conquer his fears and uncover a suppressed memory he would much rather deny.', 'tt0276830', '/bnYXbU6RJnsyJZx84KNsZjrSZQK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6746, 'Fine Dead Girls', False, True, False, False, 2002, 7.2, 'Iva and Maria, a young lesbian pair, rent a favorable, nice dwelling in a less good area of Zagreb, Croatia. But unfortunately, included in the rent are the neighbors, the neighbors from hell. The curious landlady, foreigners flogging nationalists, an old man, who live with his dead wife and a man, who beat his wife regularly, are just one part of this neighbourhood. When one of the neighbors falls in love with Iva, the situation escalates - with the result of three dead people at the end.', 'tt0329083', '/5897WssvDwtpu3pEolua4K2DCC3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6747, 'Illuminata', False, True, False, False, 1998, 6.1, 'It''s the start of the 20th century, and Tuccio, resident playwright of a theatre repertory company offers the owners of the company his new play, "Illuminata". They reject it, saying it''s not finished, and intrigue starts that involves influential critic Bevalaqua, theatre star Celimene, young lead actors and other theatre residents', 'tt0120709', '/44TZQIej4xBrv6Vf7N5nWN0q3gy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6748, 'The Secret Diaries of Miss Anne Lister', False, True, False, False, 2010, 7, 'In nineteenth century Yorkshire wealthy orphan Anne Lister lives with an aunt and uncle, anxious for her to marry well and blissfully unaware that she is a lesbian,recording her thoughts and exploits in a coded diary. When her lover Mariana Belcombe makes a marriage of convenience to rich old Charles Lawton,she feels betrayed and,although Mariana visits and has sex with her,the relationship is going nowhere. Helped by old flame Tib she makes a play for innocent Miss Browne but sees she is barking up the wrong tree and diverts herself by renovating the family hall. A drunken Tib almost exposes her secret and scornful mine-owner Christopher Rawson,whose marriage proposal she rejects,tells her that her sexuality is a subject of local gossip. Undeterred Anne meets Ann Walker who becomes her new ''wife'' and they open a coal-mine ,living happily together. An end title tells us that Anne Lister died prematurely in 1840 on holiday in Russia.', 'tt1555126', '/fpJHzNNRkLFdY41JwOdZYuAmZ23.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6749, 'Freeway II: Confessions of a Trickbaby', False, True, False, False, 1999, 5.7, 'After escaping from jail, two teens flee to Tijuana and stay with one''s childhood protector, a man masquerading as a nun.', 'tt0175536', '/pIlrXEyuu26o7wr8XoaJH7qK8hS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6750, 'Road to Nowhere', False, True, False, False, 2010, 5.4, 'A passionate filmmaker creating a film based upon a true crime casts an unknown mysterious young woman bearing a disturbing resemblance to the femme fatale in the story. Unsuspectingly, he finds himself drawn into a complex web of haunting intrigue: he becomes obsessed with the woman, the crime, her possibly notorious past, and the disturbing complexity between art and truth. From the Smoky Mountains of North Carolina to Verona, Rome, and London, new truths are revealed and clues to other crimes and passions, darker and even more complex, are uncovered.', 'tt1504319', '/zdBGZSAB6PQPUi8yLtFuitEY7ur.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6751, 'Edie & Thea: A Very Long Engagement', False, True, False, False, 2009, 7.9, 'After 42 years, feisty and delightful lesbian couple Edie and Thea are finally getting married. From the early 1960s to the present day, the tireless community activists persevere through many battles, both personal and political. As Edie says, "we just went on with this talent we have for wrestling joy from the shit". Susan Muska and Greta Olafsdottir return with a love story of two remarkable women whose commitment to each other is an inspiration to us all.', 'tt1512779', '/7HSMvRHsQyYpQcpUmFn9bqs8e0k.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6752, 'Robot Overlords', False, True, False, False, 2015, 4.6, 'Earth has been conquered by robots from another galaxy and the human survivors must stay in their homes, or risk incineration.', 'tt2145829', '/7NSZS0IrzXQ4peB0usxWYJvDP9m.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6753, 'Tower Block', False, True, False, False, 2012, 5.9, 'A year after witnessing a murder, residents of Tower Block 31 find themselves being picked off by a sniper, pitting those lucky enough to be alive into a battle for survival.', 'tt1992258', '/aXnHnjeMlkxDz6CUpoRcL7Sr2nI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6754, 'Bait', False, True, False, False, 2000, 5.8, 'A freak tsunami traps shoppers at a coastal Australian supermarket inside the building ... along with a 12-foot great white shark.', 'tt1438173', '/e2OIr4y90ZZjIRWQ0XBOVzpBn4x.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6755, 'My Father And The Man In Black', False, True, False, False, 2013, 6.5, 'The story of promoter Saul Holiff''s turbulent life and his success making Johnny Cash a superstar.', 'tt1853620', '/8ai6PklYZZc9wjIBGHN3K0G7IhQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6756, 'Firaaq', False, True, False, False, 2009, 7.4, 'Firaaq is an Urdu word that means both separation and quest. The film is a work of fiction, based on a thousand stories. The story is set over a 24-hour period, one month after a campaign that took place in Gujarat, India, in 2002. It traces the emotional journey of ordinary people- some who were victims, some perpetrators and some who choose to watch silently.', 'tt1263679', '/8vPzzXrssnKJiPmmFQmNbMkp6Mv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6757, 'The End of Love', False, True, False, False, 2013, 5.9, 'Mark is a struggling actor stuck between the life he once knew and the one waiting for him. When the mother of his two-year-old son suddenly passes away, Mark is forced to confront his shortcomings. Their fates, now intertwined, hang in the balance as Mark grapples with his ability to grow up. When he meets Lydia, a young mother, he is no longer able to live in the comfort of denial.', 'tt1827578', '/ccdh77h4H8yMKSf3zNyJZnJYGtU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6758, 'Space Pirate Captain Harlock: Arcadia of My Youth', False, True, False, False, 1982, 7.2, 'After a drawn-out, viciously-fought war, the earth has been conquered by the alien Illumidus Empire. Harlock, a captain in earth''s fleet, crashes his ship to prevent the Illumidus from using it, and flat-out refuses to join them. With the help of his allies, Tochiro and the space pirate Emereldas, and his lover Miya, he wages a private and bitter war against the Illumidus.', 'tt0084881', '/gbp4R2BSYfD7jWgeuKLp4salvZh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6759, 'Sennentuntschi', False, True, False, False, 2010, 6.5, '1975, Swiss Alps: In a remote mountain village, an immesely beautiful but disturbed and dumb woman shows up. Only the village policeman Reusch takes care of the strange woman and tries to find out whoshe is. There are hints that she came from the Höhenalp Alp, where herdsmen do unthinkable things to get the company of women. Many dark truths are revealed that should better have remained hidden. A tragedy of lust, insanity and murder is brought to a seemlingly pure and perfect world. It is the beginning of a nightmare of religious insanity, hypocrisy, abuse and belief in demons, who destroys a young family and brings a whole village to destruction.', 'tt1296077', '/c6Hz8KNvDqrfljDk8Tyd9viO9uC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6760, 'Zenith', False, True, False, False, 2010, 6.4, 'In a hellish future where human beings have become stupefied by the state of permanent happiness they have been genetically altered to experience, Jack offers relief via drugs that cause his customers the welcome phenomenon of pain. But when Jack receives a mysterious videotape of his dead father, he sets out to unmask the dangerous conspiracy that has created this dystopian world.', 'tt1363468', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6762, 'Girltrash: All Night Long', False, True, False, False, 2014, 6.4, 'The story of five girls and one epic night. The girls will find love, lust, girl-fights, rock and roll, and a whole lot of stoned sorority girls.', 'tt1555440', '/xJTrdoiowuVfij1QyzdeMicf5QP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6764, 'Buddy', False, True, False, False, 2003, 6.8, 'Kristoffer is a billboard hanger, 24 years old and carefree. When his girlfriend Elisabeth dumps him for the boss of her trend bureau, his life falls to pieces. He feels like a loser. By coincidence some of Kristoffer''s video diaries end up with the producer of the popular talk show "Karsten Tonight" on TV2. A few weeks later Kristoffer''s life has become TV entertainment. People love the sequences from his commune at Tøyen: Kristoffer''s half-twisted view of his surroundings, his crazy best friend Geir, not to mention the weird web designer Stig Inge, who hasn''t set foot outside the Tøyen shopping centre for two years. Kristoffer''s future again looks bright, everyone likes him. But revealing your life on national television comes with a price tag. As Kristoffer''s future in the TV business looks brighter and brighter, his friends start suffering. Geir''s big secret is revealed, and Stig Inge''s personal problems are much more serious than Kristoffer first thought...', 'tt0371589', '/wKZAeAWCDikomNOnmzw77Ljr9jt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6765, 'Around the Block', False, True, False, False, 2013, 5.9, 'A young Aboriginal boy is torn between his unexpected love of acting and the disintegration of his family.', 'tt1847541', '/gUfqG6LriN4TrmUaWI9mmXYNzlE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6766, 'Angry Video Game Nerd: The Movie', False, True, False, False, 2014, 5.6, 'Based on the hit web series of the same name, the science fiction/adventure/comedy, Angry Video Game Nerd: The Movie, follows a disgruntled gamer who must overcome his fear of the worst video game of all time in order to save his fans. Hilarity ensues as a simple road trip becomes an extravagant pursuit of the unexpected.', 'tt2123146', '/vXEKVaCbCUd53CYKkI0B7u1LBB6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6767, 'Unraveled', False, True, False, False, 2012, 6.2, 'Just days before Bernard Madoff captured headlines as the largest Ponzi schemer in U.S. history, Marc Dreier, a prominent Manhattan attorney, was arrested for orchestrating a massive fraud scheme that netted hundreds of millions of dollars from hedge funds. Brazen forgeries and impersonations branded the white collar crime spree remarkable. "Unraveled" is set in the purgatory of house arrest -- an Upper East Side penthouse -- where the Court has ordered Dreier confined until his sentencing day. The film weaves Dreier''s struggle to prepare for the possibility of life imprisonment with first-person flashbacks, which reveal his audacious path of destruction. Destroyed by his own hubris, Dreier attempts to grasp his tragic unraveling. With unprecedented access, "Unraveled" exposes a mastermind of criminal deception.', 'tt1595061', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6768, 'Everyday People', False, True, False, False, 2004, 6.5, 'The closing of a local restaurant concerns a number of employees who''ve dedicated their lives to the eatery', 'tt0365020', '/cIqt9Wy2EpyJI6sBlDQ4lZmFlje.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6769, 'Robot Carnival', False, True, False, False, 1987, 7, 'An anthology of various tales told in various styles with robots being the one common element among them.', 'tt0093871', '/2DTREQNErBYtZd1isRqdqNbbXA8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6770, 'Dead Heat', False, True, False, False, 1988, 6.1, 'Boston cop Pally is forced into early retirement, putting a strain on his marriage. Pally''s stepbrother, Ray, attempts to lift his spirits by tipping him off to a sure-bet racehorse. But their attempts to secure the champion equine are thwarted by a local mob boss, who steals the horse as repayment for a gambling debt. With their investment on the line, Pally and Ray become entangled in a web of underworld crime and murder.', 'tt0277744', '/83gfmVCJU9AvMXtmDH23Ow35SLD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6772, 'Walt Before Mickey', False, True, False, False, 2015, 5.9, 'Based on the book "Walt Before Mickey" covers the early years of Walt Disney''s career. The legendary Walt Disney had a tumultuous childhood, yet he was determined to overcome obstacles in his path, before the creation of his first iconic character: Mickey Mouse.', 'tt3154310', '/o1JYnewI5WTDAUAAlZdgnAzDdPs.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6773, 'Bang Bang Baby', False, True, False, False, 2014, 5, 'A small town teenager in the 1960s believes her dreams of becoming a famous singer will come true when her rock star idol gets stranded in town. But a leak in a nearby chemical plant that is believed to be causing mass mutations threatens to turn her dream into a nightmare.', 'tt2655734', '/nd5cZ2b9SfsE3I0du7FQNon1QRB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6774, 'Day of Anger', False, True, False, False, 1967, 7.1, 'Lee Van Cleef stars as Talby, a sadistic gunfighter who rides into town and takes on a young outcast as his apprentice.', 'tt0061709', '/2TYZtE3BXP1LvktbkMb45mbd42G.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6776, 'Final Girl', False, True, False, False, 2015, 4.7, 'Veronica, the new girl in town, is lured into the woods by a group of senior boys looking to make her a victim. But the boys don''t know that Veronica''s been trained to handle herself in surprisingly lethal ways.', 'tt2124787', '/7liOzGi6DNESPv5gFC7g6mpqBSK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6777, 'Unforgettable', False, True, False, False, 1996, 6, 'Julia moves in with her fiancé, David, but his ex-wife and her own haunting past join forces to rock her quiet suburban existence.', 'tt3462710', '/jKyjJu3N0bcF9TzcyGLb0Nn6Uqq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6778, 'Camila', False, True, False, False, 1984, 6.9, 'In Buenos Aires of the 1840s, a young Jesuit and a wealthy socialite fall in love and begin a torrid affair. They escape from the city, and, in disguise, set up house in a village, assuming they are safe and beyond the cares of anyone. However, both the church and Camila''s family are enraged, vowing to hunt down the lovers for a capital crime. Based on a true story.', 'tt0087027', '/ibSmdjvNXWQgZKLPeJNReRLTJrF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6779, 'Wild Bill', False, True, False, False, 1995, 5.9, 'Out on parole after 8 years inside Bill Hayward returns home to find his now 11 and 15 year old sons abandoned by their mother and fending for themselves. Unwilling to play Dad, an uncaring Bill is determined to move on.', 'tt1795702', '/6KKZ8NNVVXfURBeXXw2BrZhHjb4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6780, 'Falling Overnight', False, True, False, False, 2011, 6.7, 'Falling Overnight tells the story of twenty-two year old Elliot Carson on the day before he has risky surgery to remove a brain tumor. Facing what could be his last night, Elliot’s path intersects with Chloe Webb, a young photographer who invites him to her art show. Elliot welcomes the distraction and as the night descends, Chloe takes him on an intimate and exhilarating journey through the city. But as morning approaches, and Chloe learns of Elliot’s condition, the magic of the evening unravels, and they must together face the uncertainty of Elliot’s future.', 'tt1673736', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6782, 'Desert Blue', False, True, False, False, 1999, 6.2, 'An academic obsessed with "roadside attractions" and his tv-star daughter finally discover the world''s largest ice cream cone, the centerpiece for an old gold-rush town struggling to stay on the map. They end up staying longer than expected because of an accident that spilled an unknown cola ingredient all over the highway. They spend the next few days with the various residents of the town which include a teenage girl who loves to blow things up and a boy trying to keep alive his fathers dream of building a beachside resort in the middle of the desert.', 'tt0126261', '/26J9IvtLB2N1HLLcupHabGtbvmr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6783, 'Guncrazy', False, True, False, False, 1992, 5.5, 'California teen Anita Minteer struggles in the face of an absentee mother, her mom''s abusive boyfriend, Rooney, and a lack of respect from her classmates. This all changes when a pen-pal school project connects her with convict Howard. Anita secures Howard''s parole and violently squares off against Rooney after he rapes her. Soon enough, the gun-crazy teen is on the run with Howard, with his parole officer in pursuit.', 'tt0104377', '/tYY4Dfg3bJlu5woIz2i7LNMD1bX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6784, 'The Twilight of the Golds', False, True, False, False, 1996, 6.5, 'When Suzanne Stein has a genetic analysis done on her unborn child, she discovers that although she has a healthy baby, the child will most likely be born gay, like her brother, David. She must decide whether to keep the child, or to have an abortion. Her family enters a crisis about love and acceptance as she makes this difficult choice. Written by Phil Fernando', 'tt0120392', '/5p0c5yZxFzWaClp7wrv38vtAm8Q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6786, 'Pressure', False, True, False, False, 2015, 5.6, 'Four divers are stuck deep underwater in a vessel after a freak storm destroys their ship. Will they survive?', 'tt3029476', '/fNEai0Rp50kIaq8pqqkgKxM2j5p.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6787, 'The Girl', False, True, False, False, 2012, 6.2, 'Story of a ten-year-old girl left alone in her house in the summer of 1981.', 'tt1342378', '/9ByfjzE1XVnyR3LHaonhTsJgm3w.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6788, 'Entertaining Angels - The Dorothy Day Story', False, True, False, False, 1996, 6.1, '"Entertaining Angels" is an interesting title for this movie about the 1920''s and 30''s social activist, Dorothy Day, for it can be regarded that what this woman did, sometimes single-handedly, always controversially, in her fight against social injustice would, indeed, be wonderful entertainment for angels... or it could mean that her work was for the benefit of the ''angels'' at the bottom of the social ladder for whom she fought daily against those who would hold them down... this included her work as a suffregette. However it is meant, this film captures much of the real-life drama that took place on big city streets, and of the very personal trials which eventually led her to convert to roman Catholicism, and a dedication to helping the poor.', 'tt0116212', '/jYDSEJYcvSI0LzwIEP1JWMMAbWh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6789, 'Out of the Blue', False, True, False, False, 2002, 7.7, 'Andrea (Raoul Bova) is a handsome and confident thirty-eight-year old man: single, ''womanizer'' and superficial, with a successful career in a major product placement agency. His life of one-night stands seems to be just perfect: no responsibilities, hard work and endless fun. Until one day, he gets back home which he shares with Paulo (Edoardo Leo), a bizarre and unemployed friend of his, he finds Layla (Rosabel Laurenti Sellers), an extravagant seventeen-year old girl who claims to be his daughter. And she is not alone... She brought along her grandfather Enzo (Mark Giallini), an unlikely ex-rocker and the father of Andrea''s first fleeting and forgotten conquest... and they have come to stay!', 'tt2751576', '/i3ZB3GVSJQQYpVgJBDFpDrsaC3Q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6791, 'Sweet Rush', False, True, False, False, 2009, 6.4, 'As an aging woman married to a workaholic doctor by chance meets a young man who makes her feel young again. All of this is films by a director making a film about her which cuts in and out of the on camera and off camera drama.', 'tt1360887', '/p69rkUL4CExO1sQeg5edjTpAYVx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6792, 'Maine Pyar Kiya', False, True, False, False, 1989, 7.3, 'Karan (Alok Nath) lives in a small house with his only daughter, Suman (Bhagyashree). He decides to travel overseas in Dubai, in the Persian Gulf, so that he can accumulate enough wealth to get his daughter married. He decides to leave his daughter with his friend, Kishen Kumar choudhury (Rajiv Verma), wife Mrs. Kaushalya (Reema Lagoo), and son Prem (Salman Khan). Prem is supposed to marry Seema (Pervin Dastur), who is the only daughter of Kishen''s business partner, Ranjeet (Ajit Vachani). But Prem falls in love with Suman. He woos her until she also falls in love with him. Kaushalya approves of Suman, but Kishan does not, and asks her to get out of his house and life, as he feels that she has taken advantage of his hospitality. Karan returns and is enraged at Kishan''s behaviour. Prem is unhappy with his dad''s decision, and he decides to leave home and be near Suman.', 'tt0100095', '/aqZ9CEbXVLrEVAZFpT2F7PAE7dV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6793, 'The Curiosity of Chance', False, True, False, False, 2006, 6.6, 'High school coming-of-age tale. It is the 1980''s and new wave angst and gender-bending fashion are all the rage. Chance Marquis is the new kid at an American high school in Belgium and  is a somewhat awkward teenager who is the target of the school bully. To deal with this dilemma, Chance turns to the opposite ends of the high school spectrum for help. On one end is the flamboyant drag queen and at the other end, the varsity jock.', 'tt0481273', '/wyxqgHEuryzwE0pJYqwJGDo98Cr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6794, 'Hank and Asha', False, True, False, False, 2014, 6.5, 'An Indian student in Prague and a lonely New Yorker correspond online through video letters. A voyeuristic love story about aching for human connection in a hyper-connected world.', 'tt2330270', '/kjkPVdMArDjdxAHLZT1zNJiihL3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6795, 'Walter', False, True, False, False, 2015, 5.3, 'A ticket-taker at the local cinema believes he is the son of God. He has agreed to decide the eternal fate of everyone he comes in to contact with.', 'tt2016335', '/cwwL3zfVNpVsLrRr35U4QQVXmeG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6796, 'The Memphis Belle: A Story of a Flying Fortress', False, True, False, False, 1944, 7.3, 'Documentary about the 25th and last bombing mission of a B17, the "Memphis Belle". The "Memphis Belle" took part in a great bombing raid on sub-pens in Wilhelmshafen, Germany.  If she makes it back from this one, she''ll be retired from combat and sent home. That is, if she makes it!
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6798, 'Grand Theft Parsons', False, True, False, False, 2004, 6, 'There are times when it''s right and proper to simply bury the dead. This is not one of those times... Gram Parsons was one of the most influential musicians of his time; a bitter, brilliant, genius who knew Elvis, tripped with the Stones and fatally overdosed on morphine and tequila in 1973. And from his dying came a story. A story from deep within folklore; a story of friendship, honour and adventure; a story so extraordinary that if it didn''t really happen, no one would believe it. Two men, a hearse, a dead rock star, five gallons of petrol, and a promise. And the most extraordinary chase of modern times.', 'tt0338075', '/5sWWj5tlTDyAi2yiEjQZUmJCoKr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6799, 'Swimfan', False, True, False, False, 2002, 5.1, 'Ben Cronin has it all: the admiration of his many friends, a terrific girlfriend, and he''s on the fast-track to an athletic scholarship. Ben''s rock-solid, promising future and romance are turned upside-down with the arrival of Madison Bell. Madison, the new girl in town, quickly sets her sights on the impressionable Ben. While their first few meetings are innocent enough, the obsessive and seductive Madison wants more ... much more.', 'tt0283026', '/mOAiqOivdVecMu0yhumN6pzDWmo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6801, 'The Million Pound Note', False, True, False, False, 1954, 6.9, 'An impoverished American sailor is fortunate enough to be passing the house of two rich gentlemen who have conceived the crazy idea of distributing a note worth one million pounds. The sailor finds that whenever he tries to use the note to buy something, people treat him like a king and let him have whatever he likes for free. Ultimately, the money proves to be more troublesome than it is worth when it almost costs him his dignity and the woman he loves.', 'tt0046072', '/9Hj4NNnhP1NU2f25bCAjzexUVoh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6803, 'Warm Springs', False, True, False, False, 2005, 7.5, 'Franklin Roosevelt, left a paraplegic from polio at 39 years of age, seeks out a miracle cure in the backwoods of Georgia.', 'tt0423510', '/ieSu2e0yBZ34IwEzgNPnliHALNo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6804, 'The Living Wake', False, True, False, False, 2007, 6.3, '“The Living Wake” is a dark comedy set in a timeless storybook universe. Self-proclaimed artist and genius, K. Roth Binew, has one day to live. He has enlisted his best and only friend, Mills Joquin, to take him around on a bicycle powered rickshaw. In a final attempt to probe life’s deepest mysteries, Binew endures one ridiculous trial after the next. He concludes his day with a final performance, his living wake. On a makeshift stage in an open field, Binew’s friends and enemies gather to witness his madness one final time.', 'tt0489212', '/xxxbBu9HIsSmbFK37Nq0QuncBzI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6805, 'Businessman', False, True, False, False, 2012, 7.1, 'Businessman (Telugu: బిజినెస్ మాన్) is a Telugu crime film written and directed by Puri Jagannadh. It stars Mahesh Babu and Kajal Agarwal in the lead roles. Produced by R. R. Venkat and distributed by R. R. Movie Makers, the film was also dubbed into Tamil and Malayalam languages. It was released on 13 January 2012 and turned out to be a blockbuster.', 'tt2071613', '/aID26sABfyUqHhYbeW3o8V3I6tq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6806, 'In a Year with 13 Moons', False, True, False, False, 1978, 7.5, 'This drama follows the last few days in the life of Elvira (formerly Erwin) Weisshaupt. Years before, Erwin told a co-worker, Anton, that he loved him. "Too bad, you aren''t a woman," he replied. Erwin took Anton at his word. Trying to salvage something from the wreckage love has made of his life, he now hopes that Anton will not reject him again.', 'tt0077729', '/oXaPDIlytTEv0vQopNkgv3wYXM6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6811, 'Animal', False, True, False, False, 2014, 4.7, 'When plans for a weekend vacation hit a dead end, a group of close-knit friends find themselves stranded in unfamiliar territory, pursued by a menacing predator. Holed up in an isolated cabin, tensions mount as long-buried secrets are revealed. As the body count rises, the group must put their differences aside and fight for survival.', 'tt2996684', '/jR1nY5Cn4lpFpbsteGRTJtGDaVS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6812, 'Anguish', False, True, False, False, 2015, 4.5, 'A new girl in a quiet town, Tess tries to manage her psychosis while adjusting to her new life with her mom. After stumbling upon the shrine of Lucy, a hit and run victim, Tess finds herself overwhelmed by hallucinations of the dead girl and starts to question her sanity again.  When the spirit possesses Tess’s mind and soul, mother and daughter are at a loss for where to turn next for salvation: religion or medicine?  Both institutions have failed them to date but faith leads them to the house of Sarah, Lucy’s mother, who has been despondent since the accident.  All together with time working against them, Sarah is desperate to believe that Lucy is still alive in Tess but neither parent wants to give up on their daughter.  Now that she understands what is happening to her, ultimately Tess must decide whether she wants to keep fighting or succumb to her affliction.', 'tt3620762', '/o8B3CIbIuxWhPf0G19lse4mJrLI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6815, 'Christmas Story', False, True, False, False, 2007, 7, 'Hundreds of years ago in Lapland, a little boy named Nikolas loses his family in an accident. The villagers decide to look after the orphaned boy together. Once a year - at Christmas - Nikolas moves to a new home. To show his gratitude, Nikolas decides to make toys for the children of the families as good-bye presents. Over the years, Nikolas''s former adoptive families become many, and soon almost every house has presents on its doorstep on Christmas morning. At thirteen, Nikolas is sent to live and work with Iisakki, a grumpy old carpenter, who forbids Nikolas to continue making presents for Christmas. Gradually, however, Nikolas wins Iisakki''s trust. Together they begin to look after the Christmas traditional that Nikolas has begun. When the aged Iisakki has to leave Nikolas and move away, the tradition of Christmas presents is once again at risk. Thankfully, Nikolas comes up with a solution that brings children joy every Christmas, even continuing to today.', 'tt0772176', '/9SBgvVBhaedbQods3ZcIRY2NS4X.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6816, 'Naked as We Came', False, True, False, False, 2013, 6.2, 'Love, loss and hope are tumultuously explored amidst a tranquil backdrop and asks us all the question: What is your dream?', 'tt1754257', '/1OeU7vYojFopPUvh9wjNgUx1uhG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6818, 'Where the Lilies Bloom', False, True, False, False, 1974, 7.2, 'A family of children decide not to tell anyone their father has died, and to live on their own in the backwoods of rural North Carolina. If the state finds out they are on their own, they will be split up and sent to live in foster homes.', 'tt0072401', '/A2VQqJRNEnJ6Dc1TMkrRcYW5qL7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6819, 'Why We Fight: The Nazis Strike', False, True, False, False, 1943, 7.1, 'This documents the relentless advance of Germany''s mighty military machine, as Hitlers armies terrorize their victims with blitzkrieg tactics and spread through the Rineland,Austria, Czechoslovakia, and Poland', 'tt0184773', '/zXnZp8mmZUWG4jH3CaoULJoJcom.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6820, 'Paris Was a Woman', False, True, False, False, 1996, 7.4, 'Women (many of them lesbian) artists, writers, photographers, designers, and adventurers settled in Paris between the wars. They embraced France, some developed an ex-pat culture, and most cherished a way of life quite different than the one left behind.', 'tt0114093', '/zORtAZNBBy6b4cocGiXqgcXjHEg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6821, 'For Ellen', False, True, False, False, 2012, 6, 'After an overnight long-distance drive, Joby has a special meeting—with lawyers and his ex-wife. A struggling musician with the prerequisite tattoos, slimy hair, goatee, and his head firmly floating in the clouds, Joby hasn’t been around to be a dad. Now is his last chance to fight for shared custody of his daughter, Ellen.', 'tt1570583', '/w3qMjOVOzwheOh9PbLEnZfNKNmh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6822, 'The Adventurer: The Curse of the Midas Box', False, True, False, False, 2013, 5.4, '17-year-old Mariah Mundi''s life is turned upside down when his parents vanish and his younger brother is kidnapped. Following a trail of clues to the darkly majestic Prince Regent Hotel, Mariah discovers a hidden realm of child-stealing monsters, deadly secrets and a long-lost artifact that grants limitless wealth – but also devastating supernatural power. With the fate of his world, and his family at stake, Mariah will risk everything to unravel the Curse of the Midas Box.', 'tt1376213', '/31ykUoOS2RNdARk3B8z3y25gD8b.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6824, 'Lad: A Yorkshire Story', False, True, False, False, 2013, 7.9, 'After the death of his father, a teenage boy forms a healing friendship with a park ranger in the Yorkshire Dales', 'tt2073600', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6826, 'Google and the World Brain', False, True, False, False, 2013, 6.7, 'The most ambitious project ever conceived on the Internet: Google''s master plan to scan every book in the world and the people trying to stop them. Google says they are building a library for mankind, but some say they also have other intentions.', 'tt2551516', '/5hIa02gmwF6PyQ7Y9eWkIQ8Obfo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6829, 'The Object of Beauty', False, True, False, False, 1991, 5.6, 'A valuable piece of art is stolen from a couple.', 'tt0102573', '/vtxTzSrjCDH7Ray4RK8FPUrrlSy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6830, 'Milton''s Secret', False, True, False, False, 2016, 5.8, '11 year-old Milton Adams is growing up in an uncertain world. With his parents constantly stressing about their careers and finances, and the neighborhood bully tormenting him, Milton feels his whole world is in crisis. But when his Grandpa Howard comes to visit, he discovers anxiety about the past and worrying about the future only make things worse and prevent him from finding true happiness moment to moment.', 'tt2806390', '/iMn025BdcE3Ok5xrgxETOhZ1Nn3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6831, 'George Carlin: What Am I Doing in New Jersey?', False, True, False, False, 1988, 8.2, 'George Carlin changes his act by bringing politics into the act, but also talks about the People he can do without, Keeping People Alert, and Cars and Driving part 2.', 'tt0246644', '/185znBGXHFmeGpcQL3uqv0BjRDY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6832, 'Kill Your Friends', False, True, False, False, 2015, 6, 'In the late 1990s, a drug-addled nihilist resorts to murder to climb the ladder of the London music industry.', 'tt2474958', '/nhRbLPteFPi01bn9wFXXaxwjMF1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6833, 'Betrayed', False, True, False, False, 2018, 5.5, 'Screen superstars Clark Gable ("Gone With The Wind," "It Happened One Night") and sultry bombshell Lana Turner ("Peyton Place," "The Postman Always Rings Twice") team-up in this intriguing WWII drama. Suspected of being a Nazi spy, Dutch-resistance member Turner is given a last chance mission to redeem herself. Gable is an American colonel who falls in love with her. Co-starring Victor Mature ("My Darling Clementine") and Oscar-nominee Louis Calhern ("The Asphalt Jungle").', 'tt0046770', '/jVEC9c7O6xouxCLJT3QGVEB6cLe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6834, '1981', False, True, False, False, 2009, 7.2, 'Filmmaker Ricardo Trogi recalls the events surrounding his family moving to a new neighborhood when he was 11-years-old.', 'tt1286159', '/d2I0StFBFcAsdi5V2NGB4bgQC8K.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6835, 'Sundown', False, True, False, False, 2016, 5.4, 'High school seniors Logan and Blake prepare for an epic Spring Break as they travel to the beautiful and exotic Mexican beach resort of Puerto Vallarta hoping to hook up with their high school crushes. Surrounded by hot girls in teeny bikinis, the guys are truly in heaven. After meeting at a club, Logan falls for a mysterious local beauty named Gaby but then realizes the next morning, when his grandfather’s Rolex watch is missing, that she was after more than just his heart.', 'tt2381213', '/AFeuY7y5EgktZzE6nwByAkVYPP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6837, 'Dead or Alive 2: Birds', False, True, False, False, 2000, 6.8, 'Two contract killers cross paths in the middle of the same job and realize they are childhood friends. Together they take a break from killing and visit the small island they once called home. After reflecting on their past lives they decided to team up and use their talents in killing for good... much to the upset of the crime syndicates.', 'tt0285005', '/hEi5PhfgInQPL9Z0r3jijuYN8Im.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6839, 'Cut Snake', False, True, False, False, 2015, 5.8, 'Young, charismatic, and hardworking, Sparra Farrell seems to be sailing into a happy, respectable life. He has a solid job and an adorable fiancée named Paula, and already owns a modest house in the country outside Melbourne. The only odd thing is that Sparra says precious little about his past — but that past is about to catch up with him, and wrest control of his present.', 'tt2574576', '/sZQP3RNi3QFfE9e5IM4r13PGR0B.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6842, 'In Pursuit of Honor', False, True, False, False, 1995, 7.2, 'To save a group of horses slated to be destroyed by the US Cavalry, a group of officers rebel and begin a journey towards Canada to save themselves and the mounts.', 'tt0113399', '/wQfP0juGwqU6ZiXvC0MsnSwnQld.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6844, 'Inner Worlds, Outer Worlds', False, True, False, False, 2012, 8.6, 'There is one vibratory field that connects all things. It has been called Akasha, Logos, the primordial OM, the music of the spheres, the Higgs field, dark energy, and a thousand other names throughout history. The vibratory field is at the root of all true spiritual experience and scientific investigation. It is the same field of energy that saints, Buddhas, yogis, mystics, priests, shamans and seers, have observed by looking within themselves. Many of history''s monumental thinkers, such a Pythagoras, Kepler, Leonardo DaVinci, Tesla, and Einstein, have come to the threshold of this great mystery. It is the common link between all religions, all sciences, and the link between our inner worlds and our outer worlds.', 'tt2415372', '/kg1c2AS8KXAE73XWzot1cslnG5t.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6846, 'Accidentally Engaged', False, True, False, False, 2016, 5.8, 'When unsuccessful actress Clarissa returns to her hometown for a wedding and tries to impress her old friends by claiming she''s dating big time celebrity Chas Hunter, she suddenly finds herself in a comically false engagement when her lies go public and Chas decides to join the festivities in a stunt to escape bad press.', 'tt4742556', '/lArOsPsD5e0W0paC7vReirclHNw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6848, 'The Ladies Man', False, True, False, False, 2000, 5.2, 'After his girl leaves him for someone else, Herbert gets really depressed and starts searching for a job. He finally finds one in a big house which is inhabited by many, many women. Can he live in the same home with all these females?', 'tt0055069', '/c15TotHYrXPZSzjZCBfbup3fz4V.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6849, 'Life Without Principle', False, True, False, False, 2011, 6.9, 'A criminal, a bank clerk and a police officer find their destinies entwined when a loan shark gets assaulted after having withdrawn $10 million from the bank in the midst of the world financial crisis.', 'tt1371585', '/5ky8EPNnNTtqsD8yUHUCVraBQGH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6850, 'A Midsummer Night''s Dream', False, True, False, False, 2017, 5.5, 'Classic Shakespeare play adapted for television by Russell T Davies. In the tyrannical court of Athens, pitiless dictator Theseus plans his wedding to Hippolyta, a prisoner of war, and young Hermia is sentenced to death by her own father. Meanwhile, in the town below, amateur theatre group the Mechanicals rehearse, with all their comic rivalries. And beyond Athens, in the wild woods, dark forces are stirring...', 'tt5051278', '/8ykwua6YgZn2K3BHS6bYnJ8NjMN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6851, 'Blackball', False, True, False, False, 2003, 5.6, 'Blackball follows the fortunes of Cliff Starkey, a working-class fine of lawn bowls with an exceptional talent. Wanting to take on the Aussies he manages to become regional champion, only to get banned. Sports agent Rich Schwartz picks him up and makes him so popular the Bowls Committee deem to lift the ban. Now the question is whether he can regain his form and his friends to beat the Aussies.', 'tt0337879', '/fKQJP1LOcsGwZGqiMWeEiLPRbMT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6854, 'Knockabout', False, True, False, False, 1979, 7.3, 'Yuen Baio and Ka-Yan Leung play brothers Yipao and Taipao, lovable rogues who convince a martial arts expert to take them on as students. When Yipao discovers that their teacher is actually a wanted criminal, Silver Fox (Chia Yung Liu), he is attacked by his master. Taipao jumps in to protect his sibling but is killed; Yipao barely escape with his life. Seeking revenge, Yipao enlists the help of a beggar (Sammo Hung) who is a master of monkey-style kung fu, and together they take on Silver Fox in a breath-taking fight to the death.', 'tt0080166', '/x4mkOTENUF2MWwJpgHbiKaHcPq6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6855, 'House II: The Second Story', False, True, False, False, 1987, 5.4, 'Young urban professionals Jesse (Arye Gross) and his girlfriend Kate (Lar Park Lincoln), move into an old mansion that has been in Jesse''s family for generations. They are soon joined by Jesse''s goofy friend Charlie (Jonathan Stark), who brought along his diva girlfriend Lana (Amy Yasbeck), in the hopes of being discovered by Kate, who works for a record company. Jesse has returned to this old family mansion after his parents were murdered when he was a baby. While going through old things in the basement, Jesse finds a picture of his great-great grandfather (and namesake) in front of a Mayan temple holding a crystal skull with jewels in the eyes. In the background is a man Jesse learns is Slim Razor, a former partner of his great-great grandfather turned bitter enemy after a disagreement over who would get to keep the skull.', 'tt0093220', '/o58So9NfjKPITUQhQdpCUdTi9Vn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6856, 'Starstruck', False, True, True, False, 1982, 7, 'A new-wave extravaganza in which a young woman wants to become the next big singer with the help of her friends and despite the wishes of her working class family.', 'tt0084728', '/lFa9lMfMwo1DX8dLLyIRPPuP5c3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6859, 'The Frame', False, True, False, False, 2014, 6.7, 'Two strangers find their lives colliding in an impossible way. Alex is a methodical cargo thief working for a dangerous cartel. Sam is a determined paramedic trying to save the world while running from her past.', 'tt2567038', '/82Tpv6o0zpkN2OsNXYK3feBRe10.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6861, 'Cherry Tree Lane', False, True, False, False, 2010, 4.9, 'Prosperous professional couple Mike and Christine are settling in for a standard evening of wine, TV and low-level marital hostility when a ring on their doorbell changes everything. Turns out their son Sebastian is in a little trouble with some local boys, who are quite prepared to camp out and wait for him to get home ... the resulting culture-clash chamber drama is raw, revealing and nerve-splittingly tense.', 'tt1468829', '/xcOYvgt7YQ27vjPVotOY4ogqIzS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6862, 'Good', False, True, False, False, 2008, 6.2, 'The rise of national socialism in Germany should not be regarded as a conspiracy of madmen. Millions of "good" people found themselves in a society spiralling into terrible chaos. A film about then, which illuminates the terrors of now.', 'tt0436364', '/tZzB2vhM5HMQ0KVofoXy7laRqFp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6863, 'The Duke Is Tops', False, True, False, False, 1938, 5.5, 'A theatrical producer puts aside his own success to boost the career of a talented singer.', 'tt0030089', '/4hX85WJ4WIVBTIicS2w9yxHZBcI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6864, 'Vegucated', False, True, False, False, 2011, 7.2, 'Vegucated is a guerrilla-style documentary that follows three meat- and cheese-loving New Yorkers who agree to adopt a vegan diet for six weeks and learn what it''s all about. They have no idea that so much more than steak is at stake and that the planet''s fate may fall on their plates. Lured by tales of weight lost and health regained, they begin to uncover hidden sides of animal agriculture that make them wonder whether solutions offered in films like Food, Inc. go far enough. Before long, they find themselves risking everything to expose an industry they supported just weeks before. But can their convictions carry them through when times get tough? What about on family vacations fraught with skeptical step-dads, carnivorous cousins, and breakfast buffets? Part sociological experiment and part adventure comedy, Vegucated showcases the rapid and at times comedic evolution of three people who are trying their darnedest to change in a culture that seems dead set against it.', 'tt1814930', '/jKXD994yp5b95lXK7aGhGSGPysF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6865, 'Stark Raving Mad', False, True, False, False, 2002, 6.4, 'A crook quietly plots his revenge against the boss who murdered his brother while working for him.', 'tt0286152', '/1MtKcti03XF7btAjr9wcTLrLZ4e.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6866, 'Attack of the Crab Monsters', False, True, False, False, 1957, 4.9, 'A group of scientists travel to a remote island to study the effects of nuclear weapons tests, only to get stranded when their airplane explodes. The team soon discovers that the island has been taken over by crabs that have mutated into enormous, intelligent monsters. To add to their problems, the island is slowly sinking into the ocean. Will any of them manage to escape?', 'tt0050147', '/jb7TY1BVmXoWvYi0aZZFlq4DZ03.jpg');
