insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6896, 'Coin Locker Girl', False, True, False, False, 2015, 6.7, 'A child abandoned in a subway coin locker is sold to a ruthless and calculating loan shark and gangster boss dubbed “Mother” (Kim Hye-soo), who runs an organ-harvesting ring in Incheon’s Chinatown. Named Il-Young (Kim Go-Eun), the baby girl grows into Mother’s loyal right-hand enforcer and is groomed to be her eventual successor. But when Il-Young’s loyalty to Mother wavers, the clash between the two unleashes a merciless tidal wave of blood-soaked retribution and strife.', 'tt4647788', '/6675QSLeqcAjMxh74MQoqGuyWu3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6897, 'Heaven', False, True, False, False, 1987, 6.8, 'A woman takes the law into her own hands after police ignore her pleas to arrest the man responsible for her husband''s death, and finds herself not only under arrest for murder but falling in love with an officer.', 'tt0246677', '/xPdtOuGtcQt655uRrmIdBkSRmVx.jpg');
