insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6900, 'Original Gangstas', False, True, False, False, 1996, 5.7, 'A violent street gang, the Rebels, rule the streets of Gary, Indiana. The Rebels shoot Marvin Bookman, a store-keeper, for giving the police information about a drive-by shooting they committed. Marvin''s son, former NFL star John who created the Rebels, returns to Gary to be with his father and, with a little help from his friends, to destroy the Rebels his way.', 'tt0117260', '/uirrsTZmoPmscHZdyWz4tiXVqHE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6901, '1000 To 1', False, True, False, False, 2014, 7, 'Cory Weissman is a college basketball player who suffers a devastating stroke. He perseveres to find new meaning in his life both on, and more importantly, off the court.', 'tt2391950', '/tHaz9IAzgPFdWQ9LigNhIjCxDCW.jpg');
