insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (6908, 'Harlequin', False, True, False, False, 1980, 6.2, 'In this modern-day version of the Rasputin story, David Hemmings plays an up-and-coming senator, Nick Rast, whose young son is terminally ill with leukaemia. A mysterious faith healer, Gregory Wolfe (Robert Powell), appears and seems to cure the boy. Rast''s wife Sandy (Carmen Duncan) falls in love with Wolfe, but the powerful interests behind Rast''s career, represented by geriatric monster Doc Wheelan (Broderick Crawford) are less happy with events.', 'tt0080842', '/cpKPjzuACboHbH0BEJgPz4EaH6A.jpg');
