insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7224, 'Ragamuffin', False, True, False, False, 2014, 6.8, 'RAGAMUFFIN is based on the true story of Rich Mullins, a musical prodigy who rose to Christian music fame and fortune only to walk away and live on a Navajo reservation.  An artistic genius raised on a tree farm in Indiana by a weathered, callous father, Rich wrestled all of his life with the brokenness and crippling insecurity born of his childhood.  A lover of Jesus and a rebel in the church, Rich refused to let his struggles with alcoholism, addiction and women tear him away from a God he was determined to love.  As he struggled with success in Nashville, depression in Wichita, and oblivion in the Four Corners, Rich became one of the first of his time to live honestly amidst a culture of religion and conformity.', 'tt2412568', '/rEGqcU7bY6LlJ5t9jV6GfnIox92.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7225, 'Revolver', False, True, False, False, 1973, 7.1, 'Hotshot gambler Jake Green (Jason Statham) is long on bravado and seriously short of common sense. Rarely is he allowed in any casino because he''s a bona fide winner and, in fact, has taken so much money over the years that he''s the sole client of his accountant elder brother, Billy. Invited to a private game, Jake is in fear of losing his life.', 'tt0365686', '/AjMMNBRphfb2DPKwRKmujJlhv25.jpg');
