insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7232, 'Phyllis and Harold', False, True, False, False, 2010, 6.3, 'Phyllis and Harold is a frank journey through a disastrous 59 year old marriage. Drawing on a lifetime of her family''s home movies and interviews made over 12 years, filmmaker Cindy Kleine mixes reportage, cinema verite and animation to uncover family secrets and tell a story that could not be shown publicly as long as her father was alive.', 'tt1382323', 'None');
