insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7252, 'The Secret Life of Chaos', False, True, False, False, 2010, 8.4, 'Chaos theory has a bad name, conjuring up images of unpredictable weather, economic crashes and science gone wrong. But there is a fascinating and hidden side to Chaos, one that scientists are only now beginning to understand. It turns out that chaos theory answers a question that mankind has asked for millennia - how did we get here?', 'tt1674741', '/uZRFOf5d8evP7eqtQJJZN3Fyn7D.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7253, 'The People That Time Forgot', False, True, False, False, 1977, 5.4, 'A sequel to The Land That Time Forgot. Major Ben McBride organises a mission to the Antarctic wastes to search for his friend (Doug McClure) who has been missing in the region for several years. McBride''s party find themselves in a world populated by primitive warriors and terrifying prehistoric creatures, all of whom they must evade in order to get back safely', 'tt0076535', '/8IIrlm2CjGCY5UV94lMCNb9HC94.jpg');
