insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7260, 'Abattoir', False, True, False, False, 2016, 4.4, 'A reporter unearths an urban legend about a home being constructed from rooms where horrific tragedies have occurred.', 'tt1480285', '/c98gHIaL1yfwuU72bBSawq6TtRE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7262, 'Bent', False, True, False, False, 2018, 5.2, 'Max is a handsome young man who, after a fateful tryst with a German soldier, is forced to run for his life. Eventually Max is placed in a concentration camp where he pretends to be Jewish because in the eyes of the Nazis, gays are the lowest form of human being. But it takes a relationship with an openly gay prisoner to teach Max that without the love of another, life is not worth living.', 'tt0118698', '/zIH22c5vqvPSHialaNH13tjNNCZ.jpg');
