insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7330, 'Chapter 27', False, True, False, False, 2007, 5.7, 'The movie takes place in the three days leading up to Lennon''s murder and is intended to be an exploration of Chapman''s psyche, without putting substantial emphasis on the murder. The title "Chapter 27" suggests a continuation of J.D. Salinger''s novel The Catcher in the Rye, which has 26 chapters, and which Chapman was carrying when he shot Lennon. Chapman was obsessed with the book, to the point', 'tt0488988', '/sXaicMCrFMqkSNmHj7KzFNhckDQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7333, 'Page 3', False, True, False, False, 2005, 7.3, '''Page 3'' takes a behind-the-scenes look at the lifestyles of the A-list celebrities in metro cities. It explores the networking and the power play between the air-kissing', 'tt0443708', '/wQnQ8BOiJpLze6wC01ujoQcjyeU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7334, 'Left Behind II: Tribulation Force', False, True, False, False, 2002, 5, 'The sequel to the blockbuster "Left Behind". It is two weeks after the Rapture, and those who were left behind must band together to form the Tribulation Force and will prepare themselves for the worst seven years the planet will ever see.', 'tt0283644', '/u4R0xYkKIOInESYvQcnODukSz5t.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7335, 'Harbinger Down', False, True, False, False, 2015, 4.5, 'A group of grad students have booked passage on the fishing trawler Harbinger to study the effects of global warming on a pod of Orcas in the Bering Sea. When the ship''s crew dredges up a recently thawed piece of old Soviet space wreckage, things get downright deadly. It seems that the Russians experimented with tardigrades, tiny resilient animals able to withstand the extremes of space radiation. The creatures survived, but not without mutation. Now the crew is exposed to aggressively mutating organisms. And after being locked in ice for 3 decades, the creatures aren''t about to give up the warmth of human companionship.', 'tt3397918', '/9oM0jDsFUNiuCeqhAx3GlP81YVc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7336, 'An Apology to Elephants', False, True, False, False, 2013, 8.1, 'Elephants are among the most majestic and intelligent creatures on Earth--but for hundreds of years, they have suffered at the hands of humans. Narrated by Lily Tomlin, this documentary short traces our long history with elephants and explores the many problems that arise when they are brought to live in captivity in zoos and circuses.', 'tt2836524', '/28rNjr4q4e6Z4T71lQn6nsNfySe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7337, 'New Rose Hotel', False, True, False, False, 1999, 5.1, 'A corporate raider (Christopher Walken) and his henchman (Willem Dafoe) use a chanteuse (Asia Argento) to lure a scientific genius away from his employer and family.', 'tt0133122', '/c85qaGI5dxQGmcR3tcU1C2k9Rlf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7339, 'Golmaal Returns', False, True, False, False, 2008, 5.1, 'Golmaal Returns, the much-awaited follow-up to the uproariously comical smash-hit Golmaal, arrives with a renewed star power, chartbusting music and more laughter than ever before!', 'tt1092005', '/2X6ZSlnBpgsOpH0ELDraWTD4QO7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7340, 'Bless the Child', False, True, False, False, 2000, 5.1, 'When Maggie''s sister Jenna saddles her with an autistic newborn named Cody she touches Maggie''s heart and becomes the daughter she has always longed for. But six years later Jenna suddenly re-enters her life and, with her mysterious new husband, Eric Stark, abducts Cody. Despite the fact that Maggie has no legal rights to Cody, FBI agent John Travis, takes up her cause when he realizes that Cody shares the same birth date as several other recently missing children.', 'tt0163983', '/s1RGEF8xIA6ecF8W4do8CaWI0yE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7341, 'Alive Day Memories: Home from Iraq', False, True, False, False, 2007, 8.1, 'In a war that has left more than 25,000 wounded, ALIVE DAY MEMORIES: HOME FROM IRAQ looks at a new generation of veterans. Executive Producer James Gandolfini interviews ten Soldiers and Marines who reveal their feelings on their future, their severe disabilities and their devotion to America. The documentary surveys the physical and emotional cost of war through memories of their "alive day," the day they narrowly escaped death in Iraq.', 'tt0933877', '/oQnWBFDUy4XKvkDBVp9fGOXWrLc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7342, 'Shaolin Temple', False, True, False, False, 1976, 6.9, 'There is no place more hallowed in the martial art world than China''s Shaolin Temple. This special place deserves a special epic, which is what the martial arts maestro delivers in this battle between a brave brand of Chinese boxers and literally thousands of Ching troops - complete with betrayals, intrigues, and such novel fighting machines as 108 wooden robots. The conflicts grow in complexity, intensity and even suspense as monks struggle to stay alive in the face of overwhelming odds.', 'tt0075202', '/f6IALg4S0qujZha9xbFBLnELksZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7343, 'Man On a Mission: Richard Garriott''s Road to the Stars', False, True, False, False, 2010, 6.9, '', 'tt1611990', '/o8KpF2sVuV3AX2XV4vjtr4vst1x.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7344, 'X: The Unheard Music', False, True, False, False, 1986, 8, 'A documentary about the band X. Includes live and studio performances and interviews with the band members.', 'tt0092137', '/pDlUbdyvTPvmpiKtbXE4jsn34ca.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7345, 'Street Law', False, True, False, False, 1974, 7, 'Carlo Antonelli, an engineer from Genoa, gets mugged and decides to take justice into his own hands. At first the muggers seem to get the upper hand, but then he''s helped by Tommy, a young robber who takes his side.', 'tt0071330', '/i4ZVsXMJ8vLEbR4Wkj4rszc9EVE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7346, 'Will', False, True, False, False, 2011, 6.9, 'Will is an 2012 British sports drama directed by Ellen Perry and starring Damian Lewis, Jane March and Bob Hoskins. .At the start of the film, eleven year-old Liverpool fan Will Brennan is at a boarding school in the south of England, due to his father Gareth''s (Lewis) inability to look after him following the death of his mother. Gareth arrives one day out of the blue, with two tickets to see Liverpool play AC Milan in the 2005 Champions'' League Final in Istanbul. Unfortunately, before they can go, Gareth dies suddenly. Will is determined to go and honour his father''s memory. He runs away and makes it as far as France. He then meets Alek, a former Yugoslavian footballer who stopped playing during his country''s civil war. Despite Alek''s initial reluctance to get involved, he is inspired by Will''s determination and tries to help him to fulfil his dream. After many trials and tribulations, Will gets his wish and has a better experience at the match than he could ever have imagined.', 'tt1636844', '/9aEpPFYcOQyqtAinGIqlRws6oaq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7347, 'Who Saw Her Die?', False, True, False, False, 1972, 6.5, 'A young girl is brutally murdered somewhere in France. Sometime later, the same thing happens to the daughter of a well-known sculptor. This time the parents (the sculptor and his wife) start investigating, and soon find they are in way over their head. Meanwhile, the body-count keeps rising as the killer now starts butchering all those who find out too much...', 'tt0068367', '/nwNk6Ju6s9okLjQvwgReEGFRmyF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7348, 'Are You Here', False, True, False, False, 2013, 5.3, 'When Steve Dallas, a womanizing local weatherman, hears that his off-the-grid best friend Ben Baker has lost his estranged father, the two return to Ben''s childhood home. Once there, they discover Ben has inherited the family fortune, and the ill-equipped duo must battle Ben''s formidable sister and deal with his father''s gorgeous 25-year old widow.', 'tt1545754', '/v83MAkGH8jF8uNhJhKgADqw3Xup.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7350, 'Murder Rap: Inside the Biggie and Tupac Murders', False, True, False, False, 2015, 7.5, 'Two of the most notorious unsolved cases in the history of American crime – the murders of renowned rap stars Tupac Shakur and Biggie Smalls – have been the subject of exhaustive investigations, relentless speculation, and a web of conspiracy theories and dark secrets. Now, for the first time, the true story behind these sensational cases is laid bare in "Murder Rap: Inside the Biggie and Tupac Murders". Using information sourced from hundreds of police case files, taped confessions never shown before, and interviews with the lead detective and witnesses, this is the riveting account of the task force that finally exposed the shocking truth behind the deaths of these two rap music icons.', 'tt4715470', '/z9aPwXx6lWKb5y5U3ZRFSUcecL4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7353, 'Bernard and Doris', False, True, False, False, 2006, 6.5, 'Sympathetic look loosely based on the relationship between tobacco heiress, Doris Duke (1912-1993) - think Duke University - and her shy butler, Bernard Lafferty. The icy and mercurial Duke fires her butler for serving a chilled cantaloupe; the agency sends Lafferty, formerly household staff to Liz Taylor and to Peggy Lee. He''s an alcoholic, fresh out of rehab. He gradually becomes Duke''s gay alter ego as she romps through life sleeping with young men, making shrewd decisions quickly, managing her fortune and orchids as Lafferty manages her New Jersey estate. With a wine cellar to die for, Bernard falls off the wagon. Can he pull himself together when Doris needs him?', 'tt0470732', '/nEss2KQDSB9pvTVhuxIidsFBxig.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7354, 'Bathory: Countess of Blood', False, True, False, False, 2008, 5.9, 'Bathory is based on the legends surrounding the life and deeds of Countess Elizabeth Bathory known as the greatest murderess in the history of mankind. Contrary to popular belief, Elizabeth Bathory was a modern Renaissance woman who ultimately fell victim to mens aspirations for power and wealth.', 'tt0469640', '/hnAgDvu7zx6rBW3W6tN9uXlxVFC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7355, 'Sunset Strip', False, True, False, False, 2012, 6.8, 'A documentary on the famous Los Angeles street.', 'tt1542485', '/iUtn31GVCEKszL4nMulNF23Bnma.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7357, 'Man Friday', False, True, False, False, 1975, 6.4, 'Englishman Robinson Crusoe, stranded alone on an island for years, is overjoyed to find a fellow man, a black islander whom he names Friday. But Crusoe cannot overcome the shackles of his own heritage and upbringing and is incapable of seeing Friday as anything other than a savage who needs Crusoe''s brand of cultural and religious enlightenment. Friday attempts to share his own more generous and unashamed culture, but ultimately realizes that Crusoe can never see him as anything but an inferior being. With that awareness, Friday sets out to turn the tables on Crusoe.', 'tt0074849', '/kaT2bF0YSwtNSHL1eO6NZbfa6mk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7358, 'Louise by the Shore', False, True, False, False, 2016, 7.1, 'On the last day of summer, Louise, an old woman realizes that the last train has departed without her. She finds herself alone in a small seaside resort town, abandoned by everyone. The weather quickly turns for the worse followed by the seasonal tides. Fragile and coquettish, not nearly as well-prepared as a would-be Robinson Crusoe, Louise isn’t likely to make it through the winter. Yet, Louise takes her abandonment as a challenge. She’s going to survive, confronting the elements as well as her memories, which have found the perfect occasion to join in the adventure…', 'tt5894778', '/tjgfIldVv0380ohpptbbtE27VW2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7360, 'Emma''s Chance', False, True, False, False, 2016, 6, 'While fulfilling her community service hours at a horse rescue ranch, Emma forms an unlikely bond with an abused show horse who won''t let anyone ride him.', 'tt4948040', '/zCLzFbyaEEuOZhafdkfVqK8LcWt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7362, 'Bill Maher: The Decider', False, True, False, False, 2007, 7.2, 'Superstar comedian/writer Bill Maher, one of the most highly credited comic minds today, is back in an all-new solo HBO comedy special performed live. Maher, known for his sharp wit, offers his candid and hilarious opinions on a wide range of social and political issues including sex, drugs, Iraq, immigration, President Bush, and much more in this can''t miss special. Live show from Berklee Performance Center, Boston, Massachusetts', 'tt1039915', '/vzSoRqNVtOoob8nJBav9NF6FwNZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7364, 'Days of Darkness', False, True, False, False, 2007, 3.9, 'When a comet strikes Earth and kicks up a cloud of toxic dust, hundreds of humans join the ranks of the living dead. But there''s bad news for the survivors: The newly minted zombies are hell-bent on eradicating every last person from the planet. For the few human beings who remain, going head to head with the flesh-eating fiends is their only chance for long-term survival. Yet their battle will be dark and cold, with overwhelming odds.', 'tt0499456', '/kPbDvydsBYzjRcw3OrsKa1kdmMw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7365, 'Innocent Steps', False, True, False, False, 2005, 6.9, 'Innocent Steps is a romantic Korean tale about a 19 year old girl (Mun Geun-yeong) from China on a dance contract. She is paid to practice and dance with her partner (Park Geon Hyeong) for the upcoming ''KDFA Cup Korea Dancesport Championship''. For the next three months, she trained hard for the competition. Love blossomed, of course. Being professional during the practices, they did not reveal their feelings to each other.', 'tt0464636', '/4kddr06V0cRlMRdKHEqiE0aGQod.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7368, 'Classic Albums: Iron Maiden - The Number of the Beast', False, True, False, False, 2001, 8.3, '1982 was a momentous year for Iron Maiden. Following the addition of brilliant new vocalist Bruce Dickinson, their third album the number of the beast was released to universal acclaim from both critics and rock fans. The album topped the UK charts for 2 weeks, launched them into the US top 40 for the first time, and invaded the top 10 album charts worldwide achieving a plethora of gold and platinum awards. The number of the beast is regarded as one of the greatest and most influential heavy metal albums of all time, including, along with demonic title track, such classic metal anthems as run to the hills, hallowed be thy name, the prisoner and children of the damned. This exclusive film tells the full and vibrant story behind the making of the album.', 'tt0409827', '/zVFJKain6E8Yu7GAjcB6FgjPsnW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7369, 'Ajnabee', False, True, False, False, 2001, 6.4, 'Two couples swap mates which leads to a murder that leaves everybody shocked... but was this a setup to convicted an innocent person and spoil their reputation?', 'tt0278291', '/AnueNeojd8m5VmqjHLeBGxv7hnj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7370, 'Down the Shore', False, True, False, False, 2011, 5.9, 'Set on the Jersey shore, the lives of three childhood friends begin to unravel when a secret from their past is revealed', 'tt1155060', '/7O9Oxlj3Ar8VYpZdwfDVFgemnix.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7375, 'Clifford''s Really Big Movie', False, True, False, False, 2004, 5.3, 'Clifford overhears Mr. Bleakman say that feeding him must cost a lot of money. A carnival act called "Larry''s Amazing Animals" has just been in town, and the animals in the show told Clifford about an animal contest with a prize of a lifetime supply of Tummy Yummies. So he decides to run away along with Cleo and T-Bone, join the carnival act, win the contest, and bring the food back.', 'tt0398872', '/cpaynGAqDjr0N5I92JEEqW0dpTl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7376, 'Postman Pat: The Movie', False, True, False, False, 2014, 4.6, 'Like always, Postman Pat is happily going about his duty delivering letters and parcels around the fictional town of Greendale. One day, auditions for the TV talent show, "You''re the One", are held in Greendale, which Pat manages to pass. With Pat required to be away from Greendale whilst participating in the contest, various Patbot 3000 robots are deployed to take Pat''s postal duties. This soon turns out to be an evil plot to take over the world, which Pat must now stop.', 'tt2062622', '/718nsLt48Pk6fvHG2huwKS9o2cn.jpg');
