insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7380, 'Pipe Dream', False, True, False, False, 2002, 5.9, 'A lonely plumber poses as a director to meet women, and the writer whose script he''s stolen builds on his ruse to get her movie made.', 'tt0164810', '/e7J0uJaMb4wXYzZ41gbhqy6DXwI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7382, 'The Mutilator', False, True, False, False, 1985, 5.3, 'As a child, Ed was cleaning his father''s hunting rifles for as part of a surprise birthday treat for his old man when one firearm accidentally discharges fatally striking his own mother in the back. His father, Jack Sr came home, saw his wife dead, and blaming his own offspring tried to commit infanticide before Ed managed to escape his father''s murderous antics. Leaving his old man to sleep into mournful psychosis as he began pouring whiskey down his slain wife''s throat. Ed now in his teens returns back to his family home, his dad long since supposedly vanished...only he hasn''t and has been waiting for a chance to even the score with his kid...right about...now', 'tt0124014', '/WdTypYAKttmUPWMQ4uLzRS6Hye.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7384, 'Empire of the Wolves', False, True, False, False, 2005, 5.9, 'When the young detective Paul Nerteaux finds the third slashed female corpse of illegal Turkish immigrants, he decides to ask for support to the experienced dark retired detective Jean-Louis Schiffer to chase the serial killer. Together, they infiltrate in the Turkish mafia trying to find the answer to the crimes. Meanwhile, the worker Anna Heymes is being submitted to a brain treatment and has severe headaches and glimpses of memories and visions. When these two parallel events collide, disclose that the Turkish organization "The Wolves" is behind them.', 'tt0402158', '/AkX5I7NF3OcFvxwWm2VzsRgZ1yb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7385, 'Wolf Warrior', False, True, False, False, 2015, 5.4, 'A Chinese special force soldier with extraordinary marksmanship is confronted by a group of deadly foreign mercenaries who are hired to assassinate him by a vicious drug lord.', 'tt3540136', '/501d6RoyTUK2ViDTpkK6l4D4kwY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7386, 'The Eagle', False, True, False, False, 1925, 6.6, 'In 140 AD, twenty years after the unexplained disappearance of the entire Ninth Legion in the mountains of Scotland, young centurion Marcus Aquila (Tatum) arrives from Rome to solve the mystery and restore the reputation of his father, the commander of the Ninth. Accompanied only by his British slave Esca (Bell), Marcus sets out across Hadrian''s Wall into the uncharted highlands of Caledonia - to confront its savage tribes, make peace with his father''s memory, and retrieve the lost legion''s golden emblem, the Eagle of the Ninth.', 'tt1034389', '/mbsdwO4lfIARqt35VVTM0UlYkhZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7387, 'Gravy', False, True, False, False, 2015, 5.4, 'It''s All Hallow''s Eve. A trio of costumed misfits with very special dietary requirements seizes a Mexican cantina and force the staff to engage in a late night of gaming, food and libations. The only caveat is what''s on the menu.', 'tt2822578', '/24yoyt6M9vI8pHppI6uidR5W0y1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7389, 'Between the Folds', False, True, False, False, 2008, 7.5, 'Depicts a cast of fine artists and eccentric scientists (from MIT and NASA) who have devoted their lives to the unlikely medium of modern origami. Through their determination to reinterpret the world in paper, they arouse a fascinating mix of sensibilities towards art, form, expressiveness, creativity and meaning', 'tt1253565', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7390, 'The Night Visitor', False, True, False, False, 1971, 6.6, 'An insane Swedish farmer escapes from an asylum to get revenge on his sister, her husband and others.', 'tt0066141', '/5lryG6iweTnMScE8QwcsoA1u2w6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7392, 'Dead or Alive: Final', False, True, False, False, 2002, 5.7, 'Set in a post apocalyptic Yokohama where the population is kept under rigid control by a homosexual megalomaniac mayor. The citizens are administered drugs to suppress heterosexual urges. Officer Takeshi Honda (Riki Takeuchi) is a hard boiled cop enforcing the mayor''s agenda, and Ryō (Show Aikawa) is a mellowed out drifter that hooks up with a gang of rebels. When the gang kidnap Takeshi''s son, it begins a series of events leading to an inevitable showdown.', 'tt0301167', '/pKQYwkJweRdFRMAso5AgTA3iZxE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7395, 'Veeram', False, True, False, False, 2014, 6.5, 'A straightforward man, who uses violence to settle disputes, decides to mend his ways for the sake of his lover, but when he learns that her family is in danger, he decides to save them at all cost.', 'tt3320578', '/nN8R6j4KhKh5W7AQmtTcIgeGSK7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7396, 'Bomber', False, True, False, False, 1982, 6.4, 'An 83 year-old man returns to Germany for a long planned journey of atonement. When Ross, his useless son agrees to drive him there, a nightmare family road trip ensues.', 'tt1207926', '/sJ8xzcZua5cBhvrkpcPAILdWCzW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7398, 'Invincible Shaolin', False, True, False, False, 1978, 7, 'Three North Shaolin teachers (Lu Feng, Chang Sheng, and Sun Chien) are called on by the Manchus to teach their soldiers and are urged to challenge the current South Shaolin teachers. They defeat the South Shaolin teachers and, that night, the head general (Wang Lung Wei) kills the South Shaolin teachers and blames their death on the North Shaolin teachers. The South Shaolin master sends more of his pupils, who are killed accidentally by the North Shaolin teachers. He finally sends two more (Wei Pai and Lo Meng) of his students to train with old masters and trains one student (Kuo Chui) himself with the goal of finally defeating the North Shaolin experts.', 'tt0077973', '/pXCPlXQ7ICSpHbg8HKfqRnVbYp1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7399, 'Go for Broke!', False, True, False, False, 1951, 6.7, 'A tribute to the U.S. 442nd Regimental Combat Team, formed in 1943 by Presidential permission with Japanese-American volunteers. We follow the training of a platoon under the rueful command of Lt. Mike Grayson who shares common prejudices of the time. The 442nd serve in Italy, then France, distinguishing themselves in skirmishes and battles; gradually and naturally, Grayson''s prejudices evaporate with dawning realization that his men are better soldiers than he is.', 'tt0043590', '/6yP51y4mcGz5bjGueZKYZ3MDWx7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7401, 'Despite the Falling Snow', False, True, False, False, 2016, 5.8, 'In Cold War Moscow, a female spy steals secrets from an idealistic politician – and falls in love with him.  Moscow, 1959: Katya (Rebecca Ferguson, Mission Impossible- Rogue Nation, The Girl on the Train, The White Queen), is young, beautiful – and a spy for the Americans. When she and Mischa (Oliver Jackson-Cohen, Emerald City), begin spying on Alexander ( Sam Reid, Anonymous), an idealistic Communist politician, the last thing she expects is to fall in love with him. Her choice between love and duty leads to a nail-biting conclusion that Alexander (Charles Dance, The Imitation Game) can only unravel decades later in 1990s New York. His journey back to the snowbound streets of Moscow uncovers a love triangle and betrayals from those he trusted most.', 'tt2474932', '/sYCwDIeGfOjxesa3j6wu44EjhwU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7403, 'Anina', False, True, False, False, 2015, 7.1, 'Anina Yatay Salas is a ten-year-old whose name spells trouble: those three palindromes in a row are an ongoing source of teasing at school. When a playground fight results in mysterious punishment, Anina will learn to put her problems in perspective and empathize with others in this sweet little daydream of a tale.', 'tt1947964', '/bI0zmlwOr8QSr4NcugJsZhwWJNw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7405, 'Three and Out', False, True, False, False, 2008, 6.2, 'A London tube driver considers pursuing a third fatal accident to collect a huge payout.', 'tt1130980', '/8paAoCY8YKhD6flhm0vRR3hCAzU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7408, 'Pulling John', False, True, False, False, 2009, 7.6, 'John Brzenk is troubled by when to call it quits; should he retire when he is on top or wait until someone takes his title. After twenty-five solid years of total world domination, John Brzenk, is aware that his dethroning may be near, he just doesn''t know the cause. As the prodigal arm wrestler, Brzenk excelled in a way that seemed humanly impossible. Hungry for the best arm wrestling competition, Brzenk traveled the world beating every opponent. With a humble and understated character, his legend supercedes him in the hearts of arm wrestlers in over 120 countries. Among the super warriors of arm wrestling are 2 titans from opposite ends of the world, bonded by one defining goal, to beat John.', 'tt1289451', '/62Kw0Q2ue1JqrwoFbciPGf0kiq2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7409, 'Icebreaker', False, True, False, False, 2016, 6.2, 'At the the Killington ski resort something has gone awry. Evil terrorists led by the sinister Greig have taken the resort hostage with a stolen nuclear device. It''s up to Ski Patrol bum Matt Foster to save the day... and his fiancé.', 'tt0179861', '/wiv7IDjCBMQMjPRLb4apjz2JIHe.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7410, 'Alabama Moon', False, True, False, False, 2009, 6.5, 'After the unexpected death of his survivalist father, an eleven year old boy raised in the Alabama wilderness must learn how to make a home in the modern world.', 'tt1300155', '/zTExJLGaHxGnEtbo2n0vCgvxx7k.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7411, 'The Passion of Darkly Noon', False, True, False, False, 1995, 5.9, 'Desire torments a former cultist taking refuge at the home of a scantily clad woman whose husband is away.', 'tt0114099', '/aU1eMv0ccC4e9mzYlZaZGW2h1vw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7413, 'Love Meetings', False, True, False, False, 1965, 7.5, 'Microphone in hand, Pier Paolo Pasolini asks Italians to talk about sex: he asks children where babies come from, young and old women if they are men''s equals, men and women if a woman''s virginity matters, how they view homosexuals, how sex and honor connect, if divorce should be legal, and if they support closing the brothels (the Merlina Act). He periodically checks in with Alberto Moravia and Cesare Musatti. Bersani is intrusive and judgemental, prodding those who answer. The film''s thesis: despite the booming post-war economy, Italians'' attitudes toward sex are either rigidly Medieval (the poor and the South) or muddled and self-censoring (the bourgeoisie and the North).', 'tt0057960', '/xkfXK0hsvZAqs9CFFKcToEPsAXq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7416, 'One More Time', False, True, False, False, 2016, 5.2, 'Beautiful aspiring rock star Jude is stuck in a rut - relegated to recording commercial jingles and lost in a series of one night stands. When she is evicted from her Brooklyn apartment, she is forced to move into the Hamptons home of her wealthy - and selfish - father Paul Lombard, an over-the-hill, Sinatra-esque crooner angling for a musical comeback.', 'tt3662066', '/yrZ1azTISRD91tcYCeMk7lhhYgc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7418, 'The First of the Few', False, True, False, False, 1942, 7, 'This 1942 fictionalized biopic chronicles the true story of how two of the most remarkable men in aviation history - visionary Spitfire designer R.J. Mitchell and his test pilot Geoffrey Crisp - designed a streamlined monoplane that led to the development of the Spitfire.', 'tt0034734', '/850BLE7mw8ortMTvfTvXkcRCyTv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7419, 'The Way I Spent the End of the World', False, True, False, False, 2006, 7.2, 'Bucharest 1989 - the last year of Ceausecu''s dictatorship. Eva lives with her parents and her 7 year old brother, Lalalilu. One day at school, Eva and her boyfriend accidentally break a bust of Ceausescu. They are forced to confess their crime before a disciplinary committee and Eva is expelled from school and transferred to a reformatory establishment. There she meets Andrei, and decides to escape Romania with him. Lalalilu becomes convinced that Ceausescu is the main reason for Eva''s decision to leave. So with his friends from school he devises a plan to kill the dictator.', 'tt0799991', '/gIjQQ7XR2CBB58XSmmYeH2G0fFg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7421, 'The Red Queen Kills Seven Times', False, True, False, False, 1972, 6.6, 'The film starts out incredibly in a beautiful Gothic castle in Germany. As little girls, Kitty Wildenbrück and her sister Evelyn have been fighting when their grandfather tells them the story behind an incredibly uncanny painting: Legend has it that a fiendish Red Lady is to return to the castle every hundred years and kill seven people. Fourteen years later, Kitty (Barbara Bouchet) has become a successful fashion photographer. Suddenly, people begin to get murdered...', 'tt0068444', '/78LsL4kCR08PzBxRTJzFlKw4M31.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7423, 'Irresistible', False, True, False, False, 2006, 5.7, 'A wife and mother is consumed by the thought that her husband''s co-worker is trying to win him away from her and their family.', 'tt0448564', '/oEjIshd5I8kMWgVY1Sw8sH4SlHH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7424, 'Dear Santa', False, True, False, False, 2011, 6.4, 'Crystal, a rich party girl, finds a little girl''s letter to Santa asking for a new mother, and she vows to win over the father and daughter before the holidays.', 'tt1893218', '/18UnDP6ficSVzWKVflhh6ebjftJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7426, 'Tim', False, True, False, False, 1979, 6.4, 'Handsome but backward gardener Tim Melville has a new woman in his life. She is Mary, his widowed employer, a woman who had given up on love until she had found Tim. At first they are friends. But soon they become lovers, as Mary, drawn to Tim''s innocence and magnetism, begins to teach him all about life - and love.', 'tt0080024', '/ji8Y2jM6aISMUP053C4neWGPYiE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7428, 'Officers', False, True, False, False, 1971, 7.7, 'Two friends are army officers, and the turbulent times throw them from the Civil War in the early 20s to the Soviet conflict in Finland, from China to the Urals.', 'tt0067517', '/ig4COIGS7WE3AR2YoJyRso6NYt8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7429, 'Red Flag', False, True, False, False, 2012, 5.5, 'Writer/director/star Alex Karpovsky, a familiar face to indie filmgoers, reveals his sterling comic chops in this close-to-the-bone comedy. Teasing the line between fiction and reality, he plays an indie filmmaker named Alex Karpovsky who, dumped by a longtime girlfriend fed up with his refusal to marry, takes to the road with a reluctant old pal for a misbegotten mini tour screening his movie on college campuses and independent cinemas.', 'tt2147048', '/rVuLdqIO2AWzciwFEv4mEjsfObG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7430, 'Molly Moon and the Incredible Book of Hypnotism', False, True, False, False, 2015, 5.3, 'An orphan who practices hypnosis faces off against a bank robber who wants her book on the art form.', 'tt2445178', '/AjhnZJ1SXsqeGgxdt8PIwotIRDD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7431, 'Cinema Verite', False, True, False, False, 2011, 6.5, 'In 1973, the Loud family became a television sensation of a new kind. It was long before a metal rock star showed his eccentric family on the small screen and decades before housewives had screaming matches with each other on camera in public. CINEMA VERITE tells the behind-the-scenes story of the groundbreaking documentary "An American Family," which chronicled the lives of the Louds in the early 1970s and catapulted the Santa Barbara family to notoriety while creating a new television genre: the reality TV series.', 'tt1623742', '/yyXW04JEfVHBoK6ijeHiBle0odo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7434, 'The Vampire Bat', False, True, False, False, 1933, 5.7, 'When the villagers of Klineschloss start dying of blood loss, the town fathers suspect a resurgence of vampirism. While police inspector Karl remains skeptical, scientist Dr. von Niemann cares for the vampire''s victims one by one, and suspicion falls on simple-minded Herman Gleib because of his fondness for bats. A blood-thirsty mob hounds Gleib to his death, but the vampire attacks don''t stop.', 'tt0024727', '/bGN3Ik3CatDC9wvfV55LLBrBGh8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7435, 'Burial Ground', False, True, False, False, 1981, 5.7, 'Professor Ayres discovers a secret in an ancient stone and when he opens a crypt, he revives zombies that kill him. He had invited three couples to visit him in his manor to reveal his discovery. However, they never get around to meeting the professor. Out of the blue, the zombies attack them and they seek shelter in the mansion.', 'tt0081248', '/cV86W9mgTuDvwWgKRrnWp8t1LJ7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7436, 'Our City Dreams', False, True, False, False, 2008, 6.7, 'Filmed over the course of two years, Our City Dreams is the story of a woman''s struggles and successes as an artist in New York City. Told through five women artists, from youngest to oldest, the film features Swoon, Ghada Amer, Kiki Smith, Marina Abramovic, and Nancy Spero. From the studio to the streets of New York, from the canals of Venice to the alleys of Cairo and the beaches of Phuket, Our City Dreams takes us deep into the artists'' worlds.', 'tt0968760', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7437, 'Nancy Drew... Reporter', False, True, False, False, 1939, 6.7, 'While participating in a contest at a local newspaper in which school children are asked to submit a news story, local attorney Carson Drew''s daughter Nancy intercepts a real story assignment. She "covers" the inquest of the death of a woman who was poisoned. Nancy doesn''t think the young woman accused of the crime is guilty and corrals her neighbor Ted into searching for a vital piece of evidence, and they stumble onto the identity of the real killer.', 'tt0031709', '/j8JzNjkjZpZl0RFzrMYi8LRexGE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7439, 'Wild Combination: A Portrait of Arthur Russell', False, True, False, False, 2008, 7.5, 'Wild Combination is Matt Wolf’s acclaimed documentary on seminal avant-garde composer, singer-songwriter, cellist, and disco producer Arthur Russell. Before his death in 1992, Arthur created music that spanned both pop and the transcendent possibilities of abstract art.', 'tt1168662', '/r4g8koRwqWu25PWvmaZnbwGUHux.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7441, 'The Nature of Existence', False, True, False, False, 2010, 6.5, 'After exploring the phenomenon of TREKKIES, filmmaker Roger Nygard takes on The Nature of Existence. As he roams the globe to the source of each of the world''s philosophies, religions, and belief systems, Nygard interviews spiritual leaders, scholars, scientists, artists, pizza chefs, and others who have influenced, inspired, or freaked out humanity.', 'tt1196672', '/d1kFtLNNHlHGOUNwtPZeMdUism5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7442, 'Arcadia', False, True, False, False, 2012, 6.1, 'Greta''s dad Tom is moving the family cross-country in a dented station wagon, promising a California paradise to his kids. All that''s missing is Mom.', 'tt2056520', '/dGic2hlJe1MiTo3UfDpi5RysJuM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7443, 'The Timber', False, True, False, False, 2015, 5, 'In the wild west, two brothers embark on a journey to collect a bounty in a desperate attempt to save their home: but what they find along the way is more than they bargained for.', 'tt2215673', '/kV0jEsELVEhqIU9MPyidKaHpvh9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7444, 'JFK: The Smoking Gun', False, True, False, False, 2013, 7.2, 'Seventy-five percent of the American people still refuse to believe the official story of President John F. Kennedy''s death. They do not think he was killed by a lone gunman but by a mysterious cabal that somehow conspired to have him killed. How can this be? How can a crime this famous, witnessed and investigated by so many, remain a mystery? This is what veteran Australian police detective Colin McLaren is determined to find out. JFK: The Smoking Gun follows the forensic cold-case investigation McLaren conducted over four painstaking years, taking us back to that tragic day in Dallas at Dealey Plaza where the shooting took place, to Parkland Hospital where the president was pronounced dead, to the Bethesda Naval Hospital where the autopsy was conducted and to the conclusions of the Warren Commission that have remained controversial to this day.', 'tt2945784', '/kC6ilDtR14Y1HfpF1I3PPBlNBqQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7448, 'They Nest', False, True, False, False, 2000, 5.2, 'After freezing up under pressure in the emergency room, Dr. Cahill decides to spend a few months unwinding on a little Maine island where he immediately gets off on the wrong foot with the locals. Meanwhile, a body has washed up on shore infested with a strange type of carnivorous cockroach. But when Dr. Cahill tries to warn the townsfolk of the danger, they are naturally skeptical. Will they realize the truth before it''s too late?', 'tt0221633', '/dCSSGMXLPKcPHZF3QlwkRxQtlis.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7450, 'Skills Like This', False, True, False, False, 2007, 6.1, 'Max Solomon faces the awful truth that he will never be a writer. So, in a desperate attempt to find his true calling, he turns to crime. In this inventive comedy, three friends have their lives turned upside down when one of them realizes that larceny might be his best skill.', 'tt0800205', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7454, 'Samurai Cop', False, True, False, False, 1991, 4.6, 'When Japanese organized crime imbeds itself within LA, the police turn to one man to take down the deadly Yakuza — Joe Marshall, aka "The Samurai." With his fearless swagger and rock hard jaw, The Samurai tears a two-fisted hole through the mob and doesn''t stop until the job is done.', 'tt0130236', '/tBQGqJFx9u58NKOrlZoy0yTYXRn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7455, 'Baked in Brooklyn', False, True, False, False, 2016, 5.4, 'A recent college graduate decides to sell marijuana on the streets of Manhattan after losing his job at a consulting firm. He soon meets the girl of his dreams. With an unsupportive girlfriend, an increase of clienteles, and the growing threats of being caught or killed, he soon realizes he is in way over his head.', 'tt3138344', '/9P66ST8IackgLaHmfOk0EoQwYg5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7456, 'Bad Kids Go To Hell', False, True, False, False, 2012, 4.5, 'On a stormy Saturday afternoon, six students from Crestview Academy begin to meet horrible fates as they serve out their detentions. Is a fellow student to blame, or perhaps Crestview''s alleged ghosts are behind the terrible acts?', 'tt1865573', '/W1BUr2U0FmlFUjlzNKzQhFKL9M.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7457, 'Cottage Country', False, True, False, False, 2013, 5.6, 'When Todd takes his girlfriend Cammie up to the family cottage for a reclusive proposal, the last thing he expected to be doing was dealing with was his slacker brother and his hippie girlfriend.  But in this comedy of errors, Todd and Cammie, have to deal with his accidentally murdered brother in order to live happily ever after.', 'tt2072933', '/nbUTgFg6zJ1j47irmiivsDEI2rU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7458, 'Starcrash', False, True, False, False, 1978, 4, 'A pair of smugglers manage to pick up a castaway while running from the authorities, who turns out to be the only survivor from a secret mission to destroy a mysterious superweapon designed by the evil Count Zartharn. The smugglers are soon recruited by the Emperor to complete the mission, as well as to rescue the Emperor''s son, who has gone missing.', 'tt0079946', '/npJt35Q36aGcTYpcg9epbwQIIkz.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7460, 'Infestation', False, True, False, False, 2009, 5.9, 'Our hero, Cooper, awakes to find himself nauseous, weak and covered in webbing, hanging from the ceiling of an office where, just minutes ago, he started his new job. As he struggles out of his slimy prison he comes face to face with his opponent - a grotesque, powerful and very angry bug. All 3 ft of it', 'tt1020543', '/2wIdvayu0U3svIwgVkN0UW6jaKy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7462, 'Madhouse', False, True, False, False, 1974, 6.3, 'Julia (Trish Everly, in her only film role), a young teacher for deaf children living in Savannah, Georgia. Julia has horrid memories of her childhood, which was scarred by her sadistic twin sister Mary (Allison Biggers). At the urging of her uncle, Father James, Julia visits Mary, suffering from a severe skin disease, in a mental institution. The meet does not go well, and Mary vows to make Julia "suffer as she had suffered". As their mutual birthday approaches, several of Julia''s friends and neighbors begin to die gruesome deaths, some of which committed by a mysterious Rottweiler dog that has some sort of connection to Mary. But is Mary really the killer?', 'tt0093468', '/yuEPsdGzkz91urmDQkkKpbBScki.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7463, 'Sriracha', False, True, False, False, 2013, 7.4, 'Sriracha has earned a cult following, but the story of this spicy sauce is a mystery to most fans. Dedicated to Sriracha lovers, this fast-paced documentary travels around the globe to reveal its origin and the man behind the iconic ''rooster sauce.''', 'tt2992302', '/fPzNwJhghOlpegPQ0QJfaXXqze6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7466, 'Aakrosh', False, True, False, False, 2010, 7, 'Three friends from Delhi go missing in a small village - Jhanjhar, in Bihar. It''s 2 months and there is no clue about their disappearance. The media and students movement demand action from the authorities. It''s then that the government orders a Central Bureau of Investigation enquiry with officers Sidhant Chaturvedi (Akshaye Khanna) and Pratap Kumar (Ajay Devgn) to solve the case.', 'tt1708453', '/2bqp1DPjmDoaB6qO0Bps8bNyzun.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7467, 'Becoming Zlatan', False, True, False, False, 2016, 6.6, 'The decisive years of Swedish soccer player Zlatan Ibrahimović, told through rare archive footage in which a young Zlatan speaks openly about his life and challenges. The film closely follows him, from his debut with the Malmö FF team in 1999 through his conflict-ridden years with Ajax Amsterdam, and up to his final breakthrough with Juventus in 2005.', 'tt5280626', '/9Hqm7DGNP0oSSFdkDSBplJHEzhC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7468, 'Death in Buenos Aires', False, True, False, False, 2014, 5.7, 'Buenos Aires in the 1980s. Detective Chavez, a family man and a tough cop, once again must solve a mysterious crime. To reveal the identity of the murderer, Chavez must clarify the enigma waiting in ambush to capture his own heart. Passion surprises even the most astute detective. What will triumph: law or desire?', 'tt3621802', '/l0S18Qk82DtdMLgGQrWd3ItQs5T.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7471, 'Certain Prey', False, True, False, False, 2011, 5.4, 'Minneapolis Deputy Police Chief, Lucas Davenport, has to face off against a duo unlike any he''s ever encountered; a lethal hit woman and a ferociously cunning killer determined to hunt him down.', 'tt1864461', '/d1qkMxHYNX3fMIFIDvTN99MhJol.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7472, 'Artificial Paradises', False, True, False, False, 2012, 6.4, 'Artificial Paradises tells the story of Nando and Erika, two young people in their twenties who meet several times without noticing. It tells the story of the subculture of electronic music and rave parties and drugs like MDMA.', 'tt2102396', '/gLnH9uUNzsNj51ox0M30GzFEX95.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7473, 'One of Our Aircraft Is Missing', False, True, False, False, 1942, 7, 'During the Allied Bombing offensive of World War II the public was often informed that "A raid took place last night over ..., One (or often more) of Our Aircraft Is Missing". Behind these sombre words hid tales of death, destruction and derring-do. This is the story of one such bomber crew who were shot down and the brave Dutch patriots who helped them home.', 'tt0035153', '/50ERGbegoXH2vZKM0hJwFavMEHT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7474, 'Kathleen Madigan: Gone Madigan', False, True, False, False, 2010, 7.5, 'Finally, a comedian who isn''t whining, insulting or self-loathing. Kathleen Madigan is just brilliantly funny and is still, after 20 years in the business, actually having fun. In this hilarious stand-up special, shot at the Gramercy Theater in New York City, Kathleen takes on politics, American culture and her family who has given her full permission to sell them down them down the river. Winner of the American Comedy Award for Best Female Comic, Madigan has been seen over 12 times on The Tonight Show, The Late Show with David Letterman, The View, Dr. Phil, ESPN, The Joy Behar Show and has had her own specials on HBO and Comedy Central. Madigan was the only comedian in the history of NBC''s Last Comic Standing to go unchallenged by any other comedian. She lives in Los Angeles but spends inordinate amounts of time with her family in the Missouri Ozarks.', 'tt1820451', '/yh4mS7PMHfykPV50uKNPKOGNQ5H.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7475, 'That Gal...Who Was in That Thing: That Guy 2', False, True, False, False, 2015, 7.3, 'That Gal...Who was in That Thing: That Guy 2', 'tt4406298', '/1SlPtV8C4OvdWaXEkOpVQSC2XVA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7479, 'Roads to Koktebel', False, True, False, False, 2003, 7, 'A widowed aeronautics engineer, who has lost his job, travels with his son hopping freight trains from Moscow to Koktebel, a town by the Black Sea, to start a new life with the father''s sister.', 'tt0372366', '/y5ss5Lx0kxoK2xEkyAL8U9oGuWS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7482, 'Welcome to Happiness', False, True, False, False, 2015, 6.1, 'There''s a magical door in Woody''s closet that allows those who go through it to erase mistakes from their past. When he finds out where it goes, his life will be changed forever.', 'tt3564794', '/XQLlLlmjyIuUAT5eaKBUbi42oD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7484, 'Mad As Hell', False, True, False, False, 2014, 5.8, 'MAD AS HELL follows Cenk Uygur''s transformation from unknown talk show host on local Public Access TV to an internet sensation with his online news show "The Young Turks," which has amassed over one billion views on YouTube. Once Cenk ventures from the internet into national television and lands the 6 PM time slot on MSNBC, his uncensored brand of journalism is compromised and Cenk becomes the nexus in the battle between new and old media.', 'tt1826550', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7487, 'Graduation Day', False, True, False, False, 1981, 4.8, 'After a high school track runner, named Laura, suddenly dies from a heart attack after finishing a 30-second 200-meter race, a killer wearing a sweat suit and a fencing mask begins killing off her friends on the school track team one by one. The suspects include the track coach Michaels, Laura''s sister Anne who arrives in town for the funeral, the creepy school principal Mr. Guglione, and Laura''s strange boyfriend Kevin.', 'tt0082467', '/mt2xO12CTV1BHEZhxN1RsN3GqJA.jpg');
