insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7489, 'The Case of the Bloody Iris', False, True, False, False, 1972, 6.5, 'Beautiful young model Jennifer Lansbury and her goofy friend Marilyn Ricci move into a swanky high-rise apartment after the previous tenant gets brutally murdered. Pretty soon Jennifer is being stalked by the mysterious killer. Probable suspects include a predatory lesbian neighbor, a weird old woman and her deformed son, and even the building''s handsome architect who suffers from a severe blood phobia.', 'tt0069073', '/afpZ3Eckzo0NyU2SyKbIouoQ3jm.jpg');
