insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7501, 'Last of the Renegades', False, True, False, False, 1964, 6.6, 'Forester, a ruthless oil baron, wants to create a war between the native American tribes and the white men. Old Shatterhand, Winnetou and their sidekick Castlepool try to prevent this.', 'tt0058751', '/kSQhAu2lFt51AMppFkbIS4gy45L.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7503, 'Home Sweet Hell', False, True, False, False, 2015, 5.4, 'Mona and Don’s seemingly perfect suburban bliss is disrupted by a sexy extortionist and Mona will stop at nothing, including killing the competition, to keep her little slice of heaven.', 'tt2802136', '/vETciJiWTLD0AdDHUANb8HkFdJ5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7504, 'Programming The Nation?', False, True, False, False, 2011, 6.2, 'Programming the Nation? takes an encompassing look at the history of subliminal messaging in America. According to many authorities, since the late 1950s subliminal content has been tested and delivered through all forms of mass-media including Hollywood filmmakers Alfred Hitchcock and William Friedkin. Even our modern military has been accused of these practices in the "war on terror" against soldiers and civilians both abroad and at home. With eye-opening footage, revealing interviews, humorous anecdotes, and an array of visual effects, the film categorically explores the alleged usage of subliminals in advertising, music, film, television, anti-theft devices, political propaganda, military psychological operations, and advanced weapons development. Director Jeff Warrick makes it his personal mission to determine if these manipulative tactics have succeeded in "programming the nation?" Or, if subliminal messaging belongs in the category of what many consider urban legend.', 'tt1023345', '/e8dyK0SrKqQPNEAZeqNRBfdbmf0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7507, 'The Matchbreaker', False, True, False, False, 2016, 5.6, 'When an idealistic romantic gets fired from his day job, he is offered a "one-time gig" to break up a girl''s relationship for her disapproving parents. This "one-time" gig spreads through word-of-mouth and he ends up becoming a professional match-breaker. However, he ends up falling for one of his clients and must figure out how to balance his secret job with his love-life.', 'tt4269746', '/m9RWMUNtaFZpYKtB8HH5Yg11P5G.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7508, 'Dead Rising: Watchtower', False, True, False, False, 2015, 5.2, 'Based on the game, Dead Rising takes place during a large-scale zombie outbreak. When a mandatory government vaccine fails to stop the infection from spreading, the four leads must evade infection while also pursuing the root of the epidemic, with all signs pointing to a government conspiracy.', 'tt3816458', '/iO8w6tBjbbSpJU1inQChwa4FGY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7509, 'In Search of Blind Joe Death: The Saga of John Fahey', False, True, False, False, 2013, 6.9, 'In Search of Blind Joe Death: The Saga of John Fahey is a documentary film about the legendary American guitarist, composer and provocateur John Fahey, 1939-2001. Fahey is often considered the godfather of ''American primitive guitar''. This cinematic exploration features Pete Townshend, Chris Funk of The Decemberists and Joey Burns of Calexico. These stellar musicians, along with Fahey associates and friends such as the famous ''Dr. Demento'', radio broadcaster Barry Hansen, explore the legacy of this profoundly influential artist. The film was recorded in the Washington D.C. area where John Fahey was born, along the Mississippi Delta from Memphis to New Orleans, in Los Angeles, Toronto, Austin, New York and in Oregon where Fahey spent his last two decades.', 'tt2213938', '/rsGkmeBZd6sooW0Inut4bJyC2fO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7511, 'The Living', False, True, False, False, 2014, 6.1, 'After an alcohol induced blacked-out night of drinking, Teddy discovers he has severely beaten his wife, Molly. As he attempts to redeem himself to her she must decide whether or not she will take him back as her mother, Angela, continuously expresses her disapproval, all the while her brother, Gordon, falls in way over his head as he tries to hire an ex-con, Howard, to kill Teddy.', 'tt2950236', '/9E1RmLv161NuS7ylMvA1wemTn70.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7512, 'Shaolin Prince', False, True, False, False, 1982, 7, 'Two princes are seperated by birth. One is raised by the Prime Minister. The other is raised by three mad Shaolin Monks. They both learn kung-fu. When they are 23, they meet and combine there forces to defeat the evil 9th Prince.', 'tt0092855', '/dR6zfd2ssLrr2aZv3GHXjqJD2TQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7513, 'Jack the Giant Killer', False, True, False, False, 1962, 6.4, 'A giant beanstalk brings Jack to a land in the clouds filled with snarling, evil beasts. When the creatures make their way to the ground, Jack must figure out how to get back down before they destroy earth and everyone in it.', 'tt2552498', '/eV1qcF3ifxJqEESus7wJeDrR3z2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7515, 'Three Worlds', False, True, False, False, 2012, 6.6, 'Al, a young man from a modest background is about to marry his boss’ daughter, along with succeeding him as the head of a car dealership.  One night, while coming back from his bachelor party, he is guilty of a hit-and-run accident, urged by his two childhood friends present in the car. The next day, gnawed with guilt, Al decides to inquire about his victim. What he does not know is that Juliette, a young woman, has witnessed the entire accident from her balcony.', 'tt2369497', '/rLTISWu9tOsiPrZ6l3eEZuF2YNO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7516, 'The Entitled', False, True, False, False, 2011, 6.1, 'Without the security of the job he wants or the future he dreamed of, Paul Dynan plans the perfect crime to help his struggling family – abduct the socialite children of three wealthy men and collect a ransom of $3-million dollars. Over the course of one long night, Paul and his accomplices hold the rich kids hostage awaiting the ransom with little idea of the secrets that will surface between the fathers when they are forced to choose between their children and their money.', 'tt1650535', '/nJMru27zbwolphYGEvzqYkMmm0e.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7517, 'Dive!', False, True, False, False, 2010, 7.1, 'Follow filmmaker Jeremy Seifert and his circle of friends as they dumpster dive in the back alleys and gated garbage receptacles of L.A.''s supermarkets. In the process they uncover thousands of dollars worth of good food and an ugly truth about waste in America: grocery stores know they are wasting and most refuse to do anything about it.', 'tt1490637', '/4BJoysVachJyRi7Gi8kGwWYy40C.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7518, 'Mythica: The Necromancer', False, True, False, False, 2015, 5.5, 'Mallister takes Thane prisoner and forces Marek and her team on a quest.  Marek tracks down the Necromancer for a final showdown for the Darkspore.', 'tt3608646', '/3tDoZ8VXRlpMjOBkFB31e1dupSx.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7520, 'Stage Door Canteen', False, True, False, False, 1943, 6.3, 'A young soldier on a pass in New York City visits the famed Stage Door Canteen, where famous stars of the theater and films appear and host a recreational center for servicemen during the war. The soldier meets a pretty young hostess and they enjoy the many entertainers and a growing romance', 'tt0036384', '/odRoxasnqevzpMJrnaPw6DimoYo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (7521, 'Bloody Tie', False, True, False, False, 2006, 6.3, 'In this uncompromising vision of the gritty Korean underworld, a crystal meth dealer with a tragic past is forced to team up with a renegade cop to take down a powerful crime lord. Reminiscent of the Hong Kong action films of the 80’s but full of the dark and unpredictable machismo of Korean cinema today, Bloody Ties is a sinister and vicious film noir thriller.', 'tt0816437', '/qUQKmwU3HM6DcN5D8jHDgvRNCcH.jpg');
